import React, { useRef, useState } from "react";
import { v4 as uuidv4 } from "uuid";
import { useTranslation } from "react-i18next";
import { toast } from "react-hot-toast";
import { useFormik, FormikProvider, FieldArray, getIn } from "formik";
import * as Yup from "yup";
import { useAuth } from "../../../hooks/use-auth";
import { Trash as TrashIcon } from "../../../icons/trash";
import { useDispatch } from "../../../store";

import {
  Box,
  Button,
  Card,
  CardContent,
  Checkbox,
  Divider,
  Grid,
  InputAdornment,
  TextField,
  Typography,
} from "@mui/material";
import { Plus as PlusIcon } from "../../../icons/plus";
import { userApi } from "../../../api/user-api";

export const AccountOrderLRInvoiceSettings = (props) => {
  const { user } = useAuth();
  const { t } = useTranslation();
  const dispatch = useDispatch();

  const formik = useFormik({
    initialValues: {
      driverExpensesCategories: [
        {
          id: uuidv4(),
          driverExpenseName: "",
          showDefault: true,
        },
      ],
    },
    onSubmit: async (values, helpers) => {
      try {
        console.log(values.driverExpensesCategories),
          // await orderApi.createOrder(newOrder, dispatch);
          await userApi.updateUser(
            {
              id: user.id,
              driverExpensesCategories: JSON.stringify(
                values.driverExpensesCategories
              ),
              _version: user._version,
            },
            dispatch
          );
        toast.success("Order created!");
        // router.push("/dashboard/orders");
      } catch (err) {
        console.error(err);
        toast.error("Something went wrong!");
        helpers.setStatus({ success: false });
        helpers.setErrors({ submit: err.message });
        helpers.setSubmitting(false);
      }
    },
  });

  return (
    <form onSubmit={formik.handleSubmit} {...props}>
      <FormikProvider value={formik}>
        <FieldArray name="driverExpensesCategories" error={formik.errors}>
          {({ remove, push }) => (
            <Box sx={{ mt: 4 }} {...props}>
              <Card>
                <CardContent>
                  <Grid container spacing={3}>
                    <Grid item md={4} xs={12}>
                      <Typography variant="h6">
                        {t("Truck Driver Expenses Categories")}
                      </Typography>
                      <Typography variant="body2" color="textSecondary">
                        {t(
                          "Expenses to store the purpose of giving money to driver of own vehicles "
                        )}
                      </Typography>
                    </Grid>
                    <Grid item md={8} xs={12}>
                      <Grid container spacing={3}>
                        <Grid item xs={8}>
                          <Typography variant="body2" color="textSecondary">
                            {t("Truck Driver Expenses Categories")}
                          </Typography>
                        </Grid>
                        <Grid item xs={4}>
                          <Typography variant="body2" color="textSecondary">
                            {t("Show Default")}
                          </Typography>
                        </Grid>
                      </Grid>

                      {formik.values.driverExpensesCategories.length > 0 &&
                        formik.values.driverExpensesCategories.map(
                          (delivery, index) => {
                            const driverExpenseName = `driverExpensesCategories[${index}]`;
                            const touchedDriverExpenseName = getIn(
                              formik.touched,
                              driverExpenseName
                            );
                            const errorDriverExpenseName = getIn(
                              formik.errors,
                              driverExpenseName
                            );

                            console.log(`${formik.values[driverExpenseName]}`);
                            return (
                              <React.Fragment>
                                <Grid container spacing={3}>
                                  {index > 0 && <Divider sx={{ mb: 2 }} />}
                                  <Grid
                                    item
                                    xs={8}
                                    sx={{ mt: 2 }}
                                    className="col"
                                    key={index}
                                  >
                                    <TextField
                                      helperText={
                                        touchedDriverExpenseName &&
                                        errorDriverExpenseName
                                          ? errorDriverExpenseName
                                          : ""
                                      }
                                      error={Boolean(
                                        touchedDriverExpenseName &&
                                          errorDriverExpenseName
                                      )}
                                      variant="outlined"
                                      onChange={(event) => {
                                        formik.setFieldValue(
                                          `driverExpensesCategories[${index}].driverExpenseName`,
                                          event.target.value
                                        );
                                      }}
                                      onBlur={formik.handleBlur}
                                      id="driverExpenseName"
                                      name="driverExpenseName"
                                      label="Expense Name"
                                      fullWidth
                                      value={
                                        formik.values.driverExpensesCategories[
                                          index
                                        ].driverExpenseName
                                      }
                                    />
                                  </Grid>
                                  <Grid item xs={2} sx={{ mt: 3 }}>
                                    <Checkbox defaultChecked />
                                  </Grid>
                                  <Grid item sx={{ mt: 3 }} className="col">
                                    <Button
                                      disabled={index < 1}
                                      color="error"
                                      onClick={() => {
                                        remove(index);
                                      }}
                                    >
                                      <TrashIcon fontSize="small" />
                                    </Button>
                                  </Grid>
                                </Grid>
                              </React.Fragment>
                            );
                          }
                        )}

                      <Box
                        sx={{ mt: 2 }}
                        p={2}
                        display="flex"
                        justifyContent="flex-end"
                      >
                        <Button
                          sx={{ mr: 2 }}
                          variant="contained"
                          color="secondary"
                          startIcon={<PlusIcon fontSize="small" />}
                          onClick={() => {
                            push({
                              id: uuidv4(),
                              driverExpenseName: "",
                              showDefault: true,
                            });
                          }}
                        >
                          Add Expense
                        </Button>
                        <Button
                          color="secondary"
                          type="submit"
                          variant="contained"
                        >
                          Save Changes
                        </Button>
                      </Box>
                    </Grid>
                  </Grid>
                </CardContent>
              </Card>
            </Box>
          )}
        </FieldArray>
      </FormikProvider>
    </form>
  );
};
