import React, { useState, useEffect } from "react";
import { useRouter } from "next/router";
import toast from "react-hot-toast";
import * as Yup from "yup";
import { useFormik } from "formik";
import {
  Autocomplete,
  Box,
  Button,
  Card,
  CardContent,
  Divider,
  FormControlLabel,
  FormHelperText,
  Grid,
  MenuItem,
  Switch,
  TextField,
  Typography,
} from "@mui/material";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import { FileDropzone } from "../../file-dropzone";
import { QuillEditor } from "../../quill-editor";
import { useAuth } from "../../../hooks/use-auth";
import PartyAutocomplete from "../autocompletes/PartyAutocomplete";
import moment from "moment";
import { partyApi } from "../../../api/party-api";
import { useDispatch } from "../../../store";
import { API } from "aws-amplify";
import { createOrder } from "../../../graphql/mutations";
import { DataStore, Predicates } from "@aws-amplify/datastore";
import { Order } from "../../../models";
// import { v4 as uuid } from "uuid";

const categoryOptions = [
  {
    label: "Healthcare",
    value: "healthcare",
  },
  {
    label: "Makeup",
    value: "makeup",
  },
  {
    label: "Dress",
    value: "dress",
  },
  {
    label: "Skincare",
    value: "skincare",
  },
  {
    label: "Jewelry",
    value: "jewelry",
  },
  {
    label: "Blouse",
    value: "blouse",
  },
];

export const OrderCreateForm = (props) => {
  const router = useRouter();
  const dispatch = useDispatch();
  const [files, setFiles] = useState([]);
  const [parties, setParties] = useState([]);
  const { user } = useAuth();
  const formik = useFormik({
    initialValues: {
      saleDate: moment(),
      barcode: "925487986526",
      category: "",
      description: "",
      images: [],
      name: "",
      newPrice: 0,
      oldPrice: 0,
      sku: "IYV-8745",
      submit: null,
    },
    validationSchema: Yup.object({
      barcode: Yup.string().max(255),
      category: Yup.string().max(255),
      description: Yup.string().max(5000),
      images: Yup.array(),
      //   name: Yup.string().max(255).required(),
      //   newPrice: Yup.number().min(0).required(),
      oldPrice: Yup.number().min(0),
      sku: Yup.string().max(255),
    }),
    onSubmit: async (values, helpers) => {
      try {
        // NOTE: Make API request
        console.log(user);

        const newOrder = {
          //   id: uuid(),
          orderNo: parseInt(values.orderNo),
          user: user.id,
          saleDate: values.saleDate.format(),
        };

        const response = await API.graphql({
          query: createOrder,
          variables: { input: newOrder },
          authMode: "AMAZON_COGNITO_USER_POOLS",
        });

        toast.success("Order created!");
        router.push("/dashboard/orders");
      } catch (err) {
        console.error(err);
        toast.error("Something went wrong!");
        helpers.setStatus({ success: false });
        helpers.setErrors({ submit: err.message });
        helpers.setSubmitting(false);
      }
    },
  });

  useEffect(async () => {
    try {
      const partiesDB = await partyApi.getPartiesByUser(user, dispatch);
      setParties(partiesDB);
    } catch (error) {
      console.log(error);
    }
  }, []);

  const handleDrop = (newFiles) => {
    setFiles((prevFiles) => [...prevFiles, ...newFiles]);
  };

  const handleRemove = (file) => {
    setFiles((prevFiles) =>
      prevFiles.filter((_file) => _file.path !== file.path)
    );
  };

  const handleRemoveAll = () => {
    setFiles([]);
  };

  return (
    <form onSubmit={formik.handleSubmit} {...props}>
      <Card>
        <CardContent>
          <Grid container spacing={3}>
            <Grid item md={4} xs={12}>
              <Typography variant="h6">Basic details</Typography>
            </Grid>
            <Grid item md={8} xs={12}>
              <Grid container spacing={3}>
                <Grid item md={6} xs={12}>
                  <TextField
                    error={Boolean(
                      formik.touched.orderNo && formik.errors.orderNo
                    )}
                    fullWidth
                    helperText={formik.touched.orderNo && formik.errors.orderNo}
                    label="Order No"
                    name="orderNo"
                    onBlur={formik.handleBlur}
                    onChange={formik.handleChange}
                    value={formik.values.orderNo}
                  />
                </Grid>
                <Grid item md={6} xs={12}>
                  <DatePicker
                    id="saleDate"
                    name="saleDate"
                    label="Sale date"
                    showTodayButton={true}
                    inputFormat="DD/MM/YYYY"
                    value={formik.values.saleDate}
                    onClick={() => setFieldTouched("end")}
                    onChange={(date) =>
                      formik.setFieldValue("saleDate", moment(date))
                    }
                    renderInput={(params) => (
                      <TextField fullWidth {...params} />
                    )}
                  />
                </Grid>
                <Grid item md={6} xs={12}>
                  <Autocomplete
                    id={"type"}
                    options={parties}
                    onChange={(event, newValue) => {
                      if (typeof newValue === "string") {
                        // timeout to avoid instant validation of the dialog's form.
                        setTimeout(() => {
                          toggleOpen(true);
                          setDialogValue({
                            name: newValue.replace(/\w\S*/g, function (txt) {
                              return (
                                txt.charAt(0).toUpperCase() +
                                txt.substr(1).toLowerCase()
                              );
                            }),
                            transporter: type !== "customer" ? true : false,
                          });
                        });
                      } else if (newValue && newValue.inputValue) {
                        toggleOpen(true);
                        setDialogValue({
                          name: newValue.inputValue,
                          transporter: type !== "customer" ? true : false,
                        });
                      } else {
                        setFieldValue(type, newValue);
                        setValue(newValue);
                      }
                    }}
                    getOptionLabel={(option) => {
                      if (typeof option === "string") {
                        return option.replace(/\w\S*/g, function (txt) {
                          return (
                            txt.charAt(0).toUpperCase() +
                            txt.substr(1).toLowerCase()
                          );
                        });
                      }
                      if (option.inputValue) {
                        return option.inputValue.replace(
                          /\w\S*/g,
                          function (txt) {
                            return (
                              txt.charAt(0).toUpperCase() +
                              txt.substr(1).toLowerCase()
                            );
                          }
                        );
                      }
                      return option.name.replace(/\w\S*/g, function (txt) {
                        return (
                          txt.charAt(0).toUpperCase() +
                          txt.substr(1).toLowerCase()
                        );
                      });
                    }}
                    renderInput={(params) => (
                      <TextField
                        onKeyPress={(e) => {
                          e.which === 13 && e.preventDefault();
                        }}
                        {...params}
                        label={"Customer"}
                        variant="outlined"
                      />
                    )}
                  />
                  <Dialog
                    open={open}
                    onClose={handleClose}
                    aria-labelledby="form-dialog-name"
                  >
                    <form>
                      <DialogTitle id="form-dialog-name">
                        Add a new party
                      </DialogTitle>
                      <form onSubmit={handleSubmit} {...rest}>
                        <Card>
                          <CardContent>
                            <Grid container spacing={3}>
                              <Grid item xs={12}>
                                <TextField
                                  error={Boolean(touched.name && errors.name)}
                                  fullWidth
                                  helperText={touched.name && errors.name}
                                  label="Main Contact Name"
                                  name="name"
                                  onBlur={handleBlur}
                                  onChange={(event) => {
                                    setFieldValue(
                                      "name",
                                      event.target.value.replace(
                                        /\w\S*/g,
                                        function (txt) {
                                          return (
                                            txt.charAt(0).toUpperCase() +
                                            txt.substr(1).toLowerCase()
                                          );
                                        }
                                      )
                                    );
                                  }}
                                  required
                                  value={formik.values.name}
                                  variant="outlined"
                                />
                              </Grid>
                              <Grid item xs={12}>
                                <TextField
                                  error={Boolean(
                                    touched.mobile && errors.mobile
                                  )}
                                  fullWidth
                                  helperText={touched.mobile && errors.mobile}
                                  label="Mobile"
                                  name="mobile"
                                  onBlur={handleBlur}
                                  onChange={(event) => {
                                    setFieldValue(
                                      "mobile",
                                      event.target.value.replace(/ /g, "")
                                    );
                                  }}
                                  value={formik.values.mobile}
                                  variant="outlined"
                                />
                              </Grid>
                              <Grid item xs={12}>
                                <GoogleMaps
                                  name={"location"}
                                  field={"location"}
                                  formik={formik}
                                  error={Boolean(
                                    formik.touched.city && formik.errors.city
                                  )}
                                  label={"Your city in India"}
                                  setFieldValue={formik.setFieldValue}
                                  handleBlur={formik.handleBlur}
                                  values={formik.values}
                                />
                              </Grid>
                              <Grid item xs={12}>
                                <Typography variant="h5" color="textPrimary">
                                  Party Type
                                </Typography>
                                <Typography
                                  variant="body2"
                                  color="textSecondary"
                                >
                                  Is this party a transporter?
                                </Typography>
                                <Switch
                                  checked={formik.values.transporter}
                                  color="secondary"
                                  edge="start"
                                  name="transporter"
                                  onChange={formik.handleChange}
                                  value={formik.values.transporter}
                                />
                              </Grid>
                            </Grid>
                            <Box mt={2}>
                              <Grid container spacing={3}>
                                <Grid item>
                                  <Button
                                    variant="contained"
                                    color="secondary"
                                    type="submit"
                                    disabled={formik.isSubmitting}
                                  >
                                    Create Party
                                  </Button>
                                </Grid>
                                <Grid item>
                                  <Button
                                    variant="contained"
                                    color="secondary"
                                    onClick={handleClose}
                                  >
                                    Cancel
                                  </Button>
                                </Grid>
                              </Grid>
                            </Box>
                          </CardContent>
                        </Card>
                      </form>
                    </form>
                  </Dialog>
                </Grid>
              </Grid>
            </Grid>
          </Grid>
        </CardContent>
      </Card>
      <Box
        sx={{
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
          mx: -1,
          mb: -1,
          mt: 3,
        }}
      >
        <Button
          color="error"
          sx={{
            m: 1,
            mr: "auto",
          }}
        >
          Delete
        </Button>
        <Button sx={{ m: 1 }} variant="outlined">
          Cancel
        </Button>
        <Button sx={{ m: 1 }} type="submit" variant="contained">
          Create
        </Button>
      </Box>
    </form>
  );
};
