import React from 'react';
import { AgGridColumn, AgGridReact } from 'ag-grid-react';
import 'ag-grid-enterprise';
import 'ag-grid-community/dist/styles/ag-grid.css';
import 'ag-grid-community/dist/styles/ag-theme-alpine-dark.css';
import moment from 'moment';
import { debounce } from 'lodash';

import {
  calculateAmount,
  checkTripStatus,
  formatNumber
} from '../../../utils/AmountCalculation';

const Table = ({loading, orders,trips,deliveries}) => {
  let visibleRowNodes;
  let headerElement = document.querySelectorAll('.ag-header');
  let agGrid = document.querySelectorAll('.ag-body-viewport');
  let gridBodyHeight =
    (agGrid.length > 0 && agGrid[0].offsetHeight) -
    (headerElement.length > 0 && headerElement[0].offsetHeight);

  const visibleRowsChange = debounce(v => {
    const rowHeight = v.api.getDisplayedRowAtIndex(0)
      ? v.api.getDisplayedRowAtIndex(0).rowHeight
      : 48;
    const topIndex = v.top / rowHeight;
    const bottomIndex = (v.top + gridBodyHeight) / rowHeight - 1;

    visibleRowNodes = v.api.getRenderedNodes();
    let filteredVisibleRowNodes = visibleRowNodes
      ? visibleRowNodes.filter(
          node => node.rowIndex >= topIndex && node.rowIndex <= bottomIndex
        )
      : [];
    if (filteredVisibleRowNodes.length > 0) {
      let o = {};
      o['ordersAll'] = filteredVisibleRowNodes[0].rowIndex;
      // dispatch(updateLastSelectedRow(o));
    }
  }, 2000);

  const onGridReady = params => {
    // params.api.ensureIndexVisible(lastSelectedRow.ordersAll, 'top');
  };

  console.log(loading)

  !loading && console.log(orders)
  return (
    <div
      className={
       'ag-theme-alpine-dark'
      }
      style={{ height: '70vh', width: '100%' }}
    >
      <AgGridReact
        onGridReady={onGridReady}
        rowSelection={'multiple'}
        suppressRowClickSelection={true}
        statusBar={{
          statusPanels: [
            {
              statusPanel: 'agTotalAndFilteredRowCountComponent',
              align: 'left'
            },
            { statusPanel: 'agFilteredRowCountComponent' },
            { statusPanel: 'agSelectedRowCountComponent' },
            { statusPanel: 'agAggregationComponent' }
          ]
        }}
        enableRangeSelection={true}
        masterDetail={true}
        immutableData={true}
        detailRowAutoHeight={true}
        onBodyScroll={event => {
          if (event.direction === 'vertical') {
            visibleRowsChange(event);
          }
        }}
        getRowNodeId={data => {
          return data._id;
        }}
        rowData={!loading && orders}
        detailCellRenderer={'detailRenderer'}
        onFirstDataRendered={params => {
          const autoSizeAll = skipHeader => {
            var allColumnIds = [];
            params.columnApi.getAllColumns().forEach(function(column) {
              allColumnIds.push(column.colId);
            });
            params.columnApi.autoSizeColumns(allColumnIds, skipHeader);
          };
          params.api.sizeColumnsToFit();
          autoSizeAll();
          // params.api.ensureIndexVisible(lastSelectedRow.ordersAll, 'top');
        }}
      >
        <AgGridColumn
          field="orderNo"
          cellRenderer={'agGroupCellRenderer'}
          menuTabs={['filterMenuTab', 'generalMenuTab']}
        />
        <AgGridColumn
          field="saleDate"
          valueFormatter={params => {
            return moment(params.value).format('DD-MM-YY');
          }}
        />
        <AgGridColumn
          field="customer.name"
          headerValueGetter={params => {
            return 'Customer';
          }}
          valueFormatter={function(params) {
            return params.value.replace(/\w\S*/g, function(txt) {
              return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
            });
          }}


        />
      </AgGridReact>
    </div>
  );
};

export default Table;
