import React, { useState, useEffect, useCallback } from "react";
import PropTypes from "prop-types";
import { Autocomplete, Grid, TextField } from "@mui/material";
import { useDispatch } from "../../../../store";

const saleTypeAutocomplete = ({ formik }) => {
  const [open, setOpen] = useState(false);
  const [inputValue, setInputValue] = React.useState("");

  const saleTypes = [
    {
      value: "quantity",
      unit: "MT",
      label: "Per MT",
    },
    {
      value: "quantity",
      unit: "Kg",
      label: "Per Kg",
    },
    {
      value: "quantity",
      unit: "Box",
      label: "Per Box",
    },
    {
      value: "quantity",
      unit: "Pcs",
      label: "Per Pcs",
    },
    {
      value: "time",
      unit: "Days",
      label: "Per Days",
    },
    {
      value: "fixed",
      label: "Fixed",
    },
  ];

  const handleOnChange = (event, newValue) => {
    formik.setFieldValue("saleType", newValue);
  };

  const handleInputChange = (event, newInputValue) => {
    setInputValue(newInputValue);
  };

  return (
    <Autocomplete
      autoSelect={true}
      blurOnSelect={true}
      id="saleType"
      open={open}
      onOpen={() => {
        setOpen(true);
      }}
      onClose={() => {
        setOpen(false);
      }}
      getOptionSelected={(option, value) => {
        if (value)
          return (
            option.label ===
            value.label.replace(/\w\S*/g, function (txt) {
              return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
            })
          );
      }}
      getOptionLabel={(option) => {
        return option && option.label;
      }}
      options={saleTypes}
      value={formik.values.saleType}
      onChange={handleOnChange}
      inputValue={inputValue}
      onInputChange={handleInputChange}
      renderInput={(params) => (
        <TextField
          {...params}
          name="saleType"
          label="Sale Type"
          variant="outlined"
          error={Boolean(formik.touched.saleType && formik.errors.saleType)}
          fullWidth
          helperText={formik.touched.saleType && formik.errors.saleType}
          onBlur={formik.handleBlur}
        />
      )}
    />
  );
};

saleTypeAutocomplete.propTypes = {
  className: PropTypes.string,
};

export default saleTypeAutocomplete;
