import { useEffect, useRef, useState } from "react";
import NextLink from "next/link";
import Head from "next/head";
import { useTranslation } from "react-i18next";
import moment from "moment";
import {
  Box,
  Button,
  Divider,
  Grid,
  InputAdornment,
  Tab,
  Tabs,
  TextField,
  Typography,
} from "@mui/material";
import { styled } from "@mui/material/styles";
import { useDispatch, useSelector } from "../../../store";
import { vehicleApi } from "../../../api/vehicle-api";
import { AuthGuard } from "../../../components/authentication/auth-guard";
import { OnBoardingGuard } from "../../../components/authentication/onboarding-guard";
import { DashboardLayout } from "../../../components/dashboard/dashboard-layout";
import { VehicleDrawer } from "../../../components/dashboard/vehicle/vehicle-drawer";
import VehicleGrid from "../../../components/dashboard/vehicle/vehicle-grid";
import { useMounted } from "../../../hooks/use-mounted";
import { useAuth } from "../../../hooks/use-auth";
import { Plus as PlusIcon } from "../../../icons/plus";
import { Search as SearchIcon } from "../../../icons/search";
import { gtm } from "../../../lib/gtm";

// DataStore Imports
import { DataStore, Predicates } from "@aws-amplify/datastore";
import { Vehicle } from "../../../models";

const tabs = [
  {
    label: "All",
    value: "all",
  },
  {
    label: "Canceled",
    value: "canceled",
  },
  {
    label: "Completed",
    value: "complete",
  },
  {
    label: "Pending",
    value: "pending",
  },
  {
    label: "Rejected",
    value: "rejected",
  },
];

const sortOptions = [
  {
    label: "Newest",
    value: "desc",
  },
  {
    label: "Oldest",
    value: "asc",
  },
];

const applyFilters = (vehicles, filters) =>
  vehicles.filter((vehicle) => {
    if (filters.query) {
      // Checks only the vehicle number, but can be extended to support other fields, such as customer
      // name, email, etc.
      const containsQuery = vehicle.number
        .toLowerCase()
        .includes(filters.query.toLowerCase());

      if (!containsQuery) {
        return false;
      }
    }

    if (typeof filters.status !== "undefined") {
      const statusMatched = vehicle.status === filters.status;

      if (!statusMatched) {
        return false;
      }
    }

    return true;
  });

const applySort = (vehicles, vehicle) =>
  vehicles.sort((a, b) => {
    const comparator = a.createdAt > b.createdAt ? -1 : 1;

    return vehicle === "desc" ? comparator : -comparator;
  });

const VehicleListInner = styled("div", {
  shouldForwardProp: (prop) => prop !== "open",
})(({ theme, open }) => ({
  flexGrow: 1,
  overflow: "hidden",
  paddingBottom: theme.spacing(8),
  paddingTop: theme.spacing(8),
  zIndex: 1,
  [theme.breakpoints.up("lg")]: {
    marginRight: -500,
  },
  transition: theme.transitions.create("margin", {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  ...(open && {
    [theme.breakpoints.up("lg")]: {
      marginRight: 0,
    },
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  }),
}));

const VehicleList = () => {
  const { t } = useTranslation();
  const dispatch = useDispatch();
  const { user } = useAuth();
  const rootRef = useRef(null);
  const { vehicles } = useSelector((state) => state.vehicles);
  console.log(vehicles);
  const [drawer, setDrawer] = useState({
    isOpen: false,
    vehicleId: null,
  });

  const handleOpenDrawer = (params) => {
    setDrawer({
      isOpen: true,
      vehicleId: params.row.id,
    });
  };

  //   useEffect(async () => {
  //     try {
  //       await vehicleApi.getVehiclesByUser(user, dispatch);
  //     } catch (error) {
  //       console.log(error);
  //     }
  //   }, []);

  const handleCloseDrawer = () => {
    setDrawer({
      isOpen: false,
      vehicleId: null,
    });
  };

  return (
    <>
      <Head>
        <title>Dashboard: Vehicle List | Truckar</title>
      </Head>
      <Box
        component="main"
        ref={rootRef}
        sx={{
          backgroundColor: "background.paper",
          display: "flex",
          flexGrow: 1,
          overflow: "hidden",
        }}
      >
        <VehicleListInner open={drawer.isOpen}>
          <Box sx={{ px: 3 }}>
            <Grid container justifyContent="space-between" spacing={3}>
              <Grid item>
                <Typography variant="h4">{t("Vehicles")}</Typography>
              </Grid>
              <Grid item>
                <NextLink href="/dashboard/vehicles/new" passHref>
                  <Button
                    component="a"
                    startIcon={<PlusIcon fontSize="small" />}
                    variant="contained"
                  >
                    Add
                  </Button>
                </NextLink>
              </Grid>
            </Grid>
          </Box>
          <Box sx={{ mt: 3, px: 3, height: "70vh", width: "100%" }}>
            <Divider />
            <VehicleGrid onOpenDrawer={handleOpenDrawer} vehicles={vehicles} />
          </Box>
        </VehicleListInner>
        <VehicleDrawer
          containerRef={rootRef}
          onClose={handleCloseDrawer}
          open={drawer.isOpen}
          vehicle={vehicles.find((vehicle) => vehicle.id === drawer.vehicleId)}
        />
      </Box>
    </>
  );
};

VehicleList.getLayout = (page) => (
  <AuthGuard>
    <OnBoardingGuard>
      <DashboardLayout>{page}</DashboardLayout>
    </OnBoardingGuard>
  </AuthGuard>
);

export default VehicleList;
