import React from "react";
import { DataGrid } from "@mui/x-data-grid";
import { useDispatch } from "../../../store";
import { deliveryDetailsTable } from "../../grids/grid-columns";
import { orderApi } from "../../../api/order-api";

const Table = ({ order, deliveries }) => {
  const dispatch = useDispatch();

  const getOrderUnit = (order) => {
    switch (JSON.parse(order.saleType).value) {
      case "quantity":
        return `${JSON.parse(order.saleType).unit}`;
        break;
      case "fixed":
        return `(Fixed)`;
        break;
      case "time":
        return `${JSON.parse(order.saleType).unit}`;
        break;
      default:
        break;
    }
  };

  const processRowUpdate = React.useCallback(async (newRow) => {
    // Make the HTTP request to save in the backend
    let order = newRow.order;
    delete newRow.order;

    let newDeliveries = JSON.parse(order.deliveries).map((del) => {
      if (del.id !== newRow.id) {
        return del;
      } else {
        return newRow;
      }
    });

    let newOrder = { ...order, deliveries: JSON.stringify(newDeliveries) };

    console.log(newOrder);

    const response = await orderApi.updateOrder(newOrder, dispatch);
    // return response;
  }, []);

  deliveries.map((del) => {
    del.order = order;
    del.unit = getOrderUnit(order);
    return del;
  });
  return (
    <DataGrid
      rows={deliveries}
      autoHeight={true}
      columns={deliveryDetailsTable}
      disableSelectionOnClick
      experimentalFeatures={{ newEditingApi: true }}
      processRowUpdate={processRowUpdate}
    />
  );
};

export default Table;
