/* eslint-disable no-use-before-define */
import React, { useState, useEffect, useCallback } from "react";
import { Formik } from "formik";
import toast from "react-hot-toast";
import { v4 as uuid } from "uuid";
import * as Yup from "yup";

import {
  Box,
  Button,
  Card,
  CardContent,
  Dialog,
  DialogTitle,
  Grid,
  Switch,
  TextField,
  Typography,
} from "@mui/material";
import GooglePlaces from "./google-places-autocomplete";
import { partyApi } from "../../../../api/party-api";
import { useDispatch } from "../../../../store";
const AddNewPartyFromAutocomplete = ({
  open,
  dialogValue,
  setDialogValue,
  setFieldValue,
  toggleOpen,
  type,
  user,
  ...rest
}) => {
  const dispatch = useDispatch();

  return (
    <React.Fragment>
      <Dialog
        open={open}
        onClose={() => toggleOpen(false)}
        aria-labelledby="form-dialog-name"
      >
        <DialogTitle id="form-dialog-name">Add a new party</DialogTitle>
        <Formik
          initialValues={{
            name: dialogValue.name || "",
            mobile: dialogValue.mobile || "",
            city: dialogValue.location || "",
            isTransporter: dialogValue.isTransporter || false,
            submit: null,
          }}
          validationSchema={Yup.object().shape({
            name: Yup.string().max(255).required("Name is required"),
            mobile: Yup.string()
              .matches(/^[6-9]\d{9}$/, "Phone number is not valid")
              .required("Mobile is required"),
          })}
          onSubmit={async (values, { setErrors, setStatus, setSubmitting }) => {
            try {
              const newParty = {
                id: uuid(),
                name: values.name,
                mobile: values.mobile,
                city: JSON.stringify(values.city),
                user: user.id,
                isTransporter: values.isTransporter,
              };
              const response = await partyApi.createParty(newParty, dispatch);
              toast.success("Party created!");
              setFieldValue(type, response);
              toggleOpen(false);
            } catch (err) {
              console.log(err);
              setStatus({ success: false });
              setErrors({ submit: err.message });
              setSubmitting(false);
            }
          }}
        >
          {({
            errors,
            handleBlur,
            handleChange,
            handleSubmit,
            isSubmitting,
            setFieldValue,
            touched,
            values,
          }) => (
            <form onSubmit={handleSubmit}>
              <Card>
                <CardContent>
                  <Grid container spacing={3}>
                    <Grid item xs={12}>
                      <TextField
                        error={Boolean(touched.name && errors.name)}
                        fullWidth
                        helperText={touched.name && errors.name}
                        label="Main Contact Name"
                        name="name"
                        onBlur={handleBlur}
                        onChange={(event) => {
                          setFieldValue(
                            "name",
                            event.target.value.replace(
                              /\w\S*/g,
                              function (txt) {
                                return (
                                  txt.charAt(0).toUpperCase() +
                                  txt.substr(1).toLowerCase()
                                );
                              }
                            )
                          );
                        }}
                        required
                        value={values.name}
                        variant="outlined"
                      />
                    </Grid>
                    <Grid item xs={12}>
                      <TextField
                        error={Boolean(touched.mobile && errors.mobile)}
                        fullWidth
                        helperText={touched.mobile && errors.mobile}
                        label="Mobile"
                        name="mobile"
                        onBlur={handleBlur}
                        onChange={(event) => {
                          setFieldValue(
                            "mobile",
                            event.target.value.replace(/ /g, "")
                          );
                        }}
                        value={values.mobile}
                        variant="outlined"
                      />
                    </Grid>
                    <Grid item xs={12}>
                      <GooglePlaces
                        error={Boolean(touched.mobile && errors.mobile)}
                        name={"City"}
                        field={"city"}
                        setFieldValue={setFieldValue}
                        handleBlur={handleBlur}
                        values={values}
                      />
                    </Grid>
                    <Grid item xs={12}>
                      <Typography variant="h5" color="textPrimary">
                        Party Type
                      </Typography>
                      <Typography variant="body2" color="textSecondary">
                        Is this party a transporter?
                      </Typography>
                      <Switch
                        checked={values.isTransporter}
                        color="secondary"
                        edge="start"
                        name="isTransporter"
                        onChange={handleChange}
                        value={values.isTransporter}
                      />
                    </Grid>
                  </Grid>
                  <Box mt={2}>
                    <Grid container spacing={3}>
                      <Grid item>
                        <Button
                          variant="contained"
                          color="secondary"
                          type="submit"
                          disabled={isSubmitting}
                        >
                          Create Party
                        </Button>
                      </Grid>
                      <Grid item>
                        <Button
                          variant="contained"
                          color="secondary"
                          onClick={() => toggleOpen(false)}
                        >
                          Cancel
                        </Button>
                      </Grid>
                    </Grid>
                  </Box>
                </CardContent>
              </Card>
            </form>
          )}
        </Formik>
      </Dialog>
    </React.Fragment>
  );
};
export default AddNewPartyFromAutocomplete;
