import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { Storage } from "aws-amplify";
import { useFormik } from "formik";
import toast from "react-hot-toast";
import {
  Box,
  Button,
  Divider,
  Drawer,
  IconButton,
  TextField,
  Typography,
  useMediaQuery,
} from "@mui/material";
import { styled } from "@mui/material/styles";
import EditIcon from "@mui/icons-material/Edit";
import { useDispatch } from "../../../store";
import { X as XIcon } from "../../../icons/x";
import { PropertyList } from "../../property-list";
import { PropertyListItem } from "../../property-list-item";
import { vehicleApi } from "../../../api/vehicle-api";
import { VehicleLogoDropzone } from "./vehicle-logo-dropzone";
import { vehicleNumberFormatter } from "../../../utils/customFormatters";

const VehiclePreview = (props) => {
  const dispatch = useDispatch();
  const { lgUp, onEdit, vehicle } = props;
  const align = lgUp ? "horizontal" : "vertical";
  const [vehicleLogo, setVehicleLogo] = useState();
  const [file, setFile] = useState();
  const [croppedImage, setCroppedImage] = useState();

  return (
    <>
      <Box
        sx={{
          alignItems: "center",
          borderRadius: 1,
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
        }}
      >
        <Typography sx={{ mt: 3 }} variant="h6">
          Details
        </Typography>
        <Box
          sx={{
            alignItems: "center",
            display: "flex",
            flexWrap: "wrap",
            mt: 3,
            m: -1,
            "& > button": {
              m: 1,
            },
          }}
        >
          <Button
            onClick={onEdit}
            size="small"
            startIcon={<EditIcon fontSize="small" />}
            sx={{ pt: 3 }}
          >
            Edit
          </Button>
        </Box>
      </Box>
      <Divider sx={{ my: 3 }} />

      <PropertyList>
        <PropertyListItem
          align={align}
          disableGutters
          label="Vehicle Number"
          value={vehicle.vehicleNumber.toUpperCase()}
        />

        <PropertyListItem
          align={align}
          disableGutters
          label="Make"
          value={vehicle.make}
        />
        <PropertyListItem
          align={align}
          disableGutters
          label="Model"
          value={vehicle.model}
        />
      </PropertyList>
    </>
  );
};

const VehicleForm = (props) => {
  const { onCancel, vehicle } = props;
  const dispatch = useDispatch();
  const [file, setFile] = useState();
  const [croppedImage, setCroppedImage] = useState();

  const formik = useFormik({
    initialValues: {
      id: vehicle.id,
      vehicleNumber: vehicle.vehicleNumber,
      make: vehicle.make,
      model: vehicle.model,
      _version: vehicle._version,
    },
    // validationSchema: Yup.object({
    //   name: Yup.string().max(255).required("Name is required"),
    //   initials: Yup.string().max(255).required("Required"),
    //   addressLine1: Yup.string()
    //     .max(255)
    //     .required("Address Line 1 is required"),
    //   city: Yup.string().max(255).required("City is required"),
    //   pincode: Yup.string().max(255).required("Pincode is required"),
    //   gstin: Yup.string()
    //     .trim()
    //     .matches(
    //       /^([0-9]){2}([a-zA-Z]){5}([0-9]){4}([a-zA-Z]){1}([0-9]){1}([a-zA-Z]){1}([0-9]){1}?$/,
    //       "Invalid GST Number"
    //     ),
    //   pan: Yup.string().max(255).required("PAN is required"),
    //   jurisdiction: Yup.string().max(255).required("Jurisdiction is required"),
    // }),
    onSubmit: async (values, helpers) => {
      try {
        // NOTE: Make API request
        const filename = `${vehicle.user}_vehicleLogo_${vehicle.id}`;
        let logoBase64;
        let logo;
        if (values.logo) {
          logoBase64 = await fetch(values.logo);
          logo = await logoBase64.blob();
          values.logo = filename;
          await Storage.put(filename, logo);
        }

        await vehicleApi.updateVehicle(values, dispatch);

        toast.success("Vehicle updated!");
        onCancel();
      } catch (err) {
        console.error(err);
        toast.error("Something went wrong!");
        helpers.setStatus({ success: false });
        helpers.setErrors({ submit: err.message });
        helpers.setSubmitting(false);
      }
    },
  });

  const handleDrop = (newFile) => {
    newFile.length > 0 &&
      setFile(
        Object.assign(newFile[0], {
          preview: URL.createObjectURL(newFile[0]),
        })
      );
  };

  const handleRemove = (file) => {
    setFile();
    setCroppedImage("");
  };

  const handleSaveCroppedImage = (cropper) => {
    setCroppedImage(cropper.getCroppedCanvas().toDataURL());
