export const VehicleDrawer = (props) => {
  const { containerRef, onClose, open, vehicle, ...other } = props;
  const [isEditing, setIsEditing] = useState(false);
  const lgUp = useMediaQuery((theme) => theme.breakpoints.up("lg"));

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setIsEditing(false);
  };

  // The reason for doing this, is that the persistent drawer has to be rendered, but not it's
  // content if an vehicle is not passed.
  const content = vehicle ? (
    <>
      <Box
        sx={{
          alignItems: "center",
          backgroundColor: "primary.main",
          color: "primary.contrastText",
          display: "flex",
          justifyContent: "space-between",
          px: 3,
          py: 2,
        }}
      >
        <Typography color="inherit" variant="h6">
          {vehicle.number}
        </Typography>
        <IconButton color="inherit" onClick={onClose}>
          <XIcon fontSize="small" />
        </IconButton>
      </Box>
      <Box
        sx={{
          px: 3,
          py: 4,
        }}
      >
        {!isEditing ? (
          <VehiclePreview
            onApprove={onClose}
            onEdit={handleEdit}
            onReject={onClose}
            vehicle={vehicle}
            lgUp={lgUp}
          />
        ) : (
          <VehicleForm onCancel={handleCancel} vehicle={vehicle} />
        )}
      </Box>
    </>
  ) : null;

  if (lgUp) {
    return (
      <VehicleDrawerDesktop
        anchor="right"
        open={open}
        SlideProps={{ container: containerRef?.current }}
        variant="persistent"
        {...other}
      >
        {content}
      </VehicleDrawerDesktop>
    );
  }

  return (
    <VehicleDrawerMobile
      anchor="right"
      ModalProps={{ container: containerRef?.current }}
      onClose={onClose}
      open={open}
      SlideProps={{ container: containerRef?.current }}
      variant="temporary"
      {...other}
    >
      {content}
    </VehicleDrawerMobile>
  );
};

VehicleDrawer.propTypes = {
  containerRef: PropTypes.any,
  onClose: PropTypes.func,
  open: PropTypes.bool,
  vehicle: PropTypes.object,
};
