import { createSlice } from "@reduxjs/toolkit";
import _ from "lodash";
import { vehicleApi } from "../api/vehicle-api";
import axios from "../utils/axios";

const initialState = {
  vehicles: [],
  vehicleIds: [],
  isNextPageLoading: true,
};

export const slice = createSlice({
  name: "vehicle",
  initialState,
  reducers: {
    getVehicles(state, action) {
      const vehicles = action.payload;
      state.vehicles = vehicles;
      state.isNextPageLoading = false;
    },
    createVehicle(state, action) {
      const { vehicle } = action.payload;

      state.vehicles = [...state.vehicles, vehicle];
    },
    selectVehicle(state, action) {
      const { vehicleId = null } = action.payload;

      state.isModalOpen = true;
      state.selectedVehicleId = vehicleId;
    },
    updateVehicle(state, action) {
      const { vehicle } = action.payload;

      state.vehicles = _.map(state.vehicles, (_vehicle) => {
        if (_vehicle.id === vehicle.id) {
          return vehicle;
        }

        return _vehicle;
      });
    },
    deleteVehicle(state, action) {
      const { vehicleId } = action.payload;

      state.vehicles = _.reject(state.vehicles, {
        id: vehicleId,
      });
    },
    selectRange(state, action) {
      const { start, end } = action.payload;

      state.isModalOpen = true;
      state.selectedRange = {
        start,
        end,
      };
    },
    openModal(state) {
      state.isModalOpen = true;
    },
    closeModal(state) {
      state.isModalOpen = false;
      state.selectedVehicleId = null;
      state.selectedRange = null;
    },
  },
});

export const reducer = slice.reducer;

export const getVehicles = (limit) => async (dispatch) => {
  const response = await vehicleApi.getVehiclesByUser();
  console.log("getVehicles = ()");
  dispatch(slice.actions.getVehicles(response.data));
};

export const createVehicle = (data) => async (dispatch) => {
  const response = await axios.post("/api/calendar/vehicles/new", data);

  dispatch(slice.actions.createVehicle(response.data));
};

export const selectVehicle = (vehicleId) => async (dispatch) => {
  dispatch(slice.actions.selectVehicle({ vehicleId }));
};

export const updateVehicle = (vehicleId, update) => async (dispatch) => {
  const response = await axios.post("/api/calendar/vehicles/update", {
    vehicleId,
    update,
  });

  dispatch(slice.actions.updateVehicle(response.data));
};

export const deleteVehicle = (vehicleId) => async (dispatch) => {
  await axios.post("/api/calendar/vehicles/remove", {
    vehicleId,
  });

  dispatch(slice.actions.deleteVehicle({ vehicleId }));
};

export const selectRange = (start, end) => (dispatch) => {
  dispatch(
    slice.actions.selectRange({
      start: start.getTime(),
      end: end.getTime(),
    })
  );
};

export const openModal = () => (dispatch) => {
  dispatch(slice.actions.openModal());
};

export const closeModal = () => (dispatch) => {
  dispatch(slice.actions.closeModal());
};

export default slice;
