/* eslint-disable no-use-before-define */
import React, { useState, useEffect, useCallback } from "react";
import * as Yup from "yup";
import { Formik } from "formik";

import {
  Box,
  Button,
  Card,
  CardContent,
  Dialog,
  DialogTitle,
  Grid,
  Switch,
  TextField,
  Typography,
} from "@mui/material";
import { useDispatch } from "../../../store";
import GoogleMaps from "../welcome/google-places-autocomplete";
import { partyApi } from "../../../api/party-api";
import { FormatLineSpacing } from "@mui/icons-material";

const AddNewPartyFromAutocomplete = ({
  open,
  dialogValue,
  setDialogValue,
  toggleOpen,
  type,
  user,
  ...rest
}) => {
  useEffect(async () => {
    try {
      formik.setFieldValue("name", dialogValue.name);
    } catch (error) {
      console.log(error);
    }
  }, []);

  return (
    <React.Fragment>
      <form>
        <Dialog
          open={open}
          onClose={toggleOpen()}
          aria-labelledby="form-dialog-name"
        >
          <DialogTitle id="form-dialog-name">Add a new party</DialogTitle>
          <Formik
            initialValues={{
              name: dialogValue.name || "",
              mobile: dialogValue.mobile || "",
              location: dialogValue.location || "",
              transporter: dialogValue.transporter || "",
              submit: null,
            }}
            validationSchema={Yup.object().shape({
              name: Yup.string().max(255).required("Name is required"),
              mobile: Yup.string()
                .matches(/^[6-9]\d{9}$/, "Phone number is not valid")
                .required("Mobile is required"),
            })}
            onSubmit={async (
              values,
              { resetForm, setErrors, setStatus, setSubmitting }
            ) => {
              try {
                // NOTE: Make API request
                const response = await axios.post(`/api/parties/`, {
                  name: values.name.replace(/\w\S*/g, function (txt) {
                    return (
                      txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase()
                    );
                  }),
                  ...values,
                });
                setFieldValue(type, response.data);
                resetForm();
                setStatus({ success: true });
                setSubmitting(false);
                enqueueSnackbar("Party updated", {
                  variant: "success",
                  action: <Button>See all</Button>,
                });
                toggleOpen();
              } catch (err) {
                console.log(err);
                setStatus({ success: false });
                setErrors({ submit: err.message });
                setSubmitting(false);
              }
            }}
          >
            {({
              errors,
              handleBlur,
              handleChange,
              handleSubmit,
              isSubmitting,
              setFieldValue,
              touched,
              values,
            }) => (
              <form
                className={clsx(classes.root, className)}
                onSubmit={handleSubmit}
                {...rest}
              >
                <Card>
                  <CardContent>
                    <Grid container spacing={3}>
                      <Grid item xs={12}>
                        <TextField
                          error={Boolean(touched.name && errors.name)}
                          fullWidth
                          helperText={touched.name && errors.name}
                          label="Main Contact Name"
                          name="name"
                          onBlur={handleBlur}
                          onChange={(event) => {
                            setFieldValue(
                              "name",
                              event.target.value.replace(
                                /\w\S*/g,
                                function (txt) {
                                  return (
                                    txt.charAt(0).toUpperCase() +
                                    txt.substr(1).toLowerCase()
                                  );
                                }
                              )
                            );
                          }}
                          required
                          value={values.name}
                          variant="outlined"
                        />
                      </Grid>
                      <Grid item xs={12}>
                        <TextField
                          error={Boolean(touched.mobile && errors.mobile)}
                          fullWidth
                          helperText={touched.mobile && errors.mobile}
                          label="Mobile"
                          name="mobile"
                          onBlur={handleBlur}
                          onChange={(event) => {
                            setFieldValue(
                              "mobile",
                              event.target.value.replace(/ /g, "")
                            );
                          }}
                          value={values.mobile}
                          variant="outlined"
                        />
                      </Grid>
                      <Grid item xs={12}>
                        <GoogleMaps
                          error={Boolean(touched.mobile && errors.mobile)}
                          name={"location"}
                          field={"location"}
                          setFieldValue={setFieldValue}
                          handleBlur={handleBlur}
                          values={values}
                        />
                      </Grid>
                      <Grid item xs={12}>
                        <Typography variant="h5" color="textPrimary">
                          Party Type
                        </Typography>
                        <Typography variant="body2" color="textSecondary">
                          Is this party a transporter?
                        </Typography>
                        <Switch
                          checked={values.transporter}
                          color="secondary"
                          edge="start"
                          name="transporter"
                          onChange={handleChange}
                          value={values.transporter}
                        />
                      </Grid>
                    </Grid>
                    <Box mt={2}>
                      <Grid container spacing={3}>
                        <Grid item>
                          <Button
                            variant="contained"
                            color="secondary"
                            type="submit"
                            disabled={isSubmitting}
                          >
                            Create Party
                          </Button>
                        </Grid>
                        <Grid item>
                          <Button
                            variant="contained"
                            color="secondary"
                            onClick={handleClose}
                          >
                            Cancel
                          </Button>
                        </Grid>
                      </Grid>
                    </Box>
                  </CardContent>
                </Card>
              </form>
            )}
          </Formik>
        </Dialog>
      </form>
    </React.Fragment>
  );
};
export default AddNewPartyFromAutocomplete;
