import { API } from "aws-amplify";
import { getUser } from "../graphql/queries";

class UserApi {
  async getUser(user) {
    const User = await API.graphql({
      query: getUser,
      variables: { user: user.id.toString() },
    });
    return User;
  }
}

export const userApi = new UserApi();
