import { forwardRef, useImperativeHandle } from "react";
import toast from "react-hot-toast";
import * as Yup from "yup";
import { useFormik } from "formik";
import { Divider, Grid, TextField, Typography } from "@mui/material";
import GoogleMaps from "./google-places-autocomplete";
import { useAuth } from "../../../hooks/use-auth";
import { branchApi } from "../../../api/branch-api";
import { userApi } from "../../../api/user-api";

export const BranchCreateForm = forwardRef(
  ({ handleNext, setActiveStep, ...props }, ref) => {
    const { user } = useAuth();

    useImperativeHandle(ref, () => ({
      handleFormSubmit() {
        formik.handleSubmit();
      },
    }));

    const formik = useFormik({
      initialValues: {
        branchName: "",
        city: "",
        branchType: "main",
        submit: null,
      },
      validationSchema: Yup.object({
        branchName: Yup.string()
          .max(255)
          .required("Please enter a branch name."),
        city: Yup.object().required("Please select a city."),
      }),
      onSubmit: async (values, helpers) => {
        try {
          const newBranch = {
            branchName: values.branchName,
            city: JSON.stringify(values.city),
            branchType: values.branchType,
            user: user.id,
          };

          await branchApi.createBranch(newBranch);
          await userApi.updateUser({
            onBoardingRequired: false,
            username: user.id,
          });
          toast.success("Branch created!");
          setActiveStep(1);
          // router.push("/dashboard/branches");
        } catch (err) {
          console.error(err);
          toast.error("Something went wrong!");
          helpers.setStatus({ success: false });
          helpers.setErrors({ submit: err.message });
          helpers.setSubmitting(false);
        }
      },
    });

    return (
      <form onSubmit={formik.handleSubmit} {...props}>
        <Grid container spacing={5} alignItems="center" sx={{ mb: 4, mt: 2 }}>
          <Grid item md={4} xs={12}>
            <Typography variant="h6">Tell us your city</Typography>
          </Grid>
          <Grid item md={8} xs={12}>
            <GoogleMaps
              formik={formik}
              error={Boolean(formik.touched.city && formik.errors.city)}
              label={"Your city in India"}
              field={"city"}
              setFieldValue={formik.setFieldValue}
              handleBlur={formik.handleBlur}
              values={formik.values}
            />
          </Grid>
          <Grid item xs={12}>
            <Divider />
          </Grid>
          <Grid item md={4} xs={12}>
            <Typography variant="h6">Please pick a name</Typography>
          </Grid>
          <Grid item md={8} xs={12}>
            <TextField
              error={Boolean(
                formik.touched.branchName && formik.errors.branchName
              )}
              fullWidth
              helperText={formik.touched.branchName && formik.errors.branchName}
              label="Your branch name"
              name="branchName"
              onBlur={formik.handleBlur}
              onChange={formik.handleChange}
              value={formik.values.branchName}
            />
          </Grid>
        </Grid>
      </form>
    );
  }
);
