import { Avatar, Link } from "@mui/material";
import moment from "moment";

const name = {
  type: "string",
};

export const organisationTable = [
  {
    field: "initials",
    headerName: "ðŸ¢",
    headerAlign: "center",
    width: 60,
    editable: true,
    renderCell: (params) => {
      return <Avatar>{params.value}</Avatar>;
    },
  },
  {
    field: "name",
    headerName: "Business Name",
    editable: true,
    width: 250,
  },
  {
    field: "city",
    headerName: "City",
    width: 150,
    editable: true,
  },
  {
    field: "gstin",
    headerName: "GSTIN",
    width: 180,
    editable: true,
  },
];

export const vehicleTable = [
  {
    field: "initials",
    headerName: "ðŸšš",
    headerAlign: "center",
    sx: {
      fontSize: 50,
    },
    width: 20,
  },
  {
    field: "vehicleNumber",
    headerName: "Vehicle Number",
    width: 250,
  },
  {
    field: "make",
    headerName: "Make",
    width: 150,
  },
  {
    field: "model",
    headerName: "Model",
    width: 180,
  },
];

export const branchTable = [
  {
    field: "initials",
    headerName: "ðŸ¢",
    headerAlign: "center",
    width: 20,
  },
  {
    field: "branchName",
    headerName: "Branch Name",
    width: 250,
  },
  {
    field: "city",
    headerName: "City",
    width: 150,
    valueGetter: (params) => {
      return `${JSON.parse(params.row.city).structured_formatting.main_text}`;
    },
  },
];

export const partyTable = [
  {
    field: "name",
    headerName: "Name",
    width: 250,
  },
  {
    field: "city",
    headerName: "City",
    width: 250,
    valueGetter: (params) => {
      return `${JSON.parse(params.row.city).description}`;
    },
  },
  {
    field: "mobile",
    headerName: "Mobile",
    width: 150,
  },
];

export const driverTable = [
  {
    field: "initials",
    headerName: "ðŸ¢",
    headerAlign: "center",
    width: 20,
  },
  {
    field: "name",
    headerName: "Name",
    width: 250,
  },
  {
    field: "mobile",
    headerName: "Mobile",
    width: 150,
  },
];

export const orderTable = [
  {
    field: "initials",
    headerName: "ðŸšš",
    headerAlign: "center",
    sx: {
      fontSize: 50,
    },
    width: 20,
  },
  {
    field: "orderNo",
    headerName: "Order No",
    width: 130,
  },
  {
    field: "saleDate",
    headerName: "Date",
    width: 130,
    valueGetter: (params) => {
      if (params.value) {
        return moment(params.value).format("DD/MM/YY");
      }
    },
  },
  {
    field: "customer",
    headerName: "Customer",
    width: 250,
    valueGetter: (params) => {
      if (params.value) {
        return params.value.name;
      }
    },
  },
  {
    field: "route",
    headerName: "Route",
    width: 250,
    valueGetter: (params) => {
      let deliveries = JSON.parse(params.row.deliveries);
      let route = [];
      let waypoints = [];

      deliveries.map((delivery, index) => {
        if (index === 0)
          route[0] = delivery.loading.structured_formatting.main_text;
        if (index === deliveries.length - 1)
          route[-1] = delivery.unloading.structured_formatting.main_text;
        waypoints.push(delivery.loading.structured_formatting.main_text);
        waypoints.push(delivery.unloading.structured_formatting.main_text);
      });

      waypoints = waypoints.filter(
        (waypoint) =>
          waypoint !== deliveries[0].loading.structured_formatting.main_text
      );
      waypoints = waypoints.filter(
        (waypoint) =>
          waypoint !==
          deliveries[deliveries.length - 1].unloading.structured_formatting
            .main_text
      );

      waypoints = [...new Map(waypoints.map((item) => [item, item])).values()];

      return [route[0], ...waypoints, route[-1]].join("-");
    },
  },
  {
    field: "vehicleNumber",
    headerName: "Vehicle Number",
    width: 250,
  },
];

export const deliveryDetailsTable = [
  {
    field: "lr",
    headerName: "LR",
    width: 90,
    renderCell: () => (
      <Link
        color="secondary"
        href={`/app/management/accounts/lr`}
        variant="body"
      >
        Make LR
      </Link>
    ),
  },
  {
    field: "loading",
    headerName: "Loading",
    width: 120,
    valueGetter: (params) => {
      if (params.value) {
        return params.value.structured_formatting.main_text;
      }
    },
  },
  {
    field: "unloading",
    headerName: "Unloading",
    width: 120,
    valueGetter: (params) => {
      if (params.value) {
        return params.value.structured_formatting.main_text;
      }
    },
  },
  {
    field: "billQuantity",
    headerName: "Bill Wt",
    width: 90,
    editable: true,
    valueFormatter: (params) => {
      return `${params.value} ${params.api.getRow(params.id).unit}`;
    },
  },
  {
    field: "unloadingQuantity",
    headerName: "Unloading Wt",
    width: 120,
    editable: true,
    valueFormatter: (params) => {
      if (params.value) {
        return `${params.value} ${params.api.getRow(params.id).unit}`;
      } else {
        return `0 ${params.api.getRow(params.id).unit}`;
      }
    },
  },
];
