import { API } from "aws-amplify";
import { ordersByUser } from "../graphql/queries";

const now = new Date();

class OrderApi {
  async getOrdersByUser(user) {
    const ordersDB = await API.graphql({
      query: ordersByUser,
      variables: { user: user.id.toString() },
    });
    return ordersDB;
  }

  async validateDuplicateOrderNo(orderNo, user) {
    const response = await API.graphql({
      query: ordersByUser,
      variables: { user: user.id.toString() },
    });
    const orders = response.data.ordersByUser.items;
    console.log(response);

    const order = orders.find((order) => {
      return order.orderNo === orderNo;
    });
    return Boolean(!order);
  }
}

export const orderApi = new OrderApi();
