import { useState } from "react";
import PropTypes from "prop-types";
import { format } from "date-fns";
import numeral from "numeral";
import {
  Box,
  Button,
  Divider,
  Drawer,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  TextField,
  Typography,
  useMediaQuery,
} from "@mui/material";
import { styled } from "@mui/material/styles";
import EditIcon from "@mui/icons-material/Edit";
import { X as XIcon } from "../../../icons/x";
import { PropertyList } from "../../property-list";
import { PropertyListItem } from "../../property-list-item";
import { Scrollbar } from "../../scrollbar";

const OrganisationPreview = (props) => {
  const { lgUp, onApprove, onEdit, onReject, organisation } = props;
  const align = lgUp ? "horizontal" : "vertical";

  return (
    <>
      <Box
        sx={{
          alignItems: "center",
          borderRadius: 1,
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
        }}
      >
        <Typography sx={{ mt: 3 }} variant="h6">
          Details
        </Typography>
        <Box
          sx={{
            alignItems: "center",
            display: "flex",
            flexWrap: "wrap",
            mt: 3,
            m: -1,
            "& > button": {
              m: 1,
            },
          }}
        >
          <Button
            onClick={onEdit}
            size="small"
            startIcon={<EditIcon fontSize="small" />}
            sx={{ pt: 3 }}
          >
            Edit
          </Button>
        </Box>
      </Box>
      <Divider sx={{ my: 3 }} />

      <PropertyList>
        <PropertyListItem align={align} disableGutters label="Business Name">
          <Typography color="primary" variant="body2">
            {organisation.name}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.addressLine1}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.addressLine2}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.city}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.pincode}
          </Typography>
        </PropertyListItem>

        <PropertyListItem align={align} disableGutters label="Bank Details">
          <Typography color="textSecondary" variant="body2">
            {organisation.bankName}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.bankAccountNumber &&
              `A/c: ${organisation.bankAccountNumber}`}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.bankIFSC && `IFSC: ${organisation.bankIFSC}`}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.bankBranch && `Branch: ${organisation.bankBranch}`}
          </Typography>
        </PropertyListItem>

        <Typography sx={{ mt: 3 }} variant="h6">
          Billing Details
        </Typography>
        <Divider sx={{ my: 3 }} />

        <PropertyListItem
          align={align}
          disableGutters
          label="PAN"
          value={organisation.pan}
        />

        <PropertyListItem
          align={align}
          disableGutters
          label="GSTIN"
          value={organisation.gstin}
        />

        <PropertyListItem
          align={align}
          disableGutters
          label="Contact No."
          value={organisation.contact}
        />

        <PropertyListItem
          align={align}
          disableGutters
          label="E-mail"
          value={organisation.email}
        />

        <PropertyListItem
          align={align}
          disableGutters
          label="Terms & Conditions"
          value={organisation.email}
        />
      </PropertyList>
    </>
  );
};

const OrganisationForm = (props) => {
  const { onCancel, onSave, organisation } = props;

  return (
    <>
      <Box
        sx={{
          alignItems: "center",
          backgroundColor: (theme) =>
            theme.palette.mode === "dark" ? "neutral.800" : "neutral.100",
          borganisationRadius: 1,
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
          px: 3,
          py: 2.5,
        }}
      >
        <Typography variant="overline" sx={{ mr: 2 }} color="textSecondary">
          Organisation
        </Typography>
        <Box
          sx={{
            alignItems: "center",
            display: "flex",
            m: -1,
            "& > button": {
              m: 1,
            },
          }}
        >
          <Button
            color="primary"
            onClick={onSave}
            size="small"
            variant="contained"
          >
            Save changes
          </Button>
          <Button onClick={onCancel} size="small" variant="outlined">
            Cancel
          </Button>
        </Box>
      </Box>
      <Typography sx={{ my: 3 }} variant="h6">
