import * as React from "react";
import { useRef } from "react";
import {
  Box,
  Button,
  Card,
  CardContent,
  Divider,
  Grid,
  Typography,
  Stepper,
  Step,
  StepButton,
} from "@mui/material";
import { ArrowRight as ArrowRightIcon } from "../../../icons/arrow-right";
import { Briefcase as BriefcaseIcon } from "../../../icons/briefcase";
import { useTranslation } from "react-i18next";
import { BranchCreateForm } from "../../../components/dashboard/welcome/branch-create-form";

const steps = ["Create a main branch", "Add team members", "Create vehicles"];

export default function HorizontalNonLinearStepper() {
  const branchCreateFormRef = useRef(null);
  const [activeStep, setActiveStep] = React.useState(0);
  const [completed, setCompleted] = React.useState({});
  const { t } = useTranslation();

  const handleNext = () => {
    const newActiveStep = activeStep + 1;
    if (activeStep === 0) {
      branchCreateFormRef.current.handleFormSubmit();
      handleComplete();
    } else {
      handleComplete();
      setActiveStep(newActiveStep);
    }
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleStep = (step) => () => {
    setActiveStep(step);
  };

  const handleComplete = () => {
    const newCompleted = completed;
    newCompleted[activeStep] = true;
    setCompleted(newCompleted);
  };

  const handleReset = () => {
    setActiveStep(0);
    setCompleted({});
  };

  const createAMainBranch = (
    <Grid item xs={12} sx={{ py: 3 }}>
      <Card>
        <CardContent>
          <Box
            sx={{
              alignItems: "center",
              display: "flex",
            }}
          >
            <BriefcaseIcon color="primary" fontSize="small" />
            <Typography color="primary.main" sx={{ pl: 1 }} variant="subtitle2">
              {t("Add your main branch details")}
            </Typography>
          </Box>
          <Typography variant="h5" sx={{ mt: 2 }}>
            {t("Let us open your branch.")}
          </Typography>
          <Box
            sx={{
              alignItems: "center",
              display: "flex",
            }}
          >
            <BranchCreateForm
              handleNext={handleNext}
              ref={branchCreateFormRef}
              setActiveStep={setActiveStep}
            />
          </Box>
        </CardContent>
        <Divider />
      </Card>
    </Grid>
  );

  return (
    <Box sx={{ minWidth: "100%" }}>
      <Stepper nonLinear activeStep={activeStep}>
        {steps.map((label, index) => (
          <Step key={label} completed={completed[index]}>
            <StepButton color="inherit" onClick={handleStep(index)}>
              {label}
            </StepButton>
          </Step>
        ))}
      </Stepper>

      {activeStep === 0 && createAMainBranch}
      <Box sx={{ display: "flex", flexDirection: "row", pt: 2 }}>
        <Button
          color="inherit"
          disabled={activeStep !== 2}
          onClick={handleBack}
          sx={{ mr: 1 }}
        >
          Back
        </Button>
        <Box sx={{ flex: "1 1 auto" }} />
        <Button onClick={handleNext} sx={{ mr: 1 }}>
          Next
        </Button>
        {
          <Button onClick={handleComplete}>
            {activeStep !== 0 && "Finish"}
          </Button>
        }
      </Box>
    </Box>
  );
}
