import React, { useState, useRef, useMemo, useCallback } from "react";
import { useRouter } from "next/router";
import toast from "react-hot-toast";
import * as Yup from "yup";
import { useFormik } from "formik";
import moment from "moment";
import { useDebouncedCallback } from "use-debounce";
import {
  GoogleMap,
  Marker,
  DirectionsService,
  DirectionsRenderer,
} from "@react-google-maps/api";

import {
  Box,
  Button,
  Card,
  CardContent,
  Grid,
  TextField,
  Typography,
} from "@mui/material";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import { useAuth } from "../../../hooks/use-auth";
import DeliveryDetails from "./delivery-details";
import PartyAutocomplete from "../autocompletes/party-autocomplete/party-autocomplete";
import VehicleAutocomplete from "../autocompletes/vehicle-autocomplete/vehicle-autocomplete";
import DriverAutocomplete from "../autocompletes/driver-autocomplete/driver-autocomplete";

export const OrderCreateForm = (props) => {
  const router = useRouter();
  const { user } = useAuth();
  const [selectedVehicle, setSelectedVehicle] = useState(null);
  const [driver, setDriver] = useState();

  const [saleType, setSaleType] = React.useState("quantity");
  const [purchaseType, setPurchaseType] = React.useState("quantity");

  const saleTypes = [
    {
      value: "quantity",
      label: "Per Ton",
    },
    {
      value: "fixed",
      label: "Fixed",
    },
  ];

  const purchaseTypes = [
    {
      value: "quantity",
      label: "Per Ton",
    },
    {
      value: "fixed",
      label: "Fixed",
    },
  ];

  let validationShape = {
    customer: Yup.object().nullable().required("Customer is required"),
    vehicle: Yup.lazy((value) => {
      switch (typeof value) {
        case "object":
          return Yup.object().nullable().required("Vehicle is required");
        case "string":
          return Yup.string().required("Vehicle is required");
        default:
          return Yup.string().required("Vehicle is required");
      }
    }),
  };

  validationShape.deliveryDetails = Yup.array().of(
    Yup.object().shape({
      loading: Yup.object().required("Loading Point is Required"), // these constraints take precedence
      unloading: Yup.object().required("Unloading Point is Required"), // these constraints take precedence
    })
  );

  const formik = useFormik({
    initialValues: {
      saleDate: moment(),
      deliveryDetails: [
        {
          loading: "",
          unloading: "",
        },
      ],
    },
    // validationSchema: Yup.object().shape(validationShape),
    onSubmit: async (values, helpers) => {
      try {
        console.log(values);

        const newOrder = {
          orderNo: parseInt(values.orderNo),
          saleDate: values.saleDate.format(),
          customer: values.customer,
          vehicle: values.vehicle,
          user: user.id,
        };

        toast.success("Order created!");
        // router.push("/dashboard/orders");
      } catch (err) {
        console.error(err);
        toast.error("Something went wrong!");
        helpers.setStatus({ success: false });
        helpers.setErrors({ submit: err.message });
        helpers.setSubmitting(false);
      }
    },
  });
  // Google Maps Shiz

  const [response, setResponse] = useState(null);
  const [origin, setOrigin] = useState("");
  const [destination, setDestination] = useState("");
  const originRef = useRef(null);
  const destinationRef = useRef(null);

  const directionsServiceOptions = useMemo(() => {
    return {
      destination: destination,
      origin: origin,
      travelMode: "DRIVING",
    };
  }, []);

  const directionsRendererOptions = useMemo(() => {
    return {
      directions: response,
    };
  }, []);

  let directionsCallback = (response) => {
    // if (response !== null) {
    //   if (response.status === "OK") {
    //     console.log(response);
    //     setResponse(response);
    //   } else {
    console.log("response: ");
    //   }
    // }
  };

  const debounced = useDebouncedCallback(
    (field, value) => formik.setFieldValue(field, value),
    0
  );

  React.useEffect(() => {
    // Submit the form imperatively as an effect as soon as form values.token are 6 digits long
    console.log(formik);
  }, [formik]);

  return (
    <form onSubmit={formik.handleSubmit} {...props}>
      <Card>
        <CardContent>
          <Grid container spacing={3}>
            <Grid item md={4} xs={12}>
              <Typography variant="h6">Basic details</Typography>
            </Grid>
            <Grid item md={8} xs={12}>
              <Grid container spacing={3}>
                <Grid item md={4} xs={12}>
                  <TextField
                    error={Boolean(
                      formik.touched.orderNo && formik.errors.orderNo
                    )}
                    fullWidth
                    helperText={formik.touched.orderNo && formik.errors.orderNo}
                    label="Order No"
                    name="orderNo"
                    // onBlur={formik.handleBlur}
                    onChange={(e) => {
                      const { name, value } = e.target;
                      debounced(name, value);
                    }}
                    value={formik.values.orderNo}
                  />
                </Grid>
                <Grid item md={4} xs={12}>
                  <DatePicker
                    id="saleDate"
                    name="saleDate"
                    label="Sale date"
                    showTodayButton={true}
                    inputFormat="DD/MM/YYYY"
                    value={formik.values.saleDate}
                    onClick={() => setFieldTouched("end")}
                    onChange={(date) =>
                      formik.setFieldValue("saleDate", moment(date))
                    }
                    renderInput={(params) => (
                      <TextField fullWidth {...params} />
                    )}
                  />
                </Grid>
                <Grid item md={4} xs={12}>
                  <PartyAutocomplete
                    errors={formik.errors}
                    touched={formik.touched}
                    setFieldValue={formik.setFieldValue}
                    handleBlur={formik.handleBlur}
                    type="customer"
                    user={user}
                    formik={formik}
                  />
                </Grid>
              </Grid>
            </Grid>
          </Grid>
        </CardContent>
      </Card>
      <Card sx={{ mt: 3 }}>
        <CardContent>
          <Grid container spacing={3}>
            <Grid item md={4} xs={12}>
              <Typography variant="h6">Delivery details</Typography>
              {console.log(response)}
              {/* <GoogleMap
                options={{ mapTypeId: "hybrid", disableDefaultUI: true }}
                mapContainerStyle={{
                  width: "90%",
                  height: "100%",
                  minHeight: "12rem",
                }}
                center={{
                  lat: 22.309425,
                  lng: 72.13623,
                }}
                zoom={5}
              >
                {
                  <DirectionsService
                    // required
                    options={{
                      origin:
                        formik.values.deliveryDetails[0].loading.description,
                      destination:
                        formik.values.deliveryDetails[0].unloading.description,
                      travelMode: "DRIVING",
                    }}
                    // required
                    callback={directionsCallback}
                    // optional
                    onLoad={(directionsService) => {
                      console.log(
                        "DirectionsService onLoad directionsService: ",
                        directionsService
                      );
                    }}
                    // optional
                    onUnmount={(directionsService) => {
                      console.log(
                        "DirectionsService onUnmount directionsService: ",
                        directionsService
                      );
                    }}
                  />
                }

                {
                  <DirectionsRenderer
                    // required
                    options={{
                      // eslint-disable-line react-perf/jsx-no-new-object-as-prop
                      directions: response,
                    }}
                    // optional
                    onLoad={(directionsRenderer) => {
                      console.log(
                        "DirectionsRenderer onLoad directionsRenderer: ",
                        directionsRenderer
                      );
                    }}
                    // optional
                    onUnmount={(directionsRenderer) => {
                      console.log(
                        "DirectionsRenderer onUnmount directionsRenderer: ",
                        directionsRenderer
                      );
                    }}
                  />
                }
                {formik.values.deliveryDetails.map((deliveries) => {
                  return (
                    <React.Fragment>
                      <Marker
                        key={deliveries.loading.place_id}
                        position={{
                          lat: deliveries.loading.latitude,
                          lng: deliveries.loading.longitude,
                        }}
                      />
                      <Marker
                        key={deliveries.unloading.place_id}
                        position={{
                          lat: deliveries.unloading.latitude,
                          lng: deliveries.unloading.longitude,
                        }}
                      />
                    </React.Fragment>
                  );
                })}
              </GoogleMap> */}
            </Grid>
            <Grid item md={8} xs={12}>
              <DeliveryDetails formik={formik} />
            </Grid>
          </Grid>
        </CardContent>
      </Card>
      <Card sx={{ mt: 3 }}>
        <CardContent>
          <Grid container spacing={3}>
            <Grid item md={4} xs={12}>
              <Typography variant="h6">Vehicle details</Typography>
            </Grid>
            <Grid item md={8} xs={12}>
              <Grid container spacing={3}>
                <Grid item md={4} xs={12}>
                  <VehicleAutocomplete
                    errors={formik.errors}
                    touched={formik.touched}
                    setFieldValue={formik.setFieldValue}
                    handleBlur={formik.handleBlur}
                    setSelectedVehicle={setSelectedVehicle}
                    setDriver={setDriver}
                    user={user}
                  />
                </Grid>
                <Grid item md={4} xs={12}>
                  {typeof selectedVehicle === "object" &&
                  selectedVehicle !== null ? (
                    <DriverAutocomplete
                      errors={formik.errors}
                      touched={formik.touched}
                      setFieldValue={formik.setFieldValue}
                      handleBlur={formik.handleBlur}
                      setSelectedVehicle={setSelectedVehicle}
                      setDriver={setDriver}
                      user={user}
                      values={formik.values}
                    />
                  ) : (
                    selectedVehicle !== null && (
                      <PartyAutocomplete
                        errors={formik.errors}
                        touched={formik.touched}
                        setFieldValue={formik.setFieldValue}
                        handleBlur={formik.handleBlur}
                        type="transporter"
                        user={user}
                        formik={formik}
                      />
                    )
                  )}
                </Grid>
              </Grid>
            </Grid>
          </Grid>
        </CardContent>
      </Card>
      <Card sx={{ mt: 3 }}>
        <CardContent>
          <Grid container spacing={3}>
            <Grid item md={4} xs={12}>
              <Typography variant="h6">Sale details</Typography>
            </Grid>
            <Grid item md={8} xs={12}>
              <Grid container spacing={3}>
                <Grid item md={4} xs={12}>
                  <TextField
                    error={Boolean(
                      formik.touched.saleType && formik.errors.saleType
                    )}
                    onBlur={formik.handleBlur}
                    name="saleType"
                    label="Sale Type"
                    InputLabelProps={{
                      shrink: true,
                    }}
                    fullWidth
                    id="saleType"
                    select
                    value={saleType}
                    onChange={(event) => {
                      setSaleType(event.target.value);
                      formik.setFieldValue("saleType", event.target.value);
                    }}
                    SelectProps={{
                      native: true,
                    }}
                    helperText="Please select sale type"
                    variant="outlined"
                  >
                    {saleTypes.map((option) => (
                      <option key={option.value} value={option.value}>
                        {option.label}
                      </option>
                    ))}
                  </TextField>
                </Grid>
                <Grid item md={4} xs={12}>
                  <TextField
                    error={Boolean(
                      formik.touched.saleRate && formik.errors.saleRate
                    )}
                    variant="outlined"
                    onChange={formik.handleChange}
                    onBlur={formik.handleBlur}
                    helperText={
                      formik.touched.saleRate && formik.errors.saleRate
                    }
                    id="saleRate"
                    name="saleRate"
                    label="Sale Rate"
                    fullWidth
                  />
                </Grid>
                {saleType === "quantity" && (
                  <Grid item md={4} xs={6}>
                    <TextField
                      error={Boolean(
                        formik.touched.saleMinimumGuarantee &&
                          formik.errors.saleMinimumGuarantee
                      )}
                      variant="outlined"
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      helperText={
                        formik.touched.saleMinimumGuarantee &&
                        formik.errors.saleMinimumGuarantee
                      }
                      id="saleMinimumGuarantee"
                      name="saleMinimumGuarantee"
                      label="Min. Load Guarantee"
                      fullWidth
                    />
                  </Grid>
                )}
              </Grid>
            </Grid>
          </Grid>
        </CardContent>
      </Card>
      {!(typeof selectedVehicle === "object") && selectedVehicle !== null && (
        <Card sx={{ mt: 3 }}>
          <CardContent>
            <Grid container spacing={3}>
              <Grid item md={4} xs={12}>
                <Typography variant="h6">Purchase details</Typography>
              </Grid>
              <Grid item md={8} xs={12}>
                <Grid container spacing={3}>
                  <Grid item md={4} xs={12}>
                    <TextField
                      error={Boolean(
                        formik.touched.purchaseType &&
                          formik.errors.purchaseType
                      )}
                      onBlur={formik.handleBlur}
                      name="purchaseType"
                      label="Purchase Type"
                      InputLabelProps={{
                        shrink: true,
                      }}
                      fullWidth
                      id="purchaseType"
                      select
                      value={purchaseType}
                      onChange={(event) => {
                        setPurchaseType(event.target.value);
                        event.target.value;
                        formik.setFieldValue(
                          "purchaseType",
                          event.target.value
                        );
                      }}
                      SelectProps={{
                        native: true,
                      }}
                      helperText="Please select purchase type"
                      variant="outlined"
                    >
                      {purchaseTypes.map((option) => (
                        <option key={option.value} value={option.value}>
                          {option.label}
                        </option>
                      ))}
                    </TextField>
                  </Grid>
                  <Grid item md={4} xs={12}>
                    <TextField
                      error={Boolean(
                        formik.touched.purchaseRate &&
                          formik.errors.purchaseRate
                      )}
                      variant="outlined"
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      helperText={
                        formik.touched.purchaseRate &&
                        formik.errors.purchaseRate
                      }
                      id="purchaseRate"
                      name="purchaseRate"
                      label="Purchase Rate"
                      fullWidth
                    />
                  </Grid>
                  {purchaseType === "quantity" && (
                    <Grid item md={4} xs={6}>
                      <TextField
                        error={Boolean(
                          formik.touched.purchaseMinimumGuarantee &&
                            formik.errors.purchaseMinimumGuarantee
                        )}
                        variant="outlined"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        helperText={
                          formik.touched.purchaseMinimumGuarantee &&
                          formik.errors.purchaseMinimumGuarantee
                        }
                        id="purchaseMinimumGuarantee"
                        name="purchaseMinimumGuarantee"
                        label="Min. Load Guarantee"
                        fullWidth
                      />
                    </Grid>
                  )}
                </Grid>
              </Grid>
            </Grid>
          </CardContent>
        </Card>
      )}
      <Box
        sx={{
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
          mx: -1,
          mb: -1,
          mt: 3,
        }}
      >
        <Button
          color="error"
          sx={{
            m: 1,
            mr: "auto",
          }}
        >
          Delete
        </Button>
        <Button sx={{ m: 1 }} variant="outlined">
          Cancel
        </Button>
        <Button sx={{ m: 1 }} type="submit" variant="contained">
          Create
        </Button>
      </Box>
    </form>
  );
};
