import { useRef, useState } from "react";
import { v4 as uuidv4 } from "uuid";
import { useTranslation } from "react-i18next";
import { toast } from "react-hot-toast";
import { useFormik, FormikProvider, FieldArray, getIn } from "formik";
import * as Yup from "yup";
import { useAuth } from "../../../hooks/use-auth";

import {
  Box,
  Button,
  Card,
  CardContent,
  Checkbox,
  FormGroup,
  FormControlLabel,
  Grid,
  IconButton,
  TextField,
  Typography,
} from "@mui/material";
import { Plus as PlusIcon } from "../../../icons/plus";

export const AccountOrderLRInvoiceSettings = (props) => {
  const { user } = useAuth();
  const { t } = useTranslation();

  const formik = useFormik({
    initialValues: {
      driverExpensesCategories: [
        {
          id: uuidv4(),
          driverExpenseName: "",
          showDefault: "",
        },
      ],
    },
    onSubmit: async (values, helpers) => {
      try {
        const newOrder = {
          deliveries: JSON.stringify(values.deliveryDetails),
        };

        await orderApi.createOrder(newOrder, dispatch);

        toast.success("Order created!");
        // router.push("/dashboard/orders");
      } catch (err) {
        console.error(err);
        toast.error("Something went wrong!");
        helpers.setStatus({ success: false });
        helpers.setErrors({ submit: err.message });
        helpers.setSubmitting(false);
      }
    },
  });

  return (
    <form onSubmit={formik.handleSubmit} {...props}>
      <FormikProvider value={formik}>
        <FieldArray name="deliveryDetails" error={formik.errors}>
          {({ remove, push }) => (
            <Box sx={{ mt: 4 }} {...props}>
              <Card>
                <CardContent>
                  <Grid container spacing={3}>
                    <Grid item md={4} xs={12}>
                      <Typography variant="h6">
                        {t("Truck Driver Expenses Categories")}
                      </Typography>
                      <Typography variant="body2" color="textSecondary">
                        {t(
                          "Expenses to store the purpose of giving money to driver of own vehicles "
                        )}
                      </Typography>
                    </Grid>
                    <Grid item md={8} xs={12}>
                      <Grid container spacing={3}>
                        <Grid item xs={8}>
                          <Typography variant="body2" color="textSecondary">
                            {t("Truck Driver Expenses Categories")}
                          </Typography>
                        </Grid>
                        <Grid item xs={4}>
                          <Typography variant="body2" color="textSecondary">
                            {t("Show Default")}
                          </Typography>
                        </Grid>

                        {formik.values.deliveryDetails.length > 0 &&
                          formik.values.deliveryDetails.map(
                            (delivery, index) => {
                              const loading = `deliveryDetails[${index}].loading`;
                              const touchedLoading = getIn(
                                formik.touched,
                                loading
                              );
                              const errorLoading = getIn(
                                formik.errors,
                                loading
                              );

                              const unloading = `deliveryDetails[${index}].unloading`;
                              const touchedUnloading = getIn(
                                formik.touched,
                                unloading
                              );
                              const errorUnloading = getIn(
                                formik.errors,
                                unloading
                              );

                              const billQuantity = `deliveryDetails[${index}].billQuantity`;
                              const touchedBillQuantity = getIn(
                                formik.touched,
                                billQuantity
                              );
                              const errorBillQuantity = getIn(
                                formik.errors,
                                billQuantity
                              );

                              const unloadingQuantity = `deliveryDetails[${index}].unloadingQuantity`;
                              const touchedUnloadingQuantity = getIn(
                                formik.touched,
                                unloadingQuantity
                              );
                              const errorUnloadingQuantity = getIn(
                                formik.errors,
                                unloadingQuantity
                              );
                              return (
                                <React.Fragment>
                                  {index > 0 && <Divider sx={{ mb: 2 }} />}

                                  <Grid
                                    container
                                    spacing={3}
                                    className="row"
                                    key={index}
                                    alignItems={"center"}
                                    sx={{ mb: 2 }}
                                  >
                                    <Grid
                                      item
                                      xs={8}
                                      className="col"
                                      key={index}
                                    >
                                      <TextField
                                        helperText={
                                          touchedBillQuantity &&
                                          errorBillQuantity
                                            ? errorBillQuantity
                                            : ""
                                        }
                                        error={Boolean(
                                          touchedBillQuantity &&
                                            errorBillQuantity
                                        )}
                                        variant="outlined"
                                        onChange={(event) => {
                                          formik.setFieldValue(
                                            `deliveryDetails[${index}].billQuantity`,
                                            event.target.value
                                          );
                                        }}
                                        InputProps={{
                                          endAdornment: (
                                            <InputAdornment position="end">
                                              {`${formik.values.saleType.unit}`}
                                            </InputAdornment>
                                          ),
                                        }}
                                        onBlur={formik.handleBlur}
                                        id="billQuantity"
                                        name="billQuantity"
                                        label="Bill Quantity"
                                        fullWidth
                                      />
                                    </Grid>
                                    <Grid item>
                                      <Checkbox defaultChecked />
                                    </Grid>
                                    <Grid item className="col">
                                      <Button
                                        disabled={index < 1}
                                        color="secondary"
                                        onClick={() => {
                                          setNoOfDeliveries(noOfDeliveries - 1);
                                          remove(index);
                                        }}
                                      >
                                        Remove
                                      </Button>
                                    </Grid>
                                  </Grid>
                                </React.Fragment>
                              );
                            }
                          )}

                        <Grid item xs={8}>
                          <Box
                            sx={{
                              display: "flex",
                              alignItems: "center",
                            }}
                          >
                            <TextField
                              fullWidth
                              name="name"
                              size="small"
                              required
                              sx={{
                                flexGrow: 1,
                                mr: 3,
                              }}
                            />
                          </Box>
                        </Grid>
                      </Grid>

                      <Box p={2} display="flex" justifyContent="flex-end">
                        <Button
                          variant="contained"
                          color="secondary"
                          startIcon={<PlusIcon fontSize="small" />}
                          onClick={() => {
                            push({
                              id: uuidv4(),
                              loading: "",
                              unloading: "",
                              billQuantity: "",
                              unloadingQuantity: "",
                            });
                          }}
                        >
                          Add Expense
                        </Button>
                        <Button
                          color="secondary"
                          type="submit"
                          variant="contained"
                        >
                          Save Changes
                        </Button>
                      </Box>
                    </Grid>
                  </Grid>
                </CardContent>
              </Card>
            </Box>
          )}
        </FieldArray>
      </FormikProvider>
    </form>
  );
};
