export const OrganisationDrawer = (props) => {
  const { containerRef, onClose, open, organisation, ...other } = props;
  const [isEditing, setIsEditing] = useState(false);
  const lgUp = useMediaQuery((theme) => theme.breakpoints.up("lg"));

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setIsEditing(false);
  };

  // The reason for doing this, is that the persistent drawer has to be rendered, but not it's
  // content if an organisation is not passed.
  const content = organisation ? (
    <>
      <Box
        sx={{
          alignItems: "center",
          backgroundColor: "primary.main",
          color: "primary.contrastText",
          display: "flex",
          justifyContent: "space-between",
          px: 3,
          py: 2,
        }}
      >
        <Typography color="inherit" variant="h6">
          {organisation.number}
        </Typography>
        <IconButton color="inherit" onClick={onClose}>
          <XIcon fontSize="small" />
        </IconButton>
      </Box>
      <Box
        sx={{
          px: 3,
          py: 4,
        }}
      >
        {!isEditing ? (
          <OrganisationPreview
            onApprove={onClose}
            onEdit={handleEdit}
            onReject={onClose}
            organisation={organisation}
            lgUp={lgUp}
          />
        ) : (
          <OrganisationForm
            onCancel={handleCancel}
            organisation={organisation}
          />
        )}
      </Box>
    </>
  ) : null;

  if (lgUp) {
    return (
      <OrganisationDrawerDesktop
        anchor="right"
        open={open}
        SlideProps={{ container: containerRef?.current }}
        variant="persistent"
        {...other}
      >
        {content}
      </OrganisationDrawerDesktop>
    );
  }

  return (
    <OrganisationDrawerMobile
      anchor="right"
      ModalProps={{ container: containerRef?.current }}
      onClose={onClose}
      open={open}
      SlideProps={{ container: containerRef?.current }}
      variant="temporary"
      {...other}
    >
      {content}
    </OrganisationDrawerMobile>
  );
};

OrganisationDrawer.propTypes = {
  containerRef: PropTypes.any,
  onClose: PropTypes.func,
  open: PropTypes.bool,
  organisation: PropTypes.object,
};
