import React, { useState } from "react";
import PropTypes from "prop-types";
import {
  Button,
  Card,
  CardActions,
  CardHeader,
  Divider,
  useMediaQuery,
} from "@mui/material";
import { PropertyList } from "../../property-list";
import { PropertyListItem } from "../../property-list-item";
import { useDispatch } from "../../../store";
import GoogleMaps from "./google-places-autocomplete";
import { PartyAddressForm } from "./party-address-form";

export const AddressCard = (props) => {
  const { address } = props;
  const mdUp = useMediaQuery((theme) => theme.breakpoints.up("md"));

  const align = mdUp ? "horizontal" : "vertical";

  const [status, setStatus] = useState(true);
  const toggleStatus = () => {
    setStatus(!status);
  };

  let addressCard = (
    <React.Fragment>
      <Card {...other}>
        <CardHeader
          title={address.name}
          action={
            <Button variant="outlined" color="primary" onClick={toggleStatus}>
              Edit
            </Button>
          }
        />
        <Divider />
        <PropertyList>
          <PropertyListItem
            align={align}
            divider
            label="GSTIN"
            value={address.gstin}
          />
          <PropertyListItem
            align={align}
            divider
            label="PAN"
            value={address.pan}
          />
          <PropertyListItem
            align={align}
            divider
            label="Address Line 1"
            value={address.billingAddressLine1}
          />
          <PropertyListItem
            align={align}
            divider
            label="Address Line 2"
            value={address.billingAddressLine2}
          />
          <PropertyListItem
            align={align}
            disableGutters
            label="City"
            value={JSON.parse(address.city).description}
          />
        </PropertyList>
      </Card>
    </React.Fragment>
  );

  return status ? (
    { addressCard }
  ) : (
    <PartyAddressForm
      address={address}
      toggleStatus={toggleStatus}
      type="edit"
    />
  );
};
