/* eslint-disable no-use-before-define */
import React, { useState, useEffect, useCallback } from "react";
import * as Yup from "yup";
import { useFormik } from "formik";

import {
  Box,
  Button,
  Card,
  CardContent,
  Dialog,
  DialogTitle,
  Divider,
  Grid,
  Switch,
  TextField,
  Typography,
} from "@mui/material";
import { useDispatch } from "../../../store";
import Autocomplete, { createFilterOptions } from "@mui/material/Autocomplete";
import GoogleMaps from "../welcome/google-places-autocomplete";
import { partyApi } from "../../../api/party-api";

const filter = createFilterOptions();

const FreeSoloCreateOptionDialog = ({
  className,
  touched,
  setFieldValue,
  errors,
  handleBlur,
  type,
  values,
  user,
  ...rest
}) => {
  const dispatch = useDispatch();
  const [value, setValue] = React.useState(values && values[type]);
  const [open, toggleOpen] = React.useState(false);
  const [parties, setParties] = useState([]);
  const [dialogValue, setDialogValue] = React.useState({
    name: "",
    transporter: type !== "customer" ? true : false,
  });

  const formik = useFormik({
    initialValues: {
      name: dialogValue.name || "",
      mobile: dialogValue.mobile || "",
      location: dialogValue.location || "",
      transporter: dialogValue.transporter || "",
      submit: null,
    },
    validationSchema: Yup.object().shape({
      name: Yup.string().max(255).required("Name is required"),
      mobile: Yup.string()
        .matches(/^[6-9]\d{9}$/, "Phone number is not valid")
        .required("Mobile is required"),
    }),
    onSubmit: async (
      values,
      { resetForm, setErrors, setStatus, setSubmitting }
    ) => {
      try {
        // NOTE: Make API request
        console.log(values);
        const response = await axios.post(`/api/parties/`, {
          name: values.name.replace(/\w\S*/g, function (txt) {
            return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
          }),
          ...values,
        });
        setFieldValue(type, response.data);
        // dispatch(updateParty(party, values));
        resetForm();
        setStatus({ success: true });
        setSubmitting(false);
        handleClose();
      } catch (err) {
        console.log(err);
        setStatus({ success: false });
        setErrors({ submit: err.message });
        setSubmitting(false);
      }
    },
  });

  useEffect(async () => {
    try {
      const partiesDB = await partyApi.getPartiesByUser(user, dispatch);
      setParties(partiesDB);
    } catch (error) {
      console.log(error);
    }
  }, []);

  const handleClose = () => {
    setDialogValue({
      name: "",
      transporter: type !== "customer" ? true : false,
    });

    toggleOpen(false);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setValue({
      name: dialogValue.name,
      transporter: dialogValue.transporter,
    });

    try {
      // NOTE: Make API request
      console.log(value);
      const response = await axios.post(`/api/parties/`, {
        name: dialogValue.name.replace(/\w\S*/g, function (txt) {
          return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
        }),
        transporter: dialogValue.transporter,
      });
      console.log(response.data);
      setFieldValue(type, response.data);
    } catch (err) {
      console.error(err);
    }

    handleClose();
  };
  return (
    <React.Fragment>
      <Autocomplete
        value={value}
        onChange={(event, newValue) => {
          if (typeof newValue === "string") {
            // timeout to avoid instant validation of the dialog's form.
            setTimeout(() => {
              toggleOpen(true);
              setDialogValue({
                name: newValue.replace(/\w\S*/g, function (txt) {
                  return (
                    txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase()
                  );
                }),
                transporter: type !== "customer" ? true : false,
              });
            });
          } else if (newValue && newValue.inputValue) {
            toggleOpen(true);
            setDialogValue({
              name: newValue.inputValue,
              transporter: type !== "customer" ? true : false,
            });
          } else {
            setFieldValue(type, newValue);
            setValue(newValue);
          }
        }}
        id={type}
        options={parties}
        getOptionLabel={(option) => {
          // e.g value selected with enter, right from the input
          if (typeof option === "string") {
            return option.replace(/\w\S*/g, function (txt) {
              return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
            });
          }
          if (option.inputValue) {
            return option.inputValue.replace(/\w\S*/g, function (txt) {
              return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
            });
          }
          return option.name.replace(/\w\S*/g, function (txt) {
            return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
          });
        }}
        clearOnBlur
        handleHomeEndKeys
        renderOption={(option) => {
          return (
            <React.Fragment>
              {option.name &&
                option.name.replace(/\w\S*/g, function (txt) {
                  return (
                    txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase()
                  );
                })}
              <br />
              {option.mobile && `(M) ${option.mobile.replace(/ /g, "")}`}
              <br />
              {option.location &&
                `Location - ${option.location.structured_formatting.main_text}`}
              <Divider />
            </React.Fragment>
          );
        }}
        fullWidth
        freeSolo
        renderInput={(params) => (
          <TextField
            onKeyPress={(e) => {
              e.which === 13 && e.preventDefault();
            }}
            {...params}
            label={type.charAt(0).toUpperCase() + type.slice(1)}
            error={Boolean(
              type === "customer"
                ? touched.customer && errors.customer
                : touched.transporter && errors.transporter
            )}
            onBlur={handleBlur}
            helperText={
              type === "customer"
                ? touched.customer && errors.customer
                : touched.transporter && errors.transporter
            }
            variant="outlined"
          />
        )}
      />
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="form-dialog-name"
      >
        <form>
          <DialogTitle id="form-dialog-name">Add a new party</DialogTitle>
          <form onSubmit={handleSubmit} {...rest}>
            <Card>
              <CardContent>
                <Grid container spacing={3}>
                  <Grid item xs={12}>
                    <TextField
                      error={Boolean(touched.name && errors.name)}
                      fullWidth
                      helperText={touched.name && errors.name}
                      label="Main Contact Name"
                      name="name"
                      onBlur={handleBlur}
                      onChange={(event) => {
                        setFieldValue(
                          "name",
                          event.target.value.replace(/\w\S*/g, function (txt) {
                            return (
                              txt.charAt(0).toUpperCase() +
                              txt.substr(1).toLowerCase()
                            );
                          })
                        );
                      }}
                      required
                      value={formik.values.name}
                      variant="outlined"
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      error={Boolean(touched.mobile && errors.mobile)}
                      fullWidth
                      helperText={touched.mobile && errors.mobile}
                      label="Mobile"
                      name="mobile"
                      onBlur={handleBlur}
                      onChange={(event) => {
                        setFieldValue(
                          "mobile",
                          event.target.value.replace(/ /g, "")
                        );
                      }}
                      value={formik.values.mobile}
                      variant="outlined"
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <GoogleMaps
                      name={"location"}
                      field={"location"}
                      formik={formik}
                      error={Boolean(formik.touched.city && formik.errors.city)}
                      label={"Your city in India"}
                      setFieldValue={formik.setFieldValue}
                      handleBlur={formik.handleBlur}
                      values={formik.values}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <Typography variant="h5" color="textPrimary">
                      Party Type
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                      Is this party a transporter?
                    </Typography>
                    <Switch
                      checked={formik.values.transporter}
                      color="secondary"
                      edge="start"
                      name="transporter"
                      onChange={formik.handleChange}
                      value={formik.values.transporter}
                    />
                  </Grid>
                </Grid>
                <Box mt={2}>
                  <Grid container spacing={3}>
                    <Grid item>
                      <Button
                        variant="contained"
                        color="secondary"
                        type="submit"
                        disabled={formik.isSubmitting}
                      >
                        Create Party
                      </Button>
                    </Grid>
                    <Grid item>
                      <Button
                        variant="contained"
                        color="secondary"
                        onClick={handleClose}
                      >
                        Cancel
                      </Button>
                    </Grid>
                  </Grid>
                </Box>
              </CardContent>
            </Card>
          </form>
        </form>
      </Dialog>
    </React.Fragment>
  );
};
export default FreeSoloCreateOptionDialog;
