import React, { useState, useRef, useMemo, useCallback } from "react";
import {
  GoogleMap,
  Marker,
  DirectionsService,
  DirectionsRenderer,
} from "@react-google-maps/api";

const GoogleMaps = ({ formik }) => {
  console.log("child component");

  const [response, setResponse] = useState(null);
  const [origin, setOrigin] = useState("");
  const [destination, setDestination] = useState("");
  const originRef = useRef(null);
  const destinationRef = useRef(null);

  const directionsServiceOptions = useMemo(() => {
    return {
      destination: destination,
      origin: origin,
      travelMode: "DRIVING",
    };
  }, []);

  const directionsRendererOptions = useMemo(() => {
    return {
      directions: response,
    };
  }, []);

  let directionsCallback = (response) => {
    if (response !== null) {
      if (response.status === "OK") {
        console.log(response);
        setResponse(response);
      } else {
        console.log("response: ");
      }
    }
  };

  return (
    <GoogleMap
      options={{ mapTypeId: "hybrid", disableDefaultUI: true }}
      mapContainerStyle={{
        width: "90%",
        height: "100%",
        minHeight: "12rem",
      }}
      center={{
        lat: 22.309425,
        lng: 72.13623,
      }}
      zoom={5}
    >
      {
        <DirectionsService
          // required
          options={{
            // origin: formik.values.deliveryDetails[0].loading.description,
            origin: {
              lat: 22.309425,
              lng: 72.13623,
            },
            destination: {
              lat: 22.309425,
              lng: 72.13623,
            },
            travelMode: "DRIVING",
          }}
          // required
          callback={directionsCallback}
          // optional
          onLoad={(directionsService) => {
            console.log(
              "DirectionsService onLoad directionsService: ",
              directionsService
            );
          }}
          // optional
          onUnmount={(directionsService) => {
            console.log(
              "DirectionsService onUnmount directionsService: ",
              directionsService
            );
          }}
        />
      }

      {
        <DirectionsRenderer
          // required
          options={{
            // eslint-disable-line react-perf/jsx-no-new-object-as-prop
            directions: response,
          }}
          // optional
          onLoad={(directionsRenderer) => {
            console.log(
              "DirectionsRenderer onLoad directionsRenderer: ",
              directionsRenderer
            );
          }}
          // optional
          onUnmount={(directionsRenderer) => {
            console.log(
              "DirectionsRenderer onUnmount directionsRenderer: ",
              directionsRenderer
            );
          }}
        />
      }
      {/* {formik.values.deliveryDetails.map((deliveries) => {
        return (
          <React.Fragment>
            <Marker
              key={deliveries.loading.place_id}
              position={{
                lat: deliveries.loading.latitude,
                lng: deliveries.loading.longitude,
              }}
            />
            <Marker
              key={deliveries.unloading.place_id}
              position={{
                lat: deliveries.unloading.latitude,
                lng: deliveries.unloading.longitude,
              }}
            />
          </React.Fragment>
        );
      })} */}
    </GoogleMap>
  );
};

export default React.memo(GoogleMaps);
