import { useState } from "react";
import PropTypes from "prop-types";
import { format } from "date-fns";
import numeral from "numeral";
import {
  Box,
  Button,
  Divider,
  Drawer,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  TextField,
  Typography,
  useMediaQuery,
} from "@mui/material";
import { styled } from "@mui/material/styles";
import EditIcon from "@mui/icons-material/Edit";
import { X as XIcon } from "../../../icons/x";
import { PropertyList } from "../../property-list";
import { PropertyListItem } from "../../property-list-item";
import { Scrollbar } from "../../scrollbar";

const OrganisationPreview = (props) => {
  const { lgUp, onApprove, onEdit, onReject, organisation } = props;
  const align = lgUp ? "horizontal" : "vertical";

  return (
    <>
      <Box
        sx={{
          alignItems: "center",
          borderRadius: 1,
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
        }}
      >
        <Typography sx={{ mt: 3 }} variant="h6">
          Details
        </Typography>
        <Box
          sx={{
            alignItems: "center",
            display: "flex",
            flexWrap: "wrap",
            mt: 3,
            m: -1,
            "& > button": {
              m: 1,
            },
          }}
        >
          <Button
            onClick={onEdit}
            size="small"
            startIcon={<EditIcon fontSize="small" />}
            sx={{ pt: 3 }}
          >
            Edit
          </Button>
        </Box>
      </Box>
      <Divider sx={{ my: 3 }} />

      <PropertyList>
        <PropertyListItem
          align={align}
          disableGutters
          label="ID"
          value={organisation.id}
        />
        <PropertyListItem align={align} disableGutters label="Business Name">
          <Typography color="primary" variant="body2">
            {organisation.name}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.addressLine1}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.addressLine2}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.city}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.pincode}
          </Typography>
        </PropertyListItem>
        {/* </PropertyList>
      <Typography sx={{ my: 3 }} variant="h6">
        Bank Details
      </Typography>
      <Divider sx={{ my: 3 }} />
      <PropertyList> */}
        <PropertyListItem align={align} disableGutters label="Bank Details">
          <Typography color="primary" variant="body2">
            {organisation.bankName}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.bankAccountNumber &&
              `A/c: ${organisation.bankAccountNumber}`}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.bankIFSC && `IFSC: ${organisation.bankIFSC}`}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.bankBranch}
          </Typography>
        </PropertyListItem>
      </PropertyList>
    </>
  );
};

const OrganisationForm = (props) => {
  const { onCancel, onSave, organisation } = props;

  return (
    <>
      <Box
        sx={{
          alignItems: "center",
          backgroundColor: (theme) =>
            theme.palette.mode === "dark" ? "neutral.800" : "neutral.100",
          borganisationRadius: 1,
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
          px: 3,
          py: 2.5,
        }}
      >
        <Typography variant="overline" sx={{ mr: 2 }} color="textSecondary">
          Organisation
        </Typography>
        <Box
          sx={{
            alignItems: "center",
            display: "flex",
            m: -1,
            "& > button": {
              m: 1,
            },
          }}
        >
          <Button
            color="primary"
            onClick={onSave}
            size="small"
            variant="contained"
          >
            Save changes
          </Button>
          <Button onClick={onCancel} size="small" variant="outlined">
            Cancel
          </Button>
        </Box>
      </Box>
      <Typography sx={{ my: 3 }} variant="h6">
        Details
      </Typography>
      <TextField
        disabled
        fullWidth
        label="ID"
        margin="normal"
        name="id"
        value={organisation.id}
      />
      <TextField
        disabled
        fullWidth
        label="Number"
        margin="normal"
        name="number"
        value={organisation.number}
      />
      <TextField
        disabled
        fullWidth
        label="Customer name"
        margin="normal"
        name="customer_name"
        value={organisation.customer.name}
      />
      <TextField
        disabled
        fullWidth
        label="Date"
        margin="normal"
        name="date"
        value={format(organisation.createdAt, "dd/MM/yyyy HH:mm")}
      />
      <TextField
        fullWidth
        label="Address"
        margin="normal"
        name="address"
        value={organisation.customer.address1}
      />
      <TextField
        fullWidth
        label="Country"
        margin="normal"
        name="country"
        value={organisation.customer.country}
      />
      <TextField
        fullWidth
        label="State/Region"
        margin="normal"
        name="state_region"
        value={organisation.customer.city}
      />
      <TextField
        fullWidth
        label="Total Amount"
        margin="normal"
        name="amount"
        value={organisation.totalAmount}
      />
      <TextField
        fullWidth
        label="Status"
        margin="normal"
        name="status"
        select
        SelectProps={{ native: true }}
        value={organisation.status}
      >
        {statusOptions.map((statusOption) => (
          <option key={statusOption.value} value={statusOption.value}>
            {statusOption.label}
          </option>
        ))}
      </TextField>
      <Button color="error" sx={{ mt: 3 }}>
        Delete organisation
      </Button>
    </>
  );
};

const OrganisationDrawerDesktop = styled(Drawer)({
  width: 500,
  flexShrink: 0,
  "& .MuiDrawer-paper": {
    position: "relative",
    width: 500,
  },
});

const OrganisationDrawerMobile = styled(Drawer)({
  flexShrink: 0,
  maxWidth: "100%",
  height: "calc(100% - 64px)",
  width: 500,
  "& .MuiDrawer-paper": {
    height: "calc(100% - 64px)",
    maxWidth: "100%",
    top: 64,
    width: 500,
  },
});

export const OrganisationDrawer = (props) => {
  const { containerRef, onClose, open, organisation, ...other } = props;
  const [isEditing, setIsEditing] = useState(false);
  const lgUp = useMediaQuery((theme) => theme.breakpoints.up("lg"));

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setIsEditing(false);
  };

  // The reason for doing this, is that the persistent drawer has to be rendered, but not it's
  // content if an organisation is not passed.
  const content = organisation ? (
    <>
      <Box
        sx={{
          alignItems: "center",
          backgroundColor: "primary.main",
          color: "primary.contrastText",
          display: "flex",
          justifyContent: "space-between",
          px: 3,
          py: 2,
        }}
      >
        <Typography color="inherit" variant="h6">
          {organisation.number}
        </Typography>
        <IconButton color="inherit" onClick={onClose}>
          <XIcon fontSize="small" />
        </IconButton>
      </Box>
      <Box
        sx={{
          px: 3,
          py: 4,
        }}
      >
        {!isEditing ? (
          <OrganisationPreview
            onApprove={onClose}
            onEdit={handleEdit}
            onReject={onClose}
            organisation={organisation}
            lgUp={lgUp}
          />
        ) : (
          <OrganisationForm
            onCancel={handleCancel}
            onSave={handleCancel}
            organisation={organisation}
          />
        )}
      </Box>
    </>
  ) : null;

  if (lgUp) {
    return (
      <OrganisationDrawerDesktop
        anchor="right"
        open={open}
        SlideProps={{ container: containerRef?.current }}
        variant="persistent"
        {...other}
      >
        {content}
      </OrganisationDrawerDesktop>
    );
  }

  return (
    <OrganisationDrawerMobile
      anchor="right"
      ModalProps={{ container: containerRef?.current }}
      onClose={onClose}
      open={open}
      SlideProps={{ container: containerRef?.current }}
      variant="temporary"
      {...other}
    >
      {content}
    </OrganisationDrawerMobile>
  );
};

OrganisationDrawer.propTypes = {
  containerRef: PropTypes.any,
  onClose: PropTypes.func,
  open: PropTypes.bool,
  organisation: PropTypes.object,
};
