import { useState } from "react";
import { useRouter } from "next/router";
import toast from "react-hot-toast";
import * as Yup from "yup";
import { useFormik } from "formik";
import {
  Box,
  Button,
  Card,
  CardContent,
  FormControlLabel,
  FormHelperText,
  Grid,
  MenuItem,
  Switch,
  TextField,
  Typography,
} from "@mui/material";
import { VehicleLogoDropzone } from "./vehicle-logo-dropzone";
import { QuillEditor } from "../../quill-editor";
import { useAuth } from "../../../hooks/use-auth";
import { getInitials } from "../../../utils/get-initials";

import { API, Storage } from "aws-amplify";
import { createVehicle } from "../../../graphql/mutations";
import { DataStore, Predicates } from "@aws-amplify/datastore";
import { Vehicle } from "../../../models";
import { v4 as uuid } from "uuid";
import { vehicleApi } from "../../../api/vehicle-api";
import { useDispatch } from "../../../store";

export const VehicleCreateForm = (props) => {
  const router = useRouter();
  const dispatch = useDispatch();
  const [croppedImage, setCroppedImage] = useState();
  const [file, setFile] = useState();
  const { user } = useAuth();
  const formik = useFormik({
    initialValues: {
      vehicleNumber: "",
      make: "",
      model: "",
      yearOfPurchase: "",
      condition: "",
    },
    // validationSchema: Yup.object({
    //   name: Yup.string().max(255).required("Name is required"),
    // }),
    onSubmit: async (values, helpers) => {
      try {
        // NOTE: Make API request
        const id = uuid();
        values.id = id;
        values.user = user.id;

        await vehicleApi.createVehicle(values, dispatch);

        toast.success("Vehicle created!");
        // router.push("/dashboard/vehicles");
      } catch (err) {
        console.error(err);
        toast.error("Something went wrong!");
        helpers.setStatus({ success: false });
        helpers.setErrors({ submit: err.message });
        helpers.setSubmitting(false);
      }
    },
  });

  return (
    <form onSubmit={formik.handleSubmit} {...props}>
      <Card>
        <CardContent>
          <Grid container spacing={3}>
            <Grid item md={4} xs={12}>
              <Typography variant="h6">Business details</Typography>
            </Grid>
            <Grid item md={8} xs={12}>
              <Grid container spacing={3}>
                <Grid item md={10} xs={12}>
                  <TextField
                    error={Boolean(formik.touched.name && formik.errors.name)}
                    fullWidth
                    helperText={formik.touched.name && formik.errors.name}
                    label="Business Name"
                    name="name"
                    onBlur={formik.handleBlur}
                    onChange={(event) => {
                      formik.setFieldValue(
                        `initials`,
                        `${getInitials(event.target.value)}`
                      );
                      formik.setFieldValue(`name`, event.target.value);
                    }}
                    value={formik.values.name}
                    variant="outlined"
                  />
                </Grid>
                <Grid item md={2} xs={12}>
                  <TextField
                    error={Boolean(
                      formik.touched.initials && formik.errors.initials
                    )}
                    fullWidth
                    helperText={
                      formik.touched.initials && formik.errors.initials
                    }
                    label="Initials"
                    name="initials"
                    onBlur={formik.handleBlur}
                    onChange={formik.handleChange}
                    value={formik.values.initials}
                    variant="outlined"
                  />
                </Grid>
                <Grid item md={6} xs={12}>
                  <TextField
                    error={Boolean(
                      formik.touched.addressLine1 && formik.errors.addressLine1
                    )}
                    fullWidth
                    helperText={
                      formik.touched.addressLine1 && formik.errors.addressLine1
                    }
                    label="Address Line 1"
                    name="addressLine1"
                    onBlur={formik.handleBlur}
                    onChange={formik.handleChange}
                    value={formik.values.addressLine1}
                    variant="outlined"
                  />
                </Grid>
                <Grid item md={6} xs={12}>
                  <TextField
                    error={Boolean(
                      formik.touched.addressLine2 && formik.errors.addressLine2
                    )}
                    fullWidth
                    helperText={
                      formik.touched.addressLine2 && formik.errors.addressLine2
                    }
                    label="Address Line 2"
                    name="addressLine2"
                    onBlur={formik.handleBlur}
                    onChange={formik.handleChange}
                    value={formik.values.addressLine2}
                    variant="outlined"
                  />
                </Grid>
                <Grid item md={6} xs={12}>
                  <TextField
                    error={Boolean(formik.touched.city && formik.errors.city)}
                    fullWidth
                    helperText={formik.touched.city && formik.errors.city}
                    label="City (Jurisdication)"
                    name="city"
                    onBlur={formik.handleBlur}
                    onChange={formik.handleChange}
                    value={formik.values.city}
                    variant="outlined"
                  />
                </Grid>
                <Grid item md={6} xs={12}>
                  <TextField
                    error={Boolean(
                      formik.touched.pincode && formik.errors.pincode
                    )}
                    fullWidth
                    helperText={formik.touched.pincode && formik.errors.pincode}
                    label="Pincode"
                    name="pincode"
                    onBlur={formik.handleBlur}
                    onChange={formik.handleChange}
                    value={formik.values.pincode}
                    variant="outlined"
                  />
                </Grid>
              </Grid>
            </Grid>
          </Grid>
        </CardContent>
      </Card>

      <Box
        sx={{
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
          mx: -1,
          mb: -1,
          mt: 3,
        }}
      >
        <Button
          color="error"
          sx={{
            m: 1,
            mr: "auto",
          }}
        >
          Delete
        </Button>
        <Button sx={{ m: 1 }} variant="outlined">
          Cancel
        </Button>
        <Button sx={{ m: 1 }} type="submit" variant="contained">
          Create
        </Button>
      </Box>
    </form>
  );
};
