import { useEffect, useRef, useState } from "react";
import NextLink from "next/link";
import Head from "next/head";
import { useTranslation } from "react-i18next";
import moment from "moment";
import {
  Box,
  Button,
  Divider,
  Grid,
  InputAdornment,
  Tab,
  Tabs,
  TextField,
  Typography,
} from "@mui/material";
import { styled } from "@mui/material/styles";
import { organisationApi } from "../../../api/organisation-api";
import { AuthGuard } from "../../../components/authentication/auth-guard";
import { OnBoardingGuard } from "../../../components/authentication/onboarding-guard";
import { DashboardLayout } from "../../../components/dashboard/dashboard-layout";
import { OrganisationDrawer } from "../../../components/dashboard/organisation/organisation-drawer";
import OrganisationGrid from "../../../components/dashboard/organisation/organisation-grid";
import { useMounted } from "../../../hooks/use-mounted";
import { useAuth } from "../../../hooks/use-auth";
import { Plus as PlusIcon } from "../../../icons/plus";
import { Search as SearchIcon } from "../../../icons/search";
import { gtm } from "../../../lib/gtm";

// DataStore Imports
// import { DataStore, Predicates } from "@aws-amplify/datastore";
// import { Organisation } from "../../../models";

const tabs = [
  {
    label: "All",
    value: "all",
  },
  {
    label: "Canceled",
    value: "canceled",
  },
  {
    label: "Completed",
    value: "complete",
  },
  {
    label: "Pending",
    value: "pending",
  },
  {
    label: "Rejected",
    value: "rejected",
  },
];

const sortOptions = [
  {
    label: "Newest",
    value: "desc",
  },
  {
    label: "Oldest",
    value: "asc",
  },
];

const applyFilters = (organisations, filters) =>
  organisations.filter((organisation) => {
    if (filters.query) {
      // Checks only the organisation number, but can be extended to support other fields, such as customer
      // name, email, etc.
      const containsQuery = organisation.number
        .toLowerCase()
        .includes(filters.query.toLowerCase());

      if (!containsQuery) {
        return false;
      }
    }

    if (typeof filters.status !== "undefined") {
      const statusMatched = organisation.status === filters.status;

      if (!statusMatched) {
        return false;
      }
    }

    return true;
  });

const applySort = (organisations, organisation) =>
  organisations.sort((a, b) => {
    const comparator = a.createdAt > b.createdAt ? -1 : 1;

    return organisation === "desc" ? comparator : -comparator;
  });

const OrganisationListInner = styled("div", {
  shouldForwardProp: (prop) => prop !== "open",
})(({ theme, open }) => ({
  flexGrow: 1,
  overflow: "hidden",
  paddingBottom: theme.spacing(8),
  paddingTop: theme.spacing(8),
  zIndex: 1,
  [theme.breakpoints.up("lg")]: {
    marginRight: -500,
  },
  transition: theme.transitions.create("margin", {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  ...(open && {
    [theme.breakpoints.up("lg")]: {
      marginRight: 0,
    },
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  }),
}));

const OrganisationList = () => {
  const isMounted = useMounted();
  const { t } = useTranslation();
  const { user } = useAuth();
  const rootRef = useRef(null);
  const queryRef = useRef(null);
  const [currentTab, setCurrentTab] = useState("all");
  const [sort, setSort] = useState("desc");
  const [organisations, setOrganisations] = useState([]);

  const [filters, setFilters] = useState({
    query: "",
    status: undefined,
  });
  const [drawer, setDrawer] = useState({
    isOpen: true,
    organisationId: null,
  });

  useEffect(async () => {
    try {
      const organisationsDB = await organisationApi.getOrganisationsByUser(
        user
      );
      setOrganisations(organisationsDB.data.organisationsByUser.items);
    } catch (error) {
      console.log(error);
    }
  }, []);

  const handleCloseDrawer = () => {
    setDrawer({
      isOpen: false,
      organisationId: null,
    });
  };

  return (
    <>
      <Head>
        <title>Dashboard: Organisation List | Truckar</title>
      </Head>
      <Box
        component="main"
        ref={rootRef}
        sx={{
          backgroundColor: "background.paper",
          display: "flex",
          flexGrow: 1,
          overflow: "hidden",
        }}
      >
        <OrganisationListInner open={drawer.isOpen}>
          <Box sx={{ px: 3 }}>
            <Grid container justifyContent="space-between" spacing={3}>
              <Grid item>
                <Typography variant="h4">{t("Organisations")}</Typography>
              </Grid>
              <Grid item>
                <NextLink href="/dashboard/organisations/new" passHref>
                  <Button
                    component="a"
                    startIcon={<PlusIcon fontSize="small" />}
                    variant="contained"
                  >
                    Add
                  </Button>
                </NextLink>
              </Grid>
            </Grid>
          </Box>

          <Box sx={{ px: 3 }}>
            <Grid container justifyContent="space-between" spacing={3}>
              <Grid item>
                <Typography variant="h4">{t("Organisations")}</Typography>
              </Grid>
            </Grid>
          </Box>
          <OrganisationGrid organisations={organisations} />
        </OrganisationListInner>
        <OrganisationDrawer
          containerRef={rootRef}
          onClose={handleCloseDrawer}
          open={drawer.isOpen}
          organisation={organisations.find(
            (organisation) => organisation.id === drawer.organisationId
          )}
        />
      </Box>
    </>
  );
};

OrganisationList.getLayout = (page) => (
  <AuthGuard>
    <OnBoardingGuard>
      <DashboardLayout>{page}</DashboardLayout>
    </OnBoardingGuard>
  </AuthGuard>
);

export default OrganisationList;
