import { forwardRef, useImperativeHandle } from "react";
import toast from "react-hot-toast";
import * as Yup from "yup";
import { useFormik } from "formik";
import { Divider, Grid, TextField, Typography } from "@mui/material";
import GoogleMaps from "./google-places-autocomplete";
import { useAuth } from "../../../hooks/use-auth";
import { organisationApi } from "../../../api/organisation-api";
import { userApi } from "../../../api/user-api";

export const OrganisationCreateForm = forwardRef(
  ({ handleNext, setActiveStep, ...props }, ref) => {
    const { user, initialize } = useAuth();
    // console.log(user);

    useImperativeHandle(ref, () => ({
      handleFormSubmit() {
        formik.handleSubmit();
      },
    }));

    const formik = useFormik({
      initialValues: {
        organisationName: "",
        gstin: "",
        jurisdiction: "",
        pan: "",
        initials: "",
        addressLine1: "",
        addressLine2: "",
        bankAccountNumber: "",
        bankName: "",
        bankBranchName: "",
        bankIFSC: "",
        invoiceTermsAndConditions: "",
        contact: "",
        email: "",
        city: "",
        submit: null,
      },
      validationSchema: Yup.object({
        organisationName: Yup.string()
          .max(255)
          .required("Please enter a organisation name."),
        city: Yup.object().required("Please select a city."),
      }),
      onSubmit: async (values, helpers) => {
        try {
          const newOrganisation = {
            organisationName: values.organisationName,
            city: JSON.stringify(values.city),
            organisationType: values.organisationType,
            user: user.id,
          };

          await organisationApi.createOrganisation(newOrganisation);
          await userApi.updateUser({
            onBoardingRequired: false,
            id: user.id,
          });
          await initialize();
          toast.success("Organisation created!");
          setActiveStep(1);
          // router.push("/dashboard/organisationes");
        } catch (err) {
          console.error(err);
          toast.error("Something went wrong!");
          helpers.setStatus({ success: false });
          helpers.setErrors({ submit: err.message });
          helpers.setSubmitting(false);
        }
      },
    });

    return (
      <form onSubmit={formik.handleSubmit} {...props}>
        <Grid container spacing={5} alignItems="center" sx={{ mb: 4, mt: 2 }}>
          <Grid item md={4} xs={12}>
            <Typography variant="h6">Tell us your city</Typography>
          </Grid>
          <Grid item md={8} xs={12}>
            <GoogleMaps
              formik={formik}
              error={Boolean(formik.touched.city && formik.errors.city)}
              label={"Your city in India"}
              field={"city"}
              setFieldValue={formik.setFieldValue}
              handleBlur={formik.handleBlur}
              values={formik.values}
            />
          </Grid>
          <Grid item xs={12}>
            <Divider />
          </Grid>
          <Grid item md={4} xs={12}>
            <Typography variant="h6">Please pick a name</Typography>
          </Grid>
          <Grid item md={8} xs={12}>
            <TextField
              error={Boolean(
                formik.touched.organisationName &&
                  formik.errors.organisationName
              )}
              fullWidth
              helperText={
                formik.touched.organisationName &&
                formik.errors.organisationName
              }
              label="Your organisation name"
              name="organisationName"
              onBlur={formik.handleBlur}
              onChange={formik.handleChange}
              value={formik.values.organisationName}
            />
          </Grid>
        </Grid>
      </form>
    );
  }
);
