import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { Storage } from "aws-amplify";
import { useFormik } from "formik";
import toast from "react-hot-toast";
import {
  Box,
  Button,
  Divider,
  Drawer,
  IconButton,
  TextField,
  Typography,
  useMediaQuery,
} from "@mui/material";
import { styled } from "@mui/material/styles";
import EditIcon from "@mui/icons-material/Edit";
import { useDispatch } from "../../../store";
import { X as XIcon } from "../../../icons/x";
import { PropertyList } from "../../property-list";
import { PropertyListItem } from "../../property-list-item";
import { partyApi } from "../../../api/party-api";
import { AddressCard } from "./party-address-card";
import { Plus } from "../../../icons/plus";
import { PartyAddressForm } from "./party-address-form";
import { addressApi } from "../../../api/address-api";
import { useAuth } from "../../../hooks/use-auth";
import { useSelector } from "react-redux";

const PartyPreview = (props) => {
  const dispatch = useDispatch();
  const { user } = useAuth();
  const { lgUp, onEdit, party } = props;

  const align = lgUp ? "horizontal" : "vertical";
  const { addresses } = useSelector((state) => state.addresses);
  const [status, setStatus] = useState(false);
  const toggleStatus = () => {
    setStatus(!status);
  };
  useEffect(async () => {
    try {
      await addressApi.getAddressesByUser(user, dispatch);
    } catch (error) {
      console.log(error);
    }
  }, [party]);

  return (
    <>
      <Box
        sx={{
          alignItems: "center",
          borderRadius: 1,
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
        }}
      >
        <Typography sx={{ mt: 3 }} variant="h6">
          Party Details
        </Typography>
        <Box
          sx={{
            alignItems: "center",
            display: "flex",
            flexWrap: "wrap",
            mt: 3,
            m: -1,
            "& > button": {
              m: 1,
            },
          }}
        >
          <Button
            onClick={onEdit}
            size="small"
            startIcon={<EditIcon fontSize="small" />}
            sx={{ pt: 3 }}
          >
            Edit
          </Button>
        </Box>
      </Box>
      <PropertyList>
        <PropertyListItem
          align={align}
