import { useRef, useState } from "react";
import { useTranslation } from "react-i18next";
import { toast } from "react-hot-toast";
import { Formik } from "formik";
import * as Yup from "yup";
import axios from "../../../utils/axios";
import { useAuth } from "../../../hooks/use-auth";
import { LanguagePopover } from "../language-popover";

import {
  Box,
  Button,
  Card,
  CardContent,
  Checkbox,
  FormGroup,
  FormControlLabel,
  Grid,
  IconButton,
  TextField,
  Typography,
} from "@mui/material";

export const AccountOrderLRInvoiceSettings = (props) => {
  const { user } = useAuth();
  console.log(user);
  const { t } = useTranslation();
  console.log(t("Basic Details"));

  const formik = useFormik({
    initialValues: {
      driverExpensesCategories: [
        {
          id: uuidv4(),
          loading: "",
          unloading: "",
          billQuantity: "",
          unloadingQuantity: "",
        },
      ],
    },
    onSubmit: async (values, helpers) => {
      try {
        const newOrder = {
          deliveries: JSON.stringify(values.deliveryDetails),
        };

        await orderApi.createOrder(newOrder, dispatch);

        toast.success("Order created!");
        // router.push("/dashboard/orders");
      } catch (err) {
        console.error(err);
        toast.error("Something went wrong!");
        helpers.setStatus({ success: false });
        helpers.setErrors({ submit: err.message });
        helpers.setSubmitting(false);
      }
    },
  });

  return (
    <Formik
      enableReinitialize
      initialValues={{
        name: user.name || "",
        marketName: user.marketName || "",
        email: user.email || "",
        mobile: user.mobile || "",
        accountType: user.accountType || {
          vehicleOwner: false,
          trader: true,
          commissionAgent: false,
        },
        submit: null,
      }}
      validationSchema={Yup.object().shape({
        name: Yup.string().max(255).required("Name is required"),
        marketName: Yup.string().max(255).required("Market Name is required"),
        mobile: Yup.string().max(10).required("Phone Number is required"),
        email: Yup.string()
          .email("Must be a valid email")
          .max(255)
          .required("Email is required"),
      })}
      onSubmit={async (
        values,
        { resetForm, setErrors, setStatus, setSubmitting }
      ) => {
        try {
          // NOTE: Make API request
          console.log(values);
          await axios.patch("/api/auth/", values);
          await initialise();
          resetForm();
          setStatus({ success: true });
          setSubmitting(false);
          toast.success(t("User updated"));
        } catch (err) {
          console.error(err);
          setStatus({ success: false });
          setErrors({ submit: err.message });
          setSubmitting(false);
        }
      }}
    >
      {({
        errors,
        setFieldValue,
        handleBlur,
        handleChange,
        handleSubmit,
        isSubmitting,
        touched,
        values,
      }) => (
        <form onSubmit={handleSubmit}>
          <Box sx={{ mt: 4 }} {...props}>
            <Card>
              <CardContent>
                <Grid container spacing={3}>
                  <Grid item md={4} xs={12}>
                    <Typography variant="h6">
                      {t("Truck Driver Expenses Categories")}
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                      {t(
                        "Expenses to store the purpose of giving money to driver of own vehicles "
                      )}
                    </Typography>
                  </Grid>
                  <Grid item md={8} xs={12}>
                    <Grid container spacing={3}>
                      <Grid item xs={8}>
                        <Typography variant="body2" color="textSecondary">
                          {t("Truck Driver Expenses Categories")}
                        </Typography>
                      </Grid>
                      <Grid item xs={4}>
                        <Typography variant="body2" color="textSecondary">
                          {t("Show Default")}
                        </Typography>
                      </Grid>
                      <Grid item xs={8}>
                        <Box
                          sx={{
                            display: "flex",
                            alignItems: "center",
                          }}
                        >
                          <TextField
                            error={Boolean(touched.name && errors.name)}
                            fullWidth
                            helperText={touched.name && errors.name}
                            name="name"
                            size="small"
                            onBlur={handleBlur}
                            onChange={handleChange}
                            required
                            value={values.name}
                            sx={{
                              flexGrow: 1,
                              mr: 3,
                            }}
                          />
                        </Box>
                      </Grid>
                      <Grid item xs={4}>
                        <Checkbox defaultChecked />
                      </Grid>
                    </Grid>

                    <Box p={2} display="flex" justifyContent="flex-end">
                      <Button
                        color="secondary"
                        disabled={isSubmitting}
                        type="submit"
                        variant="contained"
                      >
                        Save Changes
                      </Button>
                    </Box>
                  </Grid>
                </Grid>
              </CardContent>
            </Card>
          </Box>
        </form>
      )}
    </Formik>
  );
};
