/* eslint-disable no-use-before-define */
import React, { useState, useEffect } from "react";
import { Divider, TextField } from "@mui/material";
import { useDispatch } from "../../../store";
import Autocomplete, { createFilterOptions } from "@mui/material/Autocomplete";
import { partyApi } from "../../../api/party-api";
import AddNewPartyFromAutocomplete from "../party-autocomplete/party-addnew-autocomplete";

const PartyAutocomplete = ({
  touched,
  errors,
  formik,
  handleBlur,
  setFieldValue,
  type,
  values,
  user,
}) => {
  const dispatch = useDispatch();
  const filter = createFilterOptions();
  const [value, setValue] = React.useState(values && values[type]);
  const [open, toggleOpen] = React.useState(false);
  const [parties, setParties] = useState([]);
  const [dialogValue, setDialogValue] = React.useState({
    name: "",
    transporter: type !== "customer" ? true : false,
  });

  useEffect(async () => {
    try {
      const partiesDB = await partyApi.getPartiesByUser(user, dispatch);
      setParties(partiesDB);
    } catch (error) {
      console.log(error);
    }
  }, []);

  return (
    <React.Fragment>
      <Autocomplete
        value={value}
        filterOptions={(options, params) => {
          const filtered = filter(options, params);

          if (params.inputValue !== "") {
            filtered.push({
              inputValue: params.inputValue,
              name: `Add "${params.inputValue}"`,
            });
          }
          return filtered;
        }}
        onChange={(event, newValue) => {
          if (typeof newValue === "string") {
            toggleOpen(true);
            setDialogValue({
              name: newValue.replace(/\w\S*/g, function (txt) {
                return (
                  txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase()
                );
              }),
              transporter: type !== "customer" ? true : false,
            });
          } else if (newValue && newValue.inputValue) {
            toggleOpen(true);
            setDialogValue({
              name: newValue.inputValue,
              transporter: type !== "customer" ? true : false,
            });
          } else {
            setFieldValue(type, newValue);
            setValue(newValue);
          }
        }}
        id={type}
        options={parties}
        getOptionLabel={(option) => {
          // e.g value selected with enter, right from the input
          if (typeof option === "string") {
            return option.replace(/\w\S*/g, function (txt) {
              return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
            });
          }
          if (option.inputValue) {
            return option.inputValue.replace(/\w\S*/g, function (txt) {
              return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
            });
          }
          return option.name.replace(/\w\S*/g, function (txt) {
            return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
          });
        }}
        clearOnBlur
        handleHomeEndKeys
        renderOption={(props, option) => {
          return (
            <React.Fragment>
              <li {...props}>
                {option.name &&
                  option.name.replace(/\w\S*/g, function (txt) {
                    return (
                      txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase()
                    );
                  })}
                <br />
                {option.mobile && `(M) ${option.mobile.replace(/ /g, "")}`}
                <br />
                {option.city &&
                  `City - ${
                    JSON.parse(option.city).structured_formatting.main_text
                  }`}
                <Divider />
              </li>
            </React.Fragment>
          );
        }}
        fullWidth
        freeSolo
        renderInput={(params) => (
          <TextField
            onKeyPress={(e) => {
              e.which === 13 && e.preventDefault();
            }}
            {...params}
            label={type.charAt(0).toUpperCase() + type.slice(1)}
            error={Boolean(
              type === "customer"
                ? touched.customer && errors.customer
                : touched.transporter && errors.transporter
            )}
            onBlur={handleBlur}
            helperText={
              type === "customer"
                ? touched.customer && errors.customer
                : touched.transporter && errors.transporter
            }
            variant="outlined"
          />
        )}
      />

      <AddNewPartyFromAutocomplete
        open={open}
        toggleOpen={toggleOpen}
        dialogValue={dialogValue}
        setDialogValue={setDialogValue}
        setFieldValue={formik.setFieldValue}
        type={type}
      />
    </React.Fragment>
  );
};
export default PartyAutocomplete;
