import React, { useRef, useState } from "react";
import PropTypes from "prop-types";
import { useDropzone } from "react-dropzone";
import Cropper from "react-cropper";
import "cropperjs/dist/cropper.css";

import { Box, Button, Link, Typography } from "@mui/material";

export const OrganisationLogoDropzone = (props) => {
  const {
    accept,
    disabled,
    file,
    getFilesFromEvent,
    maxFiles,
    maxSize,
    minSize,
    noClick,
    noDrag,
    noDragEventsBubbling,
    noKeyboard,
    onDrop,
    onDropAccepted,
    onDropRejected,
    onFileDialogCancel,
    onRemove,
    onRemoveAll,
    onUpload,
    preventDropOnDocument,
    setCroppedImage,
    showModal,
    onModalClose,
    imgURL,
    onSaveHandler,
    ...other
  } = props;

  // We did not add the remaining props to avoid component complexity
  // but you can simply add it if you need to.
  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    accept,
    maxFiles,
    maxSize,
    minSize,
    onDrop,
    onDropAccepted,
  });

  const cropperRef = useRef(null);
  const [cropper, setCropper] = useState();

  const getCroppedImage = () => {
    if (typeof cropper !== "undefined") {
      setCroppedImage(cropper.getCroppedCanvas().toDataURL());
    }
  };

  return (
    <div {...other}>
      {!file && (
        <Box
          sx={{
            alignItems: "center",
            border: 1,
            borderRadius: 1,
            borderStyle: "dashed",
            borderColor: "divider",
            display: "flex",
            flexWrap: "wrap",
            justifyContent: "center",
            outline: "none",
            p: 6,
            ...(isDragActive && {
              backgroundColor: "action.active",
              opacity: 0.5,
            }),
            "&:hover": {
              backgroundColor: "action.hover",
              cursor: "pointer",
              opacity: 0.5,
            },
          }}
          {...getRootProps()}
        >
          <input {...getInputProps()} />
          <Box
            sx={{
              "& img": {
                width: 100,
              },
            }}
          >
            <img alt="Select file" src="/static/undraw_add_file2_gvbb.svg" />
          </Box>
          <Box sx={{ p: 2 }}>
            <Typography variant="h6">
              {`Select file${maxFiles && maxFiles === 1 ? "" : "s"}`}
            </Typography>
            <Box sx={{ mt: 2 }}>
              <Typography variant="body1">
                {`Drop file${maxFiles && maxFiles === 1 ? "" : "s"}`}{" "}
                <Link underline="always">browse</Link> thorough your machine
              </Typography>
            </Box>
          </Box>
        </Box>
      )}
      {file && (
        <Box sx={{ mt: 2 }}>
          <Cropper
            src={file.preview}
            style={{ height: 500, width: "732px" }}
            viewMode={0}
            // dragMode="move"
            guides={true}
            initialAspectRatio={4.5}
            background={true}
            crop={onCrop}
            ref={cropperRef}
            minCropBoxHeight={10}
            minCropBoxWidth={10}
            responsive={true}
            autoCropArea={1}
            onInitialized={(instance) => {
              setCropper(instance);
            }}
            checkOrientation={false}
          />
          <Box
            sx={{
              display: "flex",
              justifyContent: "flex-end",
              mt: 2,
            }}
          >
            <Button onClick={onRemove} size="small" type="button">
              Remove
            </Button>
            <Button
              onClick={getCroppedImage}
              size="small"
              sx={{ ml: 2 }}
              type="button"
              variant="contained"
            >
              Save
            </Button>
          </Box>
        </Box>
      )}
    </div>
  );
};

OrganisationLogoDropzone.propTypes = {
  accept: PropTypes.oneOfType([
    PropTypes.string,
    PropTypes.arrayOf(PropTypes.string),
  ]),
  disabled: PropTypes.bool,
  file: PropTypes.array,
  getFilesFromEvent: PropTypes.func,
  maxFiles: PropTypes.number,
  maxSize: PropTypes.number,
  minSize: PropTypes.number,
  noClick: PropTypes.bool,
  noDrag: PropTypes.bool,
  noDragEventsBubbling: PropTypes.bool,
  noKeyboard: PropTypes.bool,
  onDrop: PropTypes.func,
  onDropAccepted: PropTypes.func,
  onDropRejected: PropTypes.func,
  onFileDialogCancel: PropTypes.func,
  onRemove: PropTypes.func,
  onRemoveAll: PropTypes.func,
  onUpload: PropTypes.func,
  preventDropOnDocument: PropTypes.bool,
};

OrganisationLogoDropzone.defaultProps = {
  files: [],
};
