import { useRouter } from "next/router";
import toast from "react-hot-toast";
import * as Yup from "yup";
import { useFormik } from "formik";
import {
  Box,
  Button,
  Card,
  CardContent,
  Grid,
  Switch,
  TextField,
  Typography,
} from "@mui/material";
import { useAuth } from "../../../hooks/use-auth";
import { v4 as uuid } from "uuid";
import { partyApi } from "../../../api/party-api";
import { useDispatch } from "../../../store";
import GoogleMaps from "./google-places-autocomplete";

export const PartyCreateForm = (props) => {
  const router = useRouter();
  const dispatch = useDispatch();
  const { user } = useAuth();
  const formik = useFormik({
    initialValues: {
      name: "",
      mobile: "",
      city: "",
      isTransporter: false,
    },
    validationSchema: Yup.object().shape({
      name: Yup.string().max(255).required("Name is required"),
      mobile: Yup.string()
        .matches(/^[6-9]\d{9}$/, "Phone number is not valid")
        .required("Mobile is required"),
    }),
    onSubmit: async (values, helpers) => {
      try {
        // NOTE: Make API request
        const id = uuid();
        values.id = id;
        values.user = user.id;

        await partyApi.createParty(values, dispatch);

        toast.success("Party created!");
        // router.push("/dashboard/parties");
      } catch (err) {
        console.error(err);
        toast.error("Something went wrong!");
        helpers.setStatus({ success: false });
        helpers.setErrors({ submit: err.message });
        helpers.setSubmitting(false);
      }
    },
  });

