import { useEffect, useRef, useState } from "react";
import NextLink from "next/link";
import Head from "next/head";
import { useTranslation } from "react-i18next";
import moment from "moment";
import {
  Box,
  Button,
  Divider,
  Grid,
  InputAdornment,
  Tab,
  Tabs,
  TextField,
  Typography,
} from "@mui/material";
import { styled } from "@mui/material/styles";
import { orderApi } from "../../../api/order-api";
import { AuthGuard } from "../../../components/authentication/auth-guard";
import { DashboardLayout } from "../../../components/dashboard/dashboard-layout";
import { OrderDrawer } from "../../../components/dashboard/order/order-drawer";
import OrderGrid from "../../../components/dashboard/order/order-grid";
import { useMounted } from "../../../hooks/use-mounted";
import { useAuth } from "../../../hooks/use-auth";
import { Plus as PlusIcon } from "../../../icons/plus";
import { Search as SearchIcon } from "../../../icons/search";
import { gtm } from "../../../lib/gtm";

// DataStore Imports
// import { DataStore, Predicates } from "@aws-amplify/datastore";
// import { Order } from "../../../models";

const tabs = [
  {
    label: "All",
    value: "all",
  },
  {
    label: "Canceled",
    value: "canceled",
  },
  {
    label: "Completed",
    value: "complete",
  },
  {
    label: "Pending",
    value: "pending",
  },
  {
    label: "Rejected",
    value: "rejected",
  },
];

const sortOptions = [
  {
    label: "Newest",
    value: "desc",
  },
  {
    label: "Oldest",
    value: "asc",
  },
];

const applyFilters = (orders, filters) =>
  orders.filter((order) => {
    if (filters.query) {
      // Checks only the order number, but can be extended to support other fields, such as customer
      // name, email, etc.
      const containsQuery = order.number
        .toLowerCase()
        .includes(filters.query.toLowerCase());

      if (!containsQuery) {
        return false;
      }
    }

    if (typeof filters.status !== "undefined") {
      const statusMatched = order.status === filters.status;

      if (!statusMatched) {
        return false;
      }
    }

    return true;
  });

const applySort = (orders, order) =>
  orders.sort((a, b) => {
    const comparator = a.createdAt > b.createdAt ? -1 : 1;

    return order === "desc" ? comparator : -comparator;
  });

const OrderListInner = styled("div", {
  shouldForwardProp: (prop) => prop !== "open",
})(({ theme, open }) => ({
  flexGrow: 1,
  overflow: "hidden",
  paddingBottom: theme.spacing(8),
  paddingTop: theme.spacing(8),
  zIndex: 1,
  [theme.breakpoints.up("lg")]: {
    marginRight: -500,
  },
  transition: theme.transitions.create("margin", {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  ...(open && {
    [theme.breakpoints.up("lg")]: {
      marginRight: 0,
    },
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  }),
}));

const OrderList = () => {
  const isMounted = useMounted();
  const { t } = useTranslation();
  const { user } = useAuth();
  const rootRef = useRef(null);
  const queryRef = useRef(null);
  const [currentTab, setCurrentTab] = useState("all");
  const [sort, setSort] = useState("desc");
  const [orders, setOrders] = useState([]);

  const [filters, setFilters] = useState({
    query: "",
    status: undefined,
  });
  const [drawer, setDrawer] = useState({
    isOpen: false,
    orderId: null,
  });

  // useEffect(
  //   async () => {
  //     let dataStoreItems = await DataStore.query(Order);
  //     let ordersforgrid = _.map(dataStoreItems, (item) => {
  //       return {
  //         _id: item._id,
  //         orderNo: item.orderNo,
  //         saleDate: item.saleDate,
  //         customer: item.customer,
  //       };
  //     });
  //     setOrders(ordersforgrid);
  //   },
  //   // eslint-disable-next-line react-hooks/exhaustive-deps
  //   []
  // );

  // API FETCHING OF ORDERS FROM APP SYNC

  useEffect(async () => {
    try {
      const ordersDB = await orderApi.getOrdersByUser(user);
      setOrders(ordersDB.data.ordersByUser.items);
    } catch (error) {
      console.log(error);
    }
  }, []);

  const newOrder = useEffect(() => {
    gtm.push({ event: "page_view" });
  }, []);

  const handleTabsChange = (event, value) => {
    setCurrentTab(value);
    setFilters((prevState) => ({
      ...prevState,
      status: value === "all" ? undefined : value,
    }));
  };

  const handleQueryChange = (event) => {
    event.preventDefault();
    setFilters((prevState) => ({
      ...prevState,
      query: queryRef.current?.value,
    }));
  };

  const handleSortChange = (event) => {
    const value = event.target.value;
    setSort(value);
  };

  const handleCloseDrawer = () => {
    setDrawer({
      isOpen: false,
      orderId: null,
    });
  };

  // Usually query is done on backend with indexing solutions
  const filteredOrders = applyFilters(orders, filters);
  const sortedOrders = applySort(filteredOrders, sort);

  return (
    <>
      <Head>
        <title>Welcome on Truckar</title>
      </Head>
      <Box
        component="main"
        ref={rootRef}
        sx={{
          backgroundColor: "background.paper",
          display: "flex",
          flexGrow: 1,
          overflow: "hidden",
        }}
      >
        <OrderListInner open={drawer.isOpen}>
          <Box sx={{ px: 3 }}>
            <Grid container justifyContent="space-between" spacing={3}>
              <Grid item>
                <Typography variant="h4">{`${t("Welcome")} ${
                  user.name
                },`}</Typography>
              </Grid>
            </Grid>
            <Tabs
              indicatorColor="primary"
              onChange={handleTabsChange}
              scrollButtons="auto"
              textColor="primary"
              value={currentTab}
              sx={{ mt: 3 }}
              variant="scrollable"
            >
              {tabs.map((tab) => (
                <Tab key={tab.value} label={tab.label} value={tab.value} />
              ))}
            </Tabs>
          </Box>
          <Divider />
          <Grid container spacing={4}>
            {displayBanner && (
              <Grid item xs={12}>
                <OverviewBanner onDismiss={handleDismissBanner} />
              </Grid>
            )}
            <Grid item md={6} xs={12}>
              <OverviewCryptoWallet />
            </Grid>
            <Grid item md={6} xs={12}>
              <OverviewPrivateWallet />
            </Grid>
            <Grid item md={8} xs={12}>
              <OverviewTotalTransactions />
            </Grid>
            <Grid item md={4} xs={12}>
              <OverviewTotalBalance />
            </Grid>
            <Grid item md={8} xs={12}>
              <OverviewLatestTransactions />
            </Grid>
            <Grid item md={4} xs={12}>
              <OverviewInbox />
            </Grid>
            <Grid item md={6} xs={12}>
              <Card>
                <CardContent>
                  <Box
                    sx={{
                      alignItems: "center",
                      display: "flex",
                    }}
                  >
                    <BriefcaseIcon color="primary" fontSize="small" />
                    <Typography
                      color="primary.main"
                      sx={{ pl: 1 }}
                      variant="subtitle2"
                    >
                      Jobs
                    </Typography>
                  </Box>
                  <Typography variant="h6" sx={{ mt: 2 }}>
                    Find your dream job
                  </Typography>
                  <Typography color="textSecondary" variant="body2">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed
                    do eiusmod tempor incididunt ut labore et dolore magna
                    aliqua.
                  </Typography>
                </CardContent>
                <Divider />
                <CardActions>
                  <Button
                    endIcon={<ArrowRightIcon fontSize="small" />}
                    size="small"
                  >
                    Search Jobs
                  </Button>
                </CardActions>
              </Card>
            </Grid>
            <Grid item md={6} xs={12}>
              <Card>
                <CardContent>
                  <Box
                    sx={{
                      alignItems: "center",
                      display: "flex",
                    }}
                  >
                    <InformationCircleOutlinedIcon color="primary" />
                    <Typography
                      color="primary.main"
                      sx={{ pl: 1 }}
                      variant="subtitle2"
                    >
                      Help Center
                    </Typography>
                  </Box>
                  <Typography sx={{ mt: 2 }} variant="h6">
                    Need help figuring things out?
                  </Typography>
                  <Typography color="textSecondary" variant="body2">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed
                    do eiusmod tempor incididunt ut labore et dolore magna
                    aliqua.
                  </Typography>
                </CardContent>
                <Divider />
                <CardActions>
                  <Button
                    endIcon={<ExternalLinkIcon fontSize="small" />}
                    size="small"
                  >
                    Help Center
                  </Button>
                </CardActions>
              </Card>
            </Grid>
            <Grid item md={6} xs={12}>
              <Card>
                <CardContent>
                  <Box
                    sx={{
                      alignItems: "center",
                      display: "flex",
                    }}
                  >
                    <DownloadIcon color="primary" />
                    <Typography
                      color="primary.main"
                      sx={{ pl: 1 }}
                      variant="subtitle2"
                    >
                      Download
                    </Typography>
                  </Box>
                  <Typography sx={{ mt: 2 }} variant="h6">
                    Download our Free PDF and learn how to get more job leads
                  </Typography>
                  <Typography color="textSecondary" variant="body2">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed
                    do eiusmod tempor incididunt ut labore et dolore magna
                    aliqua.
                  </Typography>
                </CardContent>
                <Divider />
                <CardActions>
                  <Button
                    endIcon={<DownloadIcon fontSize="small" />}
                    size="small"
                    variant="outlined"
                  >
                    Download Free PDF
                  </Button>
                </CardActions>
              </Card>
            </Grid>
            <Grid item md={6} xs={12}>
              <Card>
                <CardContent>
                  <Box
                    sx={{
                      alignItems: "center",
                      display: "flex",
                    }}
                  >
                    <UsersIcon color="primary" />
                    <Typography
                      color="primary.main"
                      sx={{ pl: 1 }}
                      variant="subtitle2"
                    >
                      Contacts
                    </Typography>
                  </Box>
                  <Typography sx={{ mt: 2 }} variant="h6">
                    Contacts allow you to manage your company contracts
                  </Typography>
                  <Typography color="textSecondary" variant="body2">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed
                    do eiusmod tempor incididunt ut labore et dolore magna
                    aliqua.
                  </Typography>
                </CardContent>
                <Divider />
                <CardActions>
                  <Button
                    endIcon={<ArrowRightIcon fontSize="small" />}
                    size="small"
                    variant="outlined"
                  >
                    My Contacts
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          </Grid>
        </OrderListInner>
        <OrderDrawer
          containerRef={rootRef}
          onClose={handleCloseDrawer}
          open={drawer.isOpen}
          order={orders.find((order) => order.id === drawer.orderId)}
        />
      </Box>
    </>
  );
};

OrderList.getLayout = (page) => (
  <AuthGuard>
    {/* <DashboardLayout> */}
    {page}
    {/* </DashboardLayout> */}
  </AuthGuard>
);

export default OrderList;
