import { useEffect, useState } from "react";
import NextLink from "next/link";
import numeral from "numeral";
import PropTypes from "prop-types";
import {
  Avatar,
  Box,
  Button,
  Checkbox,
  IconButton,
  Link,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TablePagination,
  TableRow,
  Typography,
} from "@mui/material";
import { ArrowRight as ArrowRightIcon } from "../../../icons/arrow-right";
import { PencilAlt as PencilAltIcon } from "../../../icons/pencil-alt";
import { getInitials } from "../../../utils/get-initials";
import { Scrollbar } from "../../scrollbar";

export const PartyListTable = (props) => {
  const {
    parties,
    partiesCount,
    onPageChange,
    onRowsPerPageChange,
    page,
    rowsPerPage,
    ...other
  } = props;
  const [selectedParties, setSelectedParties] = useState([]);

  // Reset selected parties when parties change
  useEffect(
    () => {
      if (selectedParties.length) {
        setSelectedParties([]);
      }
    },
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [parties]
  );

  const handleSelectAllParties = (event) => {
    setSelectedParties(
      event.target.checked ? parties.map((party) => party.id) : []
    );
  };

  const handleSelectOneParty = (event, partyId) => {
    if (!selectedParties.includes(partyId)) {
      setSelectedParties((prevSelected) => [...prevSelected, partyId]);
    } else {
      setSelectedParties((prevSelected) =>
        prevSelected.filter((id) => id !== partyId)
      );
    }
  };

  const enableBulkActions = selectedParties.length > 0;
  const selectedSomeParties =
    selectedParties.length > 0 && selectedParties.length < parties.length;
  const selectedAllParties = selectedParties.length === parties.length;

  return (
    <div {...other}>
      <Box
        sx={{
          backgroundColor: "neutral.100",
          display: !enableBulkActions && "none",
          px: 2,
          py: 0.5,
        }}
      >
        <Checkbox
          checked={selectedAllParties}
          indeterminate={selectedSomeParties}
          onChange={handleSelectAllParties}
        />
        <Button size="small" sx={{ ml: 2 }}>
          Delete
        </Button>
        <Button size="small" sx={{ ml: 2 }}>
          Edit
        </Button>
      </Box>
      <Scrollbar>
        <Table sx={{ minWidth: 700 }}>
          <TableHead
            sx={{ visibility: enableBulkActions ? "collapse" : "visible" }}
          >
            <TableRow>
              <TableCell padding="checkbox">
                <Checkbox
                  checked={selectedAllParties}
                  indeterminate={selectedSomeParties}
                  onChange={handleSelectAllParties}
                />
              </TableCell>
              <TableCell>Name</TableCell>
              <TableCell>Location</TableCell>
              <TableCell>Orders</TableCell>
              <TableCell>Spent</TableCell>
              <TableCell align="right">Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {parties.map((party) => {
              const isPartieselected = selectedParties.includes(party.id);

              return (
                <TableRow hover key={party.id} selected={isPartieselected}>
                  <TableCell padding="checkbox">
                    <Checkbox
                      checked={isPartieselected}
                      onChange={(event) =>
                        handleSelectOneParty(event, party.id)
                      }
                      value={isPartieselected}
                    />
                  </TableCell>
                  <TableCell>
                    <Box
                      sx={{
                        alignItems: "center",
                        display: "flex",
                      }}
                    >
                      <Avatar
                        src={party.avatar}
                        sx={{
                          height: 42,
                          width: 42,
                        }}
                      >
                        {party.name}
                      </Avatar>
                      <Box sx={{ ml: 1 }}>
                        <NextLink href="/dashboard/parties/1" passHref>
                          <Link color="inherit" variant="subtitle2">
                            {party.name}
                          </Link>
                        </NextLink>
                        <Typography color="textSecondary" variant="body2">
                          {party.email}
                        </Typography>
                      </Box>
                    </Box>
                  </TableCell>
                  <TableCell>
                    {`${party.city}, ${party.state}, ${party.country}`}
                  </TableCell>
                  <TableCell>{party.totalOrders}</TableCell>
                  <TableCell>
                    <Typography color="success.main" variant="subtitle2">
                      {numeral(party.totalAmountSpent).format(
                        `${party.currency}0,0.00`
                      )}
                    </Typography>
                  </TableCell>
                  <TableCell align="right">
                    <NextLink href="/dashboard/parties/1/edit" passHref>
                      <IconButton component="a">
                        <PencilAltIcon fontSize="small" />
                      </IconButton>
                    </NextLink>
                    <NextLink href="/dashboard/parties/1" passHref>
                      <IconButton component="a">
                        <ArrowRightIcon fontSize="small" />
                      </IconButton>
                    </NextLink>
                  </TableCell>
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </Scrollbar>
      <TablePagination
        component="div"
        count={partiesCount}
        onPageChange={onPageChange}
        onRowsPerPageChange={onRowsPerPageChange}
        page={page}
        rowsPerPage={rowsPerPage}
        rowsPerPageOptions={[5, 10, 25]}
      />
    </div>
  );
};

PartyListTable.propTypes = {
  parties: PropTypes.array.isRequired,
  partiesCount: PropTypes.number.isRequired,
  onPageChange: PropTypes.func,
  onRowsPerPageChange: PropTypes.func,
  page: PropTypes.number.isRequired,
  rowsPerPage: PropTypes.number.isRequired,
};
