import React, { useState, useEffect, useCallback } from "react";
import PropTypes from "prop-types";
import { Autocomplete, Grid, TextField } from "@mui/material";
import { useDispatch } from "../../../../store";

const saleTypeAutocomplete = ({
  touched,
  setFieldValue,
  errors,
  handleBlur,
  values,
  saleType,
  user,
}) => {
  const [open, setOpen] = useState(false);
  const [inputValue, setInputValue] = React.useState("");

  const saleTypes = [
    {
      value: "quantity",
      unit: "MT",
      label: "Per MT",
    },
    {
      value: "quantity",
      unit: "Kg",
      label: "Per Kg",
    },
    {
      value: "quantity",
      unit: "Box",
      label: "Per Box",
    },
    {
      value: "quantity",
      unit: "Pcs",
      label: "Per Pcs",
    },
    {
      value: "time",
      unit: "Days",
      label: "Per Days",
    },
    {
      value: "fixed",
      label: "Fixed",
    },
  ];

  useEffect(() => {
    console.log("saleType has changed");
    setFieldValue("saleType", saleType);
  }, [setFieldValue, saleType]);

  const handleOnChange = (event, newValue) => {
    setFieldValue("saleType", newValue);
  };

  const handleInputChange = (event, newInputValue) => {
    setInputValue(newInputValue);
  };

  return (
    <Grid item>
      <Autocomplete
        autoSelect={true}
        blurOnSelect={true}
        id="saleType"
        open={open}
        onOpen={() => {
          setOpen(true);
        }}
        onClose={() => {
          setOpen(false);
        }}
        getOptionSelected={(option, value) => {
          if (value)
            return (
              option.name ===
              value.name.replace(/\w\S*/g, function (txt) {
                return (
                  txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase()
                );
              })
            );
        }}
        getOptionLabel={(option) => {
          return (
            option &&
            option.name.replace(/\w\S*/g, function (txt) {
              return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
            })
          );
        }}
        options={saleTypes}
        value={values.saleType}
        onChange={handleOnChange}
        inputValue={inputValue}
        onInputChange={handleInputChange}
        renderInput={(params) => (
          <TextField
            {...params}
            name="saleType"
            label="saleType"
            variant="outlined"
            error={Boolean(touched.saleType && errors.saleType)}
            fullWidth
            helperText={touched.saleType && errors.saleType}
            onBlur={handleBlur}
          />
        )}
      />
    </Grid>
  );
};

saleTypeAutocomplete.propTypes = {
  className: PropTypes.string,
};

export default saleTypeAutocomplete;
