import PropTypes from "prop-types";
import { Box, Button, Card, Chip, Typography } from "@mui/material";
import { useTranslation } from "react-i18next";
import { ArrowRight as ArrowRightIcon } from "../../../icons/arrow-right";

export const OverviewBanner = (props) => {
  const { t } = useTranslation();
  const { onDismiss, ...other } = props;

  return (
    <Card
      sx={{
        alignItems: "center",
        backgroundColor: "primary.main",
        color: "primary.contrastText",
        display: "flex",
        flexDirection: {
          xs: "column",
          md: "row",
        },
        p: 4,
      }}
      {...other}
    >
      <Box
        sx={{
          mr: 4,
          width: 200,
          height: 200,
          "& img": {
            height: 200,
            width: "auto",
          },
        }}
      >
        <img alt="" src="/static/banner-illustration.png" />
      </Box>
      <div>
        <Typography color="inherit" sx={{ mt: 2 }} variant="h4">
          {t("Welcome to Truckar!")}
        </Typography>
        <Typography color="inherit" sx={{ mt: 1 }} variant="subtitle2">
          <ul>
            <li>
              <Button
                color="inherit"
                startIcon={<ArrowRightIcon fontSize="small" />}
              >
                {t("Add your business details")}
              </Button>
            </li>
            <li>{t("Add your fleet")}</li>
            <li>{t("Add Driver Details")}</li>
          </ul>
        </Typography>
        <Box sx={{ mt: 2 }}>
          <Button color="secondary" onClick={onDismiss} variant="contained">
            {t("Dismiss Banner")}
          </Button>
        </Box>
      </div>
    </Card>
  );
};

OverviewBanner.propTypes = {
  onDismiss: PropTypes.func,
};
