import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { format } from "date-fns";
import { Storage } from "aws-amplify";
import { useFormik } from "formik";
import {
  Box,
  Button,
  Divider,
  Drawer,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  TextField,
  Typography,
  useMediaQuery,
} from "@mui/material";
import { styled } from "@mui/material/styles";
import EditIcon from "@mui/icons-material/Edit";
import { X as XIcon } from "../../../icons/x";
import { PropertyList } from "../../property-list";
import { PropertyListItem } from "../../property-list-item";
import { Scrollbar } from "../../scrollbar";

const OrganisationPreview = (props) => {
  const { lgUp, onApprove, onEdit, onReject, organisation } = props;
  const align = lgUp ? "horizontal" : "vertical";
  const [organisationLogo, setOrganisationLogo] = useState();

  console.log(organisation);

  useEffect(async () => {
    try {
      const logo = await Storage.get(organisation.logo);
      setOrganisationLogo(logo);
    } catch (error) {
      console.log(error);
    }
  }, []);

  return (
    <>
      {organisationLogo && (
        <img
          style={{ width: "100%", height: "auto" }}
          src={organisationLogo}
          alt="Business Logo"
        />
      )}
      <Box
        sx={{
          alignItems: "center",
          borderRadius: 1,
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
        }}
      >
        <Typography sx={{ mt: 3 }} variant="h6">
          Details
        </Typography>
        <Box
          sx={{
            alignItems: "center",
            display: "flex",
            flexWrap: "wrap",
            mt: 3,
            m: -1,
            "& > button": {
              m: 1,
            },
          }}
        >
          <Button
            onClick={onEdit}
            size="small"
            startIcon={<EditIcon fontSize="small" />}
            sx={{ pt: 3 }}
          >
            Edit
          </Button>
        </Box>
      </Box>
      <Divider sx={{ my: 3 }} />

      <PropertyList>
        <PropertyListItem align={align} disableGutters label="Business Name">
          <Typography color="primary" variant="body2">
            {`${organisation.name} (${organisation.initials})`}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.addressLine1}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.addressLine2}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.city}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.pincode}
          </Typography>
        </PropertyListItem>

        <PropertyListItem align={align} disableGutters label="Bank Details">
          <Typography color="textSecondary" variant="body2">
            {organisation.bankName}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.bankAccountNumber &&
              `A/c: ${organisation.bankAccountNumber}`}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.bankIFSC && `IFSC: ${organisation.bankIFSC}`}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {organisation.bankBranchName &&
              `Branch: ${organisation.bankBranchName}`}
          </Typography>
        </PropertyListItem>

        <Typography sx={{ mt: 3 }} variant="h6">
          Billing Details
        </Typography>
        <Divider sx={{ my: 3 }} />

        <PropertyListItem
          align={align}
          disableGutters
          label="PAN"
          value={organisation.pan}
        />

        <PropertyListItem
          align={align}
          disableGutters
          label="GSTIN"
          value={organisation.gstin}
        />

        <PropertyListItem
          align={align}
          disableGutters
          label="Contact No."
          value={organisation.contact}
        />

        <PropertyListItem
          align={align}
          disableGutters
          label="E-mail"
          value={organisation.email}
        />

        <PropertyListItem
          align={align}
          disableGutters
          label="Terms & Conditions"
          value={organisation.invoiceTermsAndConditions}
        />
      </PropertyList>
    </>
  );
};

const OrganisationForm = (props) => {
  const { onCancel, onSave, organisation } = props;

  const formik = useFormik({
    initialValues: {
      name: organisation.name,
      initials: organisation.initials,
      addressLine1: organisation.addressLine1,
      addressLine2: organisation.addressLine2,
      city: organisation.city,
      pincode: organisation.pincode,
      gstin: organisation.gstin,
      pan: organisation.pan,
      invoiceTermsAndConditions: organisation.invoiceTermsAndConditions,
      bankName: organisation.bankName,
      bankAccountNumber: organisation.bankAccountNumber,
      bankBranchName: organisation.bankBranchName,
      bankIFSC: organisation.bankIFSC,
      contact: organisation.contact,
    },
    // validationSchema: Yup.object({
    //   name: Yup.string().max(255).required("Name is required"),
    //   initials: Yup.string().max(255).required("Required"),
    //   addressLine1: Yup.string()
    //     .max(255)
    //     .required("Address Line 1 is required"),
    //   city: Yup.string().max(255).required("City is required"),
    //   pincode: Yup.string().max(255).required("Pincode is required"),
    //   gstin: Yup.string()
    //     .trim()
    //     .matches(
    //       /^([0-9]){2}([a-zA-Z]){5}([0-9]){4}([a-zA-Z]){1}([0-9]){1}([a-zA-Z]){1}([0-9]){1}?$/,
    //       "Invalid GST Number"
    //     ),
    //   pan: Yup.string().max(255).required("PAN is required"),
    //   jurisdiction: Yup.string().max(255).required("Jurisdiction is required"),
    // }),
    onSubmit: async (values, helpers) => {
      try {
        // NOTE: Make API request
        const id = uuid();
        values.id = id;
        values.user = user.id;

        await API.graphql({
          query: createOrganisation,
          variables: { input: values },
          authMode: "AMAZON_COGNITO_USER_POOLS",
        });

        toast.success("Organisation created!");
      } catch (err) {
        console.error(err);
        toast.error("Something went wrong!");
        helpers.setStatus({ success: false });
        helpers.setErrors({ submit: err.message });
        helpers.setSubmitting(false);
      }
    },
  });

  return (
    <>
      <Box
        sx={{
          alignItems: "center",
          backgroundColor: (theme) =>
            theme.palette.mode === "dark" ? "neutral.800" : "neutral.100",
          borganisationRadius: 1,
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
          px: 3,
          py: 2.5,
        }}
      >
        <Typography variant="overline" sx={{ mr: 2 }} color="textSecondary">
          Organisation
        </Typography>
        <Box
          sx={{
            alignItems: "center",
            display: "flex",
            m: -1,
            "& > button": {
              m: 1,
            },
          }}
        >
          <Button
            color="primary"
            onClick={onSave}
            size="small"
            variant="contained"
          >
            Save changes
          </Button>
          <Button onClick={onCancel} size="small" variant="outlined">
            Cancel
          </Button>
        </Box>
      </Box>
      <Typography sx={{ my: 3 }} variant="h6">
        Details
      </Typography>
      <TextField
        margin="normal"
        error={Boolean(formik.touched.name && formik.errors.name)}
        fullWidth
        helperText={formik.touched.name && formik.errors.name}
        label="Business Name"
        name="name"
        onBlur={formik.handleBlur}
        onChange={(event) => {
          formik.setFieldValue(
            `initials`,
            `${getInitials(event.target.value)}`
          );
          formik.setFieldValue(`name`, event.target.value);
        }}
        value={formik.values.name}
        variant="outlined"
      />

      <TextField
        margin="normal"
        error={Boolean(formik.touched.initials && formik.errors.initials)}
        fullWidth
        helperText={formik.touched.initials && formik.errors.initials}
        label="Initials"
        name="initials"
        onBlur={formik.handleBlur}
        onChange={formik.handleChange}
        value={formik.values.initials}
        variant="outlined"
      />
      <TextField
        margin="normal"
        error={Boolean(
          formik.touched.addressLine1 && formik.errors.addressLine1
        )}
        fullWidth
        helperText={formik.touched.addressLine1 && formik.errors.addressLine1}
        label="Address Line 1"
        name="addressLine1"
        onBlur={formik.handleBlur}
        onChange={formik.handleChange}
        value={formik.values.addressLine1}
        variant="outlined"
      />
      <TextField
        margin="normal"
        error={Boolean(
          formik.touched.addressLine2 && formik.errors.addressLine2
        )}
        fullWidth
        helperText={formik.touched.addressLine2 && formik.errors.addressLine2}
        label="Address Line 2"
        name="addressLine2"
        onBlur={formik.handleBlur}
        onChange={formik.handleChange}
        value={formik.values.addressLine2}
        variant="outlined"
      />
      <TextField
        margin="normal"
        error={Boolean(formik.touched.city && formik.errors.city)}
        fullWidth
        helperText={formik.touched.city && formik.errors.city}
        label="City (Jurisdication)"
        name="city"
        onBlur={formik.handleBlur}
        onChange={formik.handleChange}
        value={formik.values.city}
        variant="outlined"
      />
      <TextField
        margin="normal"
        error={Boolean(formik.touched.pincode && formik.errors.pincode)}
        fullWidth
        helperText={formik.touched.pincode && formik.errors.pincode}
        label="Pincode"
        name="pincode"
        onBlur={formik.handleBlur}
        onChange={formik.handleChange}
        value={formik.values.pincode}
        variant="outlined"
      />

      <Typography sx={{ my: 3 }} variant="h6">
        Bank Details
      </Typography>

      <TextField
        error={Boolean(formik.touched.bankName && formik.errors.bankName)}
        fullWidth
        helperText={formik.touched.bankName && formik.errors.bankName}
        label="Bank name"
        name="bankName"
        onBlur={formik.handleBlur}
        onChange={formik.handleChange}
        value={formik.values.bankName}
        variant="outlined"
      />

      <TextField
        error={Boolean(
          formik.touched.bankAccountNumber && formik.errors.bankAccountNumber
        )}
        fullWidth
        helperText={
          formik.touched.bankAccountNumber && formik.errors.bankAccountNumber
        }
        label="Bank Account Number"
        name="bankAccountNumber"
        onBlur={formik.handleBlur}
        onChange={formik.handleChange}
        value={formik.values.bankAccountNumber}
        variant="outlined"
      />

      <TextField
        error={Boolean(
          formik.touched.bankBranchName && formik.errors.bankBranchName
        )}
        fullWidth
        helperText={
          formik.touched.bankBranchName && formik.errors.bankBranchName
        }
        label="Bank Branch Name"
        name="bankBranchName"
        onBlur={formik.handleBlur}
        onChange={formik.handleChange}
        value={formik.values.bankBranchName}
        variant="outlined"
      />

      <TextField
        error={Boolean(formik.touched.bankIFSC && formik.errors.bankIFSC)}
        fullWidth
        helperText={formik.touched.bankIFSC && formik.errors.bankIFSC}
        label="Bank IFS Code"
        name="bankIFSC"
        onBlur={formik.handleBlur}
        onChange={formik.handleChange}
        value={formik.values.bankIFSC}
        variant="outlined"
      />

      <Button color="error" sx={{ mt: 3 }}>
        Delete organisation
      </Button>
    </>
  );
};

const OrganisationDrawerDesktop = styled(Drawer)({
  width: 500,
  flexShrink: 0,
  "& .MuiDrawer-paper": {
    position: "relative",
    width: 500,
  },
});

const OrganisationDrawerMobile = styled(Drawer)({
  flexShrink: 0,
  maxWidth: "100%",
  height: "calc(100% - 64px)",
  width: 500,
  "& .MuiDrawer-paper": {
    height: "calc(100% - 64px)",
    maxWidth: "100%",
    top: 64,
    width: 500,
  },
});

export const OrganisationDrawer = (props) => {
  const { containerRef, onClose, open, organisation, ...other } = props;
  const [isEditing, setIsEditing] = useState(false);
  const lgUp = useMediaQuery((theme) => theme.breakpoints.up("lg"));

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setIsEditing(false);
  };

  // The reason for doing this, is that the persistent drawer has to be rendered, but not it's
  // content if an organisation is not passed.
  const content = organisation ? (
    <>
      <Box
        sx={{
          alignItems: "center",
          backgroundColor: "primary.main",
          color: "primary.contrastText",
          display: "flex",
          justifyContent: "space-between",
          px: 3,
          py: 2,
        }}
      >
        <Typography color="inherit" variant="h6">
          {organisation.number}
        </Typography>
        <IconButton color="inherit" onClick={onClose}>
          <XIcon fontSize="small" />
        </IconButton>
      </Box>
      <Box
        sx={{
          px: 3,
          py: 4,
        }}
      >
        {!isEditing ? (
          <OrganisationPreview
            onApprove={onClose}
            onEdit={handleEdit}
            onReject={onClose}
            organisation={organisation}
            lgUp={lgUp}
          />
        ) : (
          <OrganisationForm
            onCancel={handleCancel}
            onSave={handleCancel}
            organisation={organisation}
          />
        )}
      </Box>
    </>
  ) : null;

  if (lgUp) {
    return (
      <OrganisationDrawerDesktop
        anchor="right"
        open={open}
        SlideProps={{ container: containerRef?.current }}
        variant="persistent"
        {...other}
      >
        {content}
      </OrganisationDrawerDesktop>
    );
  }

  return (
    <OrganisationDrawerMobile
      anchor="right"
      ModalProps={{ container: containerRef?.current }}
      onClose={onClose}
      open={open}
      SlideProps={{ container: containerRef?.current }}
      variant="temporary"
      {...other}
    >
      {content}
    </OrganisationDrawerMobile>
  );
};

OrganisationDrawer.propTypes = {
  containerRef: PropTypes.any,
  onClose: PropTypes.func,
  open: PropTypes.bool,
  organisation: PropTypes.object,
};
