import { API } from "aws-amplify";
import { organisationsByUser } from "../graphql/queries";
import { createOrganisation, updateOrganisation } from "../graphql/mutations";
import { Organisation } from "../models";
import { DataStore, Predicates } from "@aws-amplify/datastore";
import moment from "moment";
import { slice } from "../slices/organisations";

const now = new Date();

class OrganisationApi {
  async getOrganisationsByUser(user, dispatch) {
    try {
      // const organisationesDB = await API.graphql({
      //   query: organisationsByUser,
      //   variables: { user: user.id.toString() },
      // });
      // console.log(organisationesDB.data.organisationsByUser.items);

      const response = await DataStore.query(Organisation, (c) =>
        c.user("eq", user.id)
      );

      dispatch(slice.actions.getOrganisations(response.data));
      console.log(response);

      return response;
    } catch (error) {
      console.log(error);
    }
  }

  async createOrganisation(newOrganisation) {
    const createdAt = moment().toISOString();
    let org = newOrganisation;
    org.createdAt = createdAt;

    // const response2 = await API.graphql({
    //   query: createOrganisation,
    //   variables: { input: newOrganisation },
    //   authMode: "AMAZON_COGNITO_USER_POOLS",
    // });

    const response = await DataStore.save(new Organisation(org));

    console.log(response);
    return response;
  }

  async editOrganisation(editedOrganisation) {
    const response = await API.graphql({
      query: updateOrganisation,
      variables: { input: editedOrganisation },
      authMode: "AMAZON_COGNITO_USER_POOLS",
    });
    return response;
  }
}

export const organisationApi = new OrganisationApi();
