export var getSumOfExpenses = expenses => {
  if (expenses && expenses.length > 0) {
    var total = 0;
    for (var i = 0; i < expenses.length; i++) {
      total += parseFloat(expenses[i].expenseAmount || 0);
    }
    return total;
  } else {
    return 0;
  }
};

