  console.log(expenses);
  var result = expenses.reduce(
    (acc, n) => {
      if (acc.hasOwnProperty(n.expenseName.name))
        acc[n.expenseName.name] += parseFloat(n.expenseAmount);
      else acc[n.expenseName.name] = parseFloat(n.expenseAmount);

      return acc;
    },

    {}
  );
  return result;
};

