  let sumOfBillWeight = 0;

  trips.map(trip => {
    if (Boolean(trip.billWeight)) {
      return (sumOfBillWeight = sumOfBillWeight + parseFloat(trip.billWeight));
    }
    return sumOfBillWeight;
  });
  let amount = 0;
  if (type === 'sale') {
    if (
      delivery.saleType === 'quantity' &&
      Boolean(delivery.saleMinimumQuantity)
    ) {
      if (
        parseFloat(sumOfBillWeight) < parseFloat(delivery.saleMinimumQuantity)
      ) {
        amount =
          (parseFloat(delivery.saleMinimumQuantity) *
            parseFloat(delivery.saleRate) *
            parseFloat(delivery.billWeight || 1)) /
          parseFloat(sumOfBillWeight || trips.length);
