export var getInvoiceWeight = (trips, delivery, type) => {
  let sumOfBillWeight = 0;
  let deliveryArray = [];
  trips.map(trip => {
    if (Boolean(trip.billWeight)) {
      deliveryArray.push(`${trip.billWeight} MT`);
      return (sumOfBillWeight = sumOfBillWeight + parseFloat(trip.billWeight));
    }
    deliveryArray.push(`${0} MT`);
    return sumOfBillWeight;

    // return sumOfBillWeight * parseFloat(trip.sale.saleRate);
  });
  let weight;
  let guarantee = false;
  let deliveryString = [];
  if (parseFloat(sumOfBillWeight) < parseFloat(trips[0].saleMinimumQuantity)) {
    deliveryString = deliveryArray.join(' + ');
  } else {
    deliveryString = `${delivery.billWeight || 0} MT`;
  }
  switch (type) {
    case 'sale':
      if (
        parseFloat(sumOfBillWeight) < parseFloat(trips[0].saleMinimumQuantity)
      ) {
        weight = parseFloat(trips[0].saleMinimumQuantity);
        guarantee = true;
      } else {
        weight = parseFloat(delivery.billWeight);
      }
      return { deliveryString, weight, guarantee };
    case 'purchase':
      if (
        parseFloat(sumOfBillWeight) <
        parseFloat(trips[0].purchaseMinimumQuantity)
      ) {
        weight = parseFloat(trips[0].purchaseMinimumQuantity);
        guarantee = true;
      } else {
        weight = parseFloat(delivery.billWeight);
      }
      return { deliveryString, weight, guarantee };

    default:
      return { deliveryString, weight: 0, guarantee };
  }
};

export var formatNumber = number => {
  // this puts commas into the number eg 1000 goes to 1,000,
  // i pulled this from stack overflow, i have no idea how it works
  return Math.round(number).toLocaleString('en-IN');
};

export var dataFormatter = (expr, value) => {
  if (typeof value === 'string') {
    return value;
  }
  let o = value;
  switch (expr) {
    case 'Turnover':
      o = value ? `₹ ${formatNumber(value)}` : '₹ 0';
      break;
    case 'Expenses':
      o = value ? `₹ ${formatNumber(value)}` : '₹ 0';
      break;
    case 'Profit':
      o = value ? `₹ ${formatNumber(value)}` : '₹ 0';
      break;
    case 'Profit / Trip':
      o = value ? `₹ ${formatNumber(value)}` : '₹ 0';
      break;
    case 'Profit / Day':
      o = value ? `₹ ${formatNumber(value)}` : '₹ 0';
      break;
    case 'Profit / Turnover':
      o = value ? Math.round(value * 10000) / 100 + ' %' : '0 %';
      break;

    default:
      return o;
  }
  return o;
};

export var calculateAmount = (trips, type, advance = false) => {
  let sumOfBillWeight = 0;
  trips.map(trip => {
    if (Boolean(trip.billWeight)) {
      return (sumOfBillWeight = sumOfBillWeight + parseFloat(trip.billWeight));
    }
    return sumOfBillWeight;

    // return sumOfBillWeight * parseFloat(trip.sale.saleRate);
  });
  let amount = 0;

  switch (type) {
    case 'sale':
      switch (trips[0].saleType) {
        case 'quantity':
          amount = parseFloat(sumOfBillWeight) * parseFloat(trips[0].saleRate);
          if (
            parseFloat(sumOfBillWeight) <
            parseFloat(trips[0].saleMinimumQuantity || 0)
          ) {
            amount =
              parseFloat(trips[0].saleMinimumQuantity || 0) *
              parseFloat(trips[0].saleRate);
          }

          break;
        case 'fixed':
          amount = parseFloat(trips[0].saleRate);
          break;

        default:
          break;
      }
      return (
        parseFloat(amount) +
        //Adding Sale others of every delivery
        trips.reduce(function(sum, current) {
          return (
            parseFloat(sum) +
            parseFloat(getSumOfExpenses(current.saleOthers || 0))
          );
        }, 0) -
        (advance && parseFloat(trips[0].saleAdvance || 0))
      );

    case 'purchase':
      if (trips[0].transporter) {
        switch (trips[0].purchaseType) {
          case 'quantity':
            amount =
              parseFloat(sumOfBillWeight) * parseFloat(trips[0].purchaseRate);
            if (
              parseFloat(sumOfBillWeight) <
              parseFloat(trips[0].purchaseMinimumQuantity || 0)
            ) {
              amount =
                parseFloat(trips[0].purchaseMinimumQuantity || 0) *
                parseFloat(trips[0].purchaseRate);
            }

            break;
          case 'fixed':
            amount = parseFloat(trips[0].purchaseRate);
            break;

          case 'commission':
            switch (trips[0].commissionType) {
              case 'quantity':
                amount =
                  calculateAmount(trips, 'sale') -
                  parseFloat(sumOfBillWeight) *
                    parseFloat(trips[0].purchaseRate);
                if (
                  parseFloat(sumOfBillWeight) <
                  parseFloat(trips[0].saleMinimumQuantity || 0)
                ) {
                  amount =
                    calculateAmount(trips, 'sale') -
                    parseFloat(trips[0].saleMinimumQuantity) *
                      parseFloat(trips[0].purchaseRate);
                }
                break;

              case 'percentage':
                amount =
                  calculateAmount(trips, 'sale') -
                  (parseFloat(sumOfBillWeight) *
                    parseFloat(trips[0].purchaseRate) *
                    parseFloat(trips[0].saleRate)) /
                    100;
                if (
                  parseFloat(sumOfBillWeight) <
                  parseFloat(trips[0].saleMinimumQuantity || 0)
                ) {
                  amount =
                    calculateAmount(trips, 'sale') -
                    parseFloat(trips[0].saleMinimumQuantity) *
                      parseFloat(trips[0].purchaseRate);
                }
                break;
              case 'fixed':
                amount =
                  calculateAmount(trips, 'sale') -
                  parseFloat(trips[0].purchaseRate);
                break;
              default:
                amount = 0;
            }

            break;

          default:
            break;
        }
      } else {
        amount = 0;
      }
      return (
        parseFloat(amount) +
        //Adding Purchase others of every delivery
        trips.reduce(function(sum, current) {
          return parseFloat(sum) + (parseFloat(current.purchaseOthers) || 0);
        }, 0) -
        (advance && parseFloat(trips[0].purchaseAdvance || 0))
      );

    case 'outflow':
      amount = calculateAmount(trips, 'purchase');
      return Math.round(
        parseFloat(amount) +
          //Adding Trip Expenses
          (getSumOfExpenses(trips[0].tripExpenses) || 0)
      );

    default:
      return 0;
  }
};
