import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { Storage } from "aws-amplify";
import { useFormik } from "formik";
import toast from "react-hot-toast";
import {
  Box,
  Button,
  Divider,
  Drawer,
  IconButton,
  TextField,
  Typography,
  useMediaQuery,
} from "@mui/material";
import { styled } from "@mui/material/styles";
import EditIcon from "@mui/icons-material/Edit";
import { useDispatch } from "../../../store";
import { X as XIcon } from "../../../icons/x";
import { PropertyList } from "../../property-list";
import { PropertyListItem } from "../../property-list-item";
import { branchApi } from "../../../api/branch-api";
import { BranchLogoDropzone } from "./branch-logo-dropzone";
import { branchNumberFormatter } from "../../../utils/customFormatters";

const BranchPreview = (props) => {
  const dispatch = useDispatch();
  const { lgUp, onEdit, branch } = props;
  const align = lgUp ? "horizontal" : "vertical";
  const [branchLogo, setBranchLogo] = useState();
  const [file, setFile] = useState();
  const [croppedImage, setCroppedImage] = useState();

  const handleDrop = (newFile) => {
    newFile.length > 0 &&
      setFile(
        Object.assign(newFile[0], {
          preview: URL.createObjectURL(newFile[0]),
        })
      );
  };

  const handleRemove = (file) => {
    setFile();
    setCroppedImage("");
  };

  const handleSaveCroppedImage = async (cropper) => {
    const filename = `${branch.user}_branchLogo_${branch.id}`;
    let logoBase64;
    let logo;
    logoBase64 = await fetch(cropper.getCroppedCanvas().toDataURL());
    logo = await logoBase64.blob();
    await Storage.put(filename, logo);

    let editedBranch = {
      id: branch.id,
      _version: branch._version,
      logo: filename,
    };

    await branchApi.updateBranch(editedBranch, dispatch);
    setBranchLogo(logo);
  };

  useEffect(async () => {
    try {
      setBranchLogo(null);
      if (branch.logo) {
        const logo = await Storage.get(branch.logo);
        setBranchLogo(logo);
      } else {
        setBranchLogo(null);
      }
    } catch (error) {
      console.log(error);
    }
  }, [branch]);

  return (
    <>
      {branchLogo && (
        <img
          style={{ width: "100%", height: "auto" }}
          src={branchLogo}
          alt="Business Logo"
        />
      )}
      {!branchLogo && (
        <BranchLogoDropzone
          accept="image/*"
          file={file}
          croppedImage={croppedImage}
          onDrop={handleDrop}
          onRemove={handleRemove}
          maxFiles={1}
          onSaveCroppedImage={handleSaveCroppedImage}
          displayInDrawer={true}
        />
      )}
      <Box
        sx={{
          alignItems: "center",
          borderRadius: 1,
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
        }}
      >
        <Typography sx={{ mt: 3 }} variant="h6">
          Details
        </Typography>
        <Box
          sx={{
            alignItems: "center",
            display: "flex",
            flexWrap: "wrap",
            mt: 3,
            m: -1,
            "& > button": {
              m: 1,
            },
          }}
        >
          <Button
            onClick={onEdit}
            size="small"
            startIcon={<EditIcon fontSize="small" />}
            sx={{ pt: 3 }}
          >
            Edit
          </Button>
        </Box>
      </Box>
      <Divider sx={{ my: 3 }} />

