const BranchForm = (props) => {
  const { onCancel, branch } = props;
  const dispatch = useDispatch();
  const [file, setFile] = useState();
  const [croppedImage, setCroppedImage] = useState();

  const formik = useFormik({
    initialValues: {
      id: branch.id,
      branchNumber: branch.branchNumber,
      make: branch.make,
      model: branch.model,
      _version: branch._version,
    },
    // validationSchema: Yup.object({
    //   name: Yup.string().max(255).required("Name is required"),
    //   initials: Yup.string().max(255).required("Required"),
    //   addressLine1: Yup.string()
    //     .max(255)
    //     .required("Address Line 1 is required"),
    //   city: Yup.string().max(255).required("City is required"),
    //   pincode: Yup.string().max(255).required("Pincode is required"),
    //   gstin: Yup.string()
    //     .trim()
    //     .matches(
    //       /^([0-9]){2}([a-zA-Z]){5}([0-9]){4}([a-zA-Z]){1}([0-9]){1}([a-zA-Z]){1}([0-9]){1}?$/,
    //       "Invalid GST Number"
    //     ),
    //   pan: Yup.string().max(255).required("PAN is required"),
    //   jurisdiction: Yup.string().max(255).required("Jurisdiction is required"),
    // }),
    onSubmit: async (values, helpers) => {
      try {
        // NOTE: Make API request
        const filename = `${branch.user}_branchLogo_${branch.id}`;
        let logoBase64;
        let logo;
        if (values.logo) {
          logoBase64 = await fetch(values.logo);
          logo = await logoBase64.blob();
          values.logo = filename;
          await Storage.put(filename, logo);
        }

        await branchApi.updateBranch(values, dispatch);

        toast.success("Branch updated!");
        onCancel();
      } catch (err) {
        console.error(err);
        toast.error("Something went wrong!");
        helpers.setStatus({ success: false });
        helpers.setErrors({ submit: err.message });
        helpers.setSubmitting(false);
      }
    },
  });

  const handleDrop = (newFile) => {
    newFile.length > 0 &&
      setFile(
        Object.assign(newFile[0], {
          preview: URL.createObjectURL(newFile[0]),
        })
      );
  };

  const handleRemove = (file) => {
    setFile();
    setCroppedImage("");
  };

  const handleSaveCroppedImage = (cropper) => {
    setCroppedImage(cropper.getCroppedCanvas().toDataURL());
    formik.setFieldValue(`logo`, cropper.getCroppedCanvas().toDataURL());
  };

  return (
    <>
      <form onSubmit={formik.handleSubmit} {...props}>
        <Box
          sx={{
            alignItems: "center",
            backgroundColor: (theme) =>
              theme.palette.mode === "dark" ? "neutral.800" : "neutral.100",
            bbranchRadius: 1,
            display: "flex",
            flexWrap: "wrap",
            justifyContent: "space-between",
            px: 3,
            py: 2.5,
          }}
        >
          <Typography variant="overline" sx={{ mr: 2 }} color="textSecondary">
            Branch
          </Typography>
          <Box
            sx={{
              alignItems: "center",
              display: "flex",
              m: -1,
              "& > button": {
                m: 1,
              },
            }}
          >
            <Button
              color="primary"
              type="submit"
              size="small"
              variant="contained"
            >
              Save changes
            </Button>
            <Button onClick={onCancel} size="small" variant="outlined">
              Cancel
            </Button>
          </Box>
        </Box>
        <Box sx={{ mt: 3 }}>
          <BranchLogoDropzone
            accept="image/*"
            file={file}
            croppedImage={croppedImage}
            onDrop={handleDrop}
            onRemove={handleRemove}
            maxFiles={1}
            onSaveCroppedImage={handleSaveCroppedImage}
            displayInDrawer={true}
          />
          {croppedImage && (
            <img
              style={{ width: "100%", height: "auto" }}
              src={croppedImage}
              alt="cropped"
            />
          )}
          {croppedImage && (
            <Box
              sx={{
                display: "flex",

                mt: 2,
              }}
            >
              <Button onClick={handleRemove} size="small" type="button">
                Remove Logo
              </Button>
            </Box>
          )}
        </Box>

        <Typography sx={{ my: 3 }} variant="h6">
          Details
        </Typography>

        <TextField
          margin="normal"
          error={Boolean(
            formik.touched.branchNumber && formik.errors.branchNumber
          )}
          fullWidth
          helperText={formik.touched.branchNumber && formik.errors.branchNumber}
          label="Branch Number"
          name="branchNumber"
          onBlur={formik.handleBlur}
          onChange={(event) => {
            formik.setFieldValue(
              `branchNumber`,
              event.target.value.toUpperCase()
            );
          }}
          value={formik.values.branchNumber}
          variant="outlined"
          inputProps={{ style: { textTransform: "uppercase" } }}
          InputProps={{
            inputComponent: branchNumberFormatter,
          }}
        />

        <TextField
          margin="normal"
          error={Boolean(formik.touched.make && formik.errors.make)}
          fullWidth
          helperText={formik.touched.make && formik.errors.make}
          label="Make"
          name="make"
          onBlur={formik.handleBlur}
          onChange={formik.handleChange}
          value={formik.values.make}
          variant="outlined"
        />
        <TextField
          margin="normal"
          error={Boolean(formik.touched.model && formik.errors.model)}
          fullWidth
          helperText={formik.touched.model && formik.errors.model}
          label="Model"
          name="model"
          onBlur={formik.handleBlur}
          onChange={formik.handleChange}
          value={formik.values.model}
          variant="outlined"
        />

        <Button color="error" sx={{ mt: 3 }}>
          Delete branch
        </Button>
      </form>
    </>
  );
};

