import React from "react";
import { FieldArray } from "formik";
import { getIn } from "formik";

import { Grid, Typography, Button } from "@material-ui/core";

import GoogleMaps from "src/components/Formik/GoogleMapsAutocomplete";

const DeliveryForm = ({
  className,
  touched,
  setFieldValue,
  errors,
  handleBlur,
  handleChange,
  values,
  ...rest
}) => {
  const [noOfDeliveries, setNoOfDeliveries] = React.useState(1);

  return (
    <React.Fragment>
      <FieldArray name="deliveryDetails" error={errors}>
        {({ remove, push }) => (
          <React.Fragment>
            <Grid container spacing={3}>
              <Grid item>
                <Typography variant="h3" color="textSecondary">
                  No of Deliveries: {noOfDeliveries}
                </Typography>
              </Grid>
              <Grid item>
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={() => {
                    setNoOfDeliveries(noOfDeliveries + 1);
                    push({ loading: "", unloading: "" });
                  }}
                >
                  Add Delivery
                </Button>
              </Grid>
            </Grid>

            {values.deliveryDetails.length > 0 &&
              values.deliveryDetails.map((delivery, index) => {
                const loading = `deliveryDetails[${index}].loading`;
                const touchedLoading = getIn(touched, loading);
                const errorLoading = getIn(errors, loading);

                const unloading = `deliveryDetails[${index}].unloading`;
                const touchedUnloading = getIn(touched, unloading);
                const errorUnloading = getIn(errors, unloading);

                return (
                  <Grid container spacing={3} className="row" key={index}>
                    <Grid item md={3} xs={12} className="col" key={index}>
                      <GoogleMaps
                        error={errorLoading}
                        touched={touchedLoading}
                        name={loading}
                        setFieldValue={setFieldValue}
                        handleBlur={handleBlur}
                        values={values}
                        index={index}
                        type="loading"
                      />
                    </Grid>
                    <Grid item md={3} xs={12} className="col">
                      <GoogleMaps
                        error={errorUnloading}
                        touched={touchedUnloading}
                        name={loading}
                        setFieldValue={setFieldValue}
                        handleBlur={handleBlur}
                        values={values}
                        index={index}
                        type="unloading"
                      />
                    </Grid>
                    <Grid item className="col">
                      <Button
                        disabled={index < 1}
                        variant="contained"
                        color="secondary"
                        onClick={() => {
                          setNoOfDeliveries(noOfDeliveries - 1);
                          remove(index);
                        }}
                      >
                        Remove
                      </Button>
                    </Grid>
                  </Grid>
                );
              })}
          </React.Fragment>
        )}
      </FieldArray>
    </React.Fragment>
  );
};

export default DeliveryForm;
