import React, { useState } from "react";
import PropTypes from "prop-types";
import { format } from "date-fns";
import numeral from "numeral";
import {
  Box,
  Button,
  Divider,
  Drawer,
  IconButton,
  Link,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  TextField,
  Typography,
  useMediaQuery,
} from "@mui/material";
import { styled } from "@mui/material/styles";
import DeliveryGrid from "../../../components/dashboard/order/delivery-details-grid";
import { X as XIcon } from "../../../icons/x";
import { PropertyList } from "../../property-list";
import { PropertyListItem } from "../../property-list-item";
import { Scrollbar } from "../../scrollbar";
import moment from "moment";

const statusOptions = [
  {
    label: "Canceled",
    value: "canceled",
  },
  {
    label: "Complete",
    value: "complete",
  },
  {
    label: "Pending",
    value: "pending",
  },
  {
    label: "Rejected",
    value: "rejected",
  },
];

const OrderPreview = (props) => {
  const { lgUp, onApprove, onEdit, onReject, order } = props;
  const align = lgUp ? "horizontal" : "vertical";

  const getOrderUnit = (order) => {
    switch (JSON.parse(order.saleType).value) {
      case "quantity":
        return `${JSON.parse(order.saleType).unit}`;
        break;
      case "fixed":
        return `(Fixed)`;
        break;
      case "time":
        return `${JSON.parse(order.saleType).unit}`;
        break;
      default:
        break;
    }
  };

  return (
    <>
      <Typography sx={{ my: 3 }} variant="h6">
        Details
      </Typography>

      <PropertyList>
        <PropertyListItem
          align={align}
          disableGutters
          label="Order No"
          value={order.orderNo}
        />
        <PropertyListItem
          align={align}
          disableGutters
          label="Sale Date"
          value={moment(order.saleDate).format("DD/MM/YY")}
        />
        <PropertyListItem align={align} disableGutters label="Customer">
          <Typography color="primary" variant="body2">
            {order.customer.name}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {order.customer.mobile}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {JSON.parse(order.customer.city).description}
          </Typography>
        </PropertyListItem>
        <Divider sx={{ my: 3 }} />

        <Typography sx={{ my: 3 }} variant="h6">
          Deliveries
        </Typography>

        <Box sx={{ mt: 3, px: 3, height: "70vh", width: "100%" }}>
          <DeliveryGrid deliveries={JSON.parse(order.deliveries)} />
        </Box>

        <Scrollbar>
          <Table sx={{ minWidth: 400 }}>
            <TableHead>
              <TableRow>
                <TableCell>LR</TableCell>
                <TableCell>Loading</TableCell>
                <TableCell>Unloading</TableCell>
                <TableCell>Bill Wt</TableCell>
                <TableCell>Unloading Wt</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {JSON.parse(order.deliveries).map((item) => (
                <TableRow key={item.id}>
                  <TableCell>
                    <Link
                      color="secondary"
                      href={`/app/management/accounts/lr`}
                      variant="body"
                    >
                      Make LR
                    </Link>
                  </TableCell>

                  <TableCell>
                    {item.loading.structured_formatting.main_text}
                  </TableCell>
                  <TableCell>
                    {item.unloading.structured_formatting.main_text}
                  </TableCell>
                  <TableCell>{`${item.billQuantity} ${
                    JSON.parse(order.saleType).unit
                  }`}</TableCell>
                  <TableCell>{`${item.unloadingQuantity} ${
                    JSON.parse(order.saleType).unit
                  }`}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Scrollbar>

        <Typography sx={{ mt: 6, mb: 3 }} variant="h6">
          Vehicle Details
        </Typography>
        <PropertyListItem
          align={align}
          disableGutters
          label="Vehicle Number"
          value={order.vehicleNumber}
        />
        {!(typeof order.vehicleId === "object") ||
          (order.vehicleId === null && (
            <PropertyListItem align={align} disableGutters label="Transporter">
              <Typography color="primary" variant="body2">
                {order.transporter.name}
              </Typography>
              <Typography color="textSecondary" variant="body2">
                {order.transporter.mobile}
              </Typography>
              <Typography color="textSecondary" variant="body2">
                {JSON.parse(order.transporter.city).description}
              </Typography>
            </PropertyListItem>
          ))}
        {order.driver !== null && (
          <PropertyListItem align={align} disableGutters label="Driver">
            <Typography color="primary" variant="body2">
              {order.driver.name}
            </Typography>
            <Typography color="textSecondary" variant="body2">
              {order.driver.mobile}
            </Typography>
          </PropertyListItem>
        )}
      </PropertyList>
      <Divider sx={{ my: 3 }} />

      <Typography sx={{ my: 3 }} variant="h6">
        Sale Details
      </Typography>

      <PropertyList>
        <PropertyListItem
          align={align}
          disableGutters
          label="Sale Rate"
          value={`Rs ${order.saleRate}/ ${getOrderUnit(order)}`}
        />
        {order.minimumSaleGuarantee && (
          <PropertyListItem
            align={align}
            disableGutters
            label="Min Sale Guarantee"
            value={`${order.minimumSaleGuarantee} ${
              JSON.parse(order.saleType).unit
            }`}
          />
        )}
      </PropertyList>
      <Divider sx={{ my: 3 }} />

      {!(typeof order.vehicleId === "object") ||
        (order.vehicleId === null && (
          <React.Fragment>
            <Typography sx={{ my: 3 }} variant="h6">
              Purchase Details
            </Typography>
            <PropertyList>
              <PropertyListItem
                align={align}
                disableGutters
                label="Purchase Rate"
                value={`Rs ${order.purchaseRate}/ ${getOrderUnit(order)}`}
              />
              {order.minimumPurchaseGuarantee && (
                <PropertyListItem
                  align={align}
                  disableGutters
                  label="Min Purchase Guarantee"
                  value={`${order.minimumPurchaseGuarantee} ${
                    JSON.parse(order.saleType).unit
                  }`}
                />
              )}
            </PropertyList>
            <Divider sx={{ my: 3 }} />
          </React.Fragment>
        ))}
    </>
  );
};

const OrderForm = (props) => {
  const { onCancel, onSave, order } = props;

  return (
    <>
      <Box
        sx={{
          alignItems: "center",
          backgroundColor: (theme) =>
            theme.palette.mode === "dark" ? "neutral.800" : "neutral.100",
          borderRadius: 1,
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
          px: 3,
          py: 2.5,
        }}
      >
        <Typography variant="overline" sx={{ mr: 2 }} color="textSecondary">
          Order
        </Typography>
        <Box
          sx={{
            alignItems: "center",
            display: "flex",
            m: -1,
            "& > button": {
              m: 1,
            },
          }}
        >
          <Button
            color="primary"
            onClick={onSave}
            size="small"
            variant="contained"
          >
            Save changes
          </Button>
          <Button onClick={onCancel} size="small" variant="outlined">
            Cancel
          </Button>
        </Box>
      </Box>
      <Typography sx={{ my: 3 }} variant="h6">
        Details
      </Typography>
      <TextField
        disabled
        fullWidth
        label="ID"
        margin="normal"
        name="id"
        value={order.id}
      />
      <TextField
        disabled
        fullWidth
        label="Number"
        margin="normal"
        name="number"
        value={order.number}
      />
      <TextField
        disabled
        fullWidth
        label="Customer name"
        margin="normal"
        name="customer_name"
        value={order.customer.name}
      />
      <TextField
        disabled
        fullWidth
        label="Date"
        margin="normal"
        name="date"
        value={format(order.createdAt, "dd/MM/yyyy HH:mm")}
      />
      <TextField
        fullWidth
        label="Address"
        margin="normal"
        name="address"
        value={order.customer.address1}
      />
      <TextField
        fullWidth
        label="Country"
        margin="normal"
        name="country"
        value={order.customer.country}
      />
      <TextField
        fullWidth
        label="State/Region"
        margin="normal"
        name="state_region"
        value={order.customer.city}
      />
      <TextField
        fullWidth
        label="Total Amount"
        margin="normal"
        name="amount"
        value={order.totalAmount}
      />
      <TextField
        fullWidth
        label="Status"
        margin="normal"
        name="status"
        select
        SelectProps={{ native: true }}
        value={order.status}
      >
        {statusOptions.map((statusOption) => (
          <option key={statusOption.value} value={statusOption.value}>
            {statusOption.label}
          </option>
        ))}
      </TextField>
      <Button color="error" sx={{ mt: 3 }}>
        Delete order
      </Button>
    </>
  );
};

const OrderDrawerDesktop = styled(Drawer)({
  width: 600,
  flexShrink: 0,
  "& .MuiDrawer-paper": {
    position: "relative",
    width: 600,
  },
});

const OrderDrawerMobile = styled(Drawer)({
  flexShrink: 0,
  maxWidth: "100%",
  height: "calc(100% - 64px)",
  width: 500,
  "& .MuiDrawer-paper": {
    height: "calc(100% - 64px)",
    maxWidth: "100%",
    top: 64,
    width: 500,
  },
});

export const OrderDrawer = (props) => {
  const { containerRef, onClose, open, order, ...other } = props;
  const [isEditing, setIsEditing] = useState(false);
  const lgUp = useMediaQuery((theme) => theme.breakpoints.up("lg"));

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setIsEditing(false);
  };

  // The reason for doing this, is that the persistent drawer has to be rendered, but not it's
  // content if an order is not passed.
  const content = order ? (
    <>
      <Box
        sx={{
          alignItems: "center",
          backgroundColor: "primary.main",
          color: "primary.contrastText",
          display: "flex",
          justifyContent: "space-between",
          px: 3,
          py: 2,
        }}
      >
        <Typography color="inherit" variant="h6">
          {order.number}
        </Typography>
        <IconButton color="inherit" onClick={onClose}>
          <XIcon fontSize="small" />
        </IconButton>
      </Box>
      <Box
        sx={{
          px: 3,
          py: 4,
        }}
      >
        {!isEditing ? (
          <OrderPreview
            onApprove={onClose}
            onEdit={handleEdit}
            onReject={onClose}
            order={order}
            lgUp={lgUp}
          />
        ) : (
          <OrderForm
            onCancel={handleCancel}
            onSave={handleCancel}
            order={order}
          />
        )}
      </Box>
    </>
  ) : null;

  if (lgUp) {
    return (
      <OrderDrawerDesktop
        anchor="right"
        open={open}
        SlideProps={{ container: containerRef?.current }}
        variant="persistent"
        {...other}
      >
        {content}
      </OrderDrawerDesktop>
    );
  }

  return (
    <OrderDrawerMobile
      anchor="right"
      ModalProps={{ container: containerRef?.current }}
      onClose={onClose}
      open={open}
      SlideProps={{ container: containerRef?.current }}
      variant="temporary"
      {...other}
    >
      {content}
    </OrderDrawerMobile>
  );
};

OrderDrawer.propTypes = {
  containerRef: PropTypes.any,
  onClose: PropTypes.func,
  open: PropTypes.bool,
  order: PropTypes.object,
};
