import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { Storage } from "aws-amplify";
import { useFormik } from "formik";
import toast from "react-hot-toast";
import {
  Box,
  Button,
  Divider,
  Drawer,
  IconButton,
  TextField,
  Typography,
  useMediaQuery,
} from "@mui/material";
import { styled } from "@mui/material/styles";
import EditIcon from "@mui/icons-material/Edit";
import { useDispatch } from "../../../store";
import { X as XIcon } from "../../../icons/x";
import { PropertyList } from "../../property-list";
import { PropertyListItem } from "../../property-list-item";
import { partyApi } from "../../../api/party-api";
import { AddressCard } from "./party-address-card";
import { Plus } from "../../../icons/plus";
import { PartyAddressForm } from "./party-address-form";
import { addressApi } from "../../../api/address-api";
import { useAuth } from "../../../hooks/use-auth";
import { useSelector } from "react-redux";

const PartyPreview = (props) => {
  const dispatch = useDispatch();
  const { user } = useAuth();
  const { lgUp, onEdit, party } = props;

  const align = lgUp ? "horizontal" : "vertical";
  const addresses = useSelector((state) => state.addresses);
  const [status, setStatus] = useState(false);
  const toggleStatus = () => {
    setStatus(!status);
  };

  useEffect(async () => {
    try {
      await addressApi.getAddressesByParty(party, dispatch);
      setAddresses(addresses);
    } catch (error) {
      console.log(error);
    }
  }, [party]);

  return (
    <>
      <Box
        sx={{
          alignItems: "center",
          borderRadius: 1,
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
        }}
      >
        <Typography sx={{ mt: 3 }} variant="h6">
          Party Details
        </Typography>
        <Box
          sx={{
            alignItems: "center",
            display: "flex",
            flexWrap: "wrap",
            mt: 3,
            m: -1,
            "& > button": {
              m: 1,
            },
          }}
        >
          <Button
            onClick={onEdit}
            size="small"
            startIcon={<EditIcon fontSize="small" />}
            sx={{ pt: 3 }}
          >
            Edit
          </Button>
        </Box>
      </Box>
      <PropertyList>
        <PropertyListItem
          align={align}
          disableGutters
          label="Name"
          value={party.name}
        />

        <PropertyListItem
          align={align}
          disableGutters
          label="City"
          value={JSON.parse(party.city).description}
        />
        <PropertyListItem
          align={align}
          disableGutters
          label="Mobile"
          value={party.mobile}
        />
      </PropertyList>
      <Divider sx={{ my: 1 }} />
      <Box
        sx={{
          alignItems: "center",
          borderRadius: 1,
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
        }}
      >
        <Typography sx={{ mt: 3 }} variant="h6">
          Addresses (Consignor/Consignee)
        </Typography>
        {!status && (
          <Box
            sx={{
              alignItems: "center",
              display: "flex",
              flexWrap: "wrap",
              mt: 3,
              m: -1,
              "& > button": {
                m: 1,
              },
            }}
          >
            <Button
              onClick={toggleStatus}
              size="small"
              startIcon={<Plus fontSize="small" />}
              sx={{ pt: 3 }}
            >
              Add
            </Button>
          </Box>
        )}
      </Box>
      {status && (
        <PartyAddressForm
          party={party}
          address={{}}
          toggleStatus={toggleStatus}
          type="new"
        />
      )}
      {!status &&
        addresses &&
        addresses.map((address) => {
          return <AddressCard address={address} key={address._id} />;
        })}
    </>
  );
};

const PartyForm = (props) => {
  const { onCancel, party } = props;
  const dispatch = useDispatch();
  const [file, setFile] = useState();
  const [croppedImage, setCroppedImage] = useState();

  const formik = useFormik({
    initialValues: {
      id: party.id,
      name: party.name,
      make: party.make,
      model: party.model,
      _version: party._version,
    },
    // validationSchema: Yup.object({
    //   name: Yup.string().max(255).required("Name is required"),
    //   initials: Yup.string().max(255).required("Required"),
    //   addressLine1: Yup.string()
    //     .max(255)
    //     .required("Address Line 1 is required"),
    //   city: Yup.string().max(255).required("City is required"),
    //   pincode: Yup.string().max(255).required("Pincode is required"),
    //   gstin: Yup.string()
    //     .trim()
    //     .matches(
    //       /^([0-9]){2}([a-zA-Z]){5}([0-9]){4}([a-zA-Z]){1}([0-9]){1}([a-zA-Z]){1}([0-9]){1}?$/,
    //       "Invalid GST Number"
    //     ),
    //   pan: Yup.string().max(255).required("PAN is required"),
    //   jurisdiction: Yup.string().max(255).required("Jurisdiction is required"),
    // }),
    onSubmit: async (values, helpers) => {
      try {
        // NOTE: Make API request
        const filename = `${party.user}_partyLogo_${party.id}`;
        let logoBase64;
        let logo;
        if (values.logo) {
          logoBase64 = await fetch(values.logo);
          logo = await logoBase64.blob();
          values.logo = filename;
          await Storage.put(filename, logo);
        }

        await partyApi.updateParty(values, dispatch);

        toast.success("Party updated!");
        onCancel();
      } catch (err) {
        console.error(err);
        toast.error("Something went wrong!");
        helpers.setStatus({ success: false });
        helpers.setErrors({ submit: err.message });
        helpers.setSubmitting(false);
      }
    },
  });

  return (
    <>
      <form onSubmit={formik.handleSubmit} {...props}>
        <Box
          sx={{
            alignItems: "center",
            backgroundColor: (theme) =>
              theme.palette.mode === "dark" ? "neutral.800" : "neutral.100",
            bpartyRadius: 1,
            display: "flex",
            flexWrap: "wrap",
            justifyContent: "space-between",
            px: 3,
            py: 2.5,
          }}
        >
          <Typography variant="overline" sx={{ mr: 2 }} color="textSecondary">
            Party
          </Typography>
          <Box
            sx={{
              alignItems: "center",
              display: "flex",
              m: -1,
              "& > button": {
                m: 1,
              },
            }}
          >
            <Button
              color="primary"
              type="submit"
              size="small"
              variant="contained"
            >
              Save changes
            </Button>
            <Button onClick={onCancel} size="small" variant="outlined">
              Cancel
            </Button>
          </Box>
        </Box>

        <Typography sx={{ my: 3 }} variant="h6">
          Details
        </Typography>

        <TextField
          margin="normal"
          error={Boolean(formik.touched.name && formik.errors.name)}
          fullWidth
          helperText={formik.touched.name && formik.errors.name}
          label="Name"
          name="name"
          onBlur={formik.handleBlur}
          onChange={(event) => {
            formik.setFieldValue(`name`, event.target.value);
          }}
          value={formik.values.name}
          variant="outlined"
        />

        <TextField
          margin="normal"
          error={Boolean(formik.touched.make && formik.errors.make)}
          fullWidth
          helperText={formik.touched.make && formik.errors.make}
          label="Make"
          name="make"
          onBlur={formik.handleBlur}
          onChange={formik.handleChange}
          value={formik.values.make}
          variant="outlined"
        />
        <TextField
          margin="normal"
          error={Boolean(formik.touched.model && formik.errors.model)}
          fullWidth
          helperText={formik.touched.model && formik.errors.model}
          label="Model"
          name="model"
          onBlur={formik.handleBlur}
          onChange={formik.handleChange}
          value={formik.values.model}
          variant="outlined"
        />

        <Button color="error" sx={{ mt: 3 }}>
          Delete party
        </Button>
      </form>
    </>
  );
};

