import React, { useState, useEffect, useCallback } from "react";
import PropTypes from "prop-types";
import Autocomplete from "@material-ui/lab/Autocomplete";
import axios from "src/utils/axios";
import useIsMountedRef from "src/hooks/useIsMountedRef";

import { CircularProgress, Grid, TextField } from "@material-ui/core";

const DriverAutocomplete = ({
  className,
  touched,
  setFieldValue,
  errors,
  handleBlur,
  values,
  driver,
  setDriver,
  ...rest
}) => {
  const [open, setOpen] = useState(false);
  const [drivers, setDrivers] = useState([]);
  const [inputValue, setInputValue] = React.useState("");

  const loading = open && drivers.length === 0;
  const isMountedRef = useIsMountedRef();

  const getDrivers = useCallback(async () => {
    try {
      const response = await axios.get("/api/drivers/");
      if (isMountedRef.current) {
        setDrivers(response.data);
      }
    } catch (err) {
      console.error(err);
    }
  }, [isMountedRef]);

  useEffect(() => {
    getDrivers();

    if (!open) {
      setDrivers([]);
    }
  }, [getDrivers, open]);

  useEffect(() => {
    console.log("driver has changed");
    setFieldValue("driver", driver);
  }, [setFieldValue, driver]);

  const handleOnChange = (event, newValue) => {
    setFieldValue("driver", newValue);

    // setFieldValue('driver', newValue);
  };

  const handleInputChange = (event, newInputValue) => {
    setInputValue(newInputValue);
    // setFieldValue('driver', newInputValue);
  };

  return (
    <Grid item>
      <Autocomplete
        autoSelect={true}
        blurOnSelect={true}
        id="driver"
        open={open}
        onOpen={() => {
          setOpen(true);
        }}
        onClose={() => {
          setOpen(false);
        }}
        getOptionSelected={(option, value) => {
          if (value)
            return (
              option.name ===
              value.name.replace(/\w\S*/g, function (txt) {
                return (
                  txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase()
                );
              })
            );
        }}
        getOptionLabel={(option) => {
          return (
            option &&
            option.name.replace(/\w\S*/g, function (txt) {
              return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
            })
          );
        }}
        options={drivers}
        loading={loading}
        value={values.driver}
        onChange={handleOnChange}
        inputValue={inputValue}
        onInputChange={handleInputChange}
        renderInput={(params) => (
          <TextField
            {...params}
            name="driver"
            label="Driver"
            variant="outlined"
            error={Boolean(touched.driver && errors.driver)}
            fullWidth
            helperText={touched.driver && errors.driver}
            onBlur={handleBlur}
            InputProps={{
              ...params.InputProps,
              endAdornment: (
                <React.Fragment>
                  {loading ? (
                    <CircularProgress color="inherit" size={20} />
                  ) : null}
                  {params.InputProps.endAdornment}
                </React.Fragment>
              ),
            }}
          />
        )}
      />
    </Grid>
  );
};

DriverAutocomplete.propTypes = {
  className: PropTypes.string,
};

export default DriverAutocomplete;
