import { useEffect, useState } from "react";
import Head from "next/head";
import NextLink from "next/link";
import { useRouter } from "next/router";
import {
  Box,
  Button,
  Card,
  CardActions,
  CardContent,
  Container,
  Divider,
  Grid,
  MenuItem,
  TextField,
  Typography,
} from "@mui/material";
import { useTranslation } from "react-i18next";
import { AuthGuard } from "../../../components/authentication/auth-guard";
import { DashboardLayout } from "../../../components/dashboard/dashboard-layout";
import { ArrowRight as ArrowRightIcon } from "../../../icons/arrow-right";
import { Briefcase as BriefcaseIcon } from "../../../icons/briefcase";
import { Download as DownloadIcon } from "../../../icons/download";
import { ExternalLink as ExternalLinkIcon } from "../../../icons/external-link";
import { InformationCircleOutlined as InformationCircleOutlinedIcon } from "../../../icons/information-circle-outlined";
import { Users as UsersIcon } from "../../../icons/users";
import { gtm } from "../../../lib/gtm";
import { useAuth } from "../../../hooks/use-auth";

const Overview = () => {
  const [displayBanner, setDisplayBanner] = useState(true);
  const { user } = useAuth();
  const { t } = useTranslation();
  //   const router = useRouter();

  useEffect(() => {
    gtm.push({ event: "page_view" });
  }, []);

  useEffect(() => {
    // Restore the persistent state from local/session storage
    if (user.onBoardingRequired === true) {
      //   console.log(user.onBoardingRequired);
      // router.push({
      //   pathname: "/dashboard/welcome",
      //   query: { returnUrl: router.asPath },
      // });
      // setDisplayBanner(false);
    }
  }, []);

  const handleDismissBanner = () => {
    // Update the persistent state
    // globalThis.sessionStorage.setItem('dismiss-banner', 'true');
    setDisplayBanner(false);
  };

  return (
    <>
      <Head>
        <title>Dashboard: Overview | Material Kit Pro</title>
      </Head>
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          py: 8,
        }}
      >
        <Container maxWidth="xl">
          <Box sx={{ mb: 4 }}>
            <Grid container justifyContent="space-between" spacing={3}>
              <Grid item>
                <Typography variant="h4">{`${t("Welcome")} ${
                  user.name
                },`}</Typography>
                <Typography sx={{ mt: 2 }} variant="h6">
                  {t(
                    "We are so happy to have you with us. Let us help you figure things out."
                  )}
                </Typography>
              </Grid>
            </Grid>
          </Box>
          <Grid container spacing={4}>
            <Grid item md={6} xs={12}>
              <Card>
                <CardContent>
                  <Box
                    sx={{
                      alignItems: "center",
                      display: "flex",
                    }}
                  >
                    <BriefcaseIcon color="primary" fontSize="small" />
                    <Typography
                      color="primary.main"
                      sx={{ pl: 1 }}
                      variant="subtitle2"
                    >
                      Add your team
                    </Typography>
                  </Box>
                  <Typography variant="h6" sx={{ mt: 2 }}>
                    Add Branches and Office Employees
                  </Typography>
                  <Typography color="textSecondary" variant="body2">
                    In this software, you can add multiple team members who have
                    to use the software assign them branches.
                  </Typography>
                </CardContent>
                <Divider />
                <CardActions>
                  <NextLink href="/dashboard/welcome/new" passHref>
                    <Button
                      endIcon={<ArrowRightIcon fontSize="small" />}
                      size="small"
                    >
                      Add Employees
                    </Button>
                  </NextLink>
                </CardActions>
              </Card>
            </Grid>
            <Grid item md={6} xs={12}>
              <Card>
                <CardContent>
                  <Box
                    sx={{
                      alignItems: "center",
                      display: "flex",
                    }}
                  >
                    <InformationCircleOutlinedIcon color="primary" />
                    <Typography
                      color="primary.main"
                      sx={{ pl: 1 }}
                      variant="subtitle2"
                    >
                      Help Center
                    </Typography>
                  </Box>
                  <Typography sx={{ mt: 2 }} variant="h6">
                    Need help figuring things out?
                  </Typography>
                  <Typography color="textSecondary" variant="body2">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed
                    do eiusmod tempor incididunt ut labore et dolore magna
                    aliqua.
                  </Typography>
                </CardContent>
                <Divider />
                <CardActions>
                  <Button
                    endIcon={<ExternalLinkIcon fontSize="small" />}
                    size="small"
                  >
                    Help Center
                  </Button>
                </CardActions>
              </Card>
            </Grid>
            <Grid item md={6} xs={12}>
              <Card>
                <CardContent>
                  <Box
                    sx={{
                      alignItems: "center",
                      display: "flex",
                    }}
                  >
                    <DownloadIcon color="primary" />
                    <Typography
                      color="primary.main"
                      sx={{ pl: 1 }}
                      variant="subtitle2"
                    >
                      Download
                    </Typography>
                  </Box>
                  <Typography sx={{ mt: 2 }} variant="h6">
                    Download our Free PDF and learn how to get more job leads
                  </Typography>
                  <Typography color="textSecondary" variant="body2">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed
                    do eiusmod tempor incididunt ut labore et dolore magna
                    aliqua.
                  </Typography>
                </CardContent>
                <Divider />
                <CardActions>
                  <Button
                    endIcon={<DownloadIcon fontSize="small" />}
                    size="small"
                    variant="outlined"
                  >
                    Download Free PDF
                  </Button>
                </CardActions>
              </Card>
            </Grid>
            <Grid item md={6} xs={12}>
              <Card>
                <CardContent>
                  <Box
                    sx={{
                      alignItems: "center",
                      display: "flex",
                    }}
                  >
                    <UsersIcon color="primary" />
                    <Typography
                      color="primary.main"
                      sx={{ pl: 1 }}
                      variant="subtitle2"
                    >
                      Contacts
                    </Typography>
                  </Box>
                  <Typography sx={{ mt: 2 }} variant="h6">
                    Contacts allow you to manage your company contracts
                  </Typography>
                  <Typography color="textSecondary" variant="body2">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed
                    do eiusmod tempor incididunt ut labore et dolore magna
                    aliqua.
                  </Typography>
                </CardContent>
                <Divider />
                <CardActions>
                  <Button
                    endIcon={<ArrowRightIcon fontSize="small" />}
                    size="small"
                    variant="outlined"
                  >
                    My Contacts
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          </Grid>
        </Container>
      </Box>
    </>
  );
};

Overview.getLayout = (page) => (
  <AuthGuard>
    <DashboardLayout>{page}</DashboardLayout>
  </AuthGuard>
);

export default Overview;
