import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import { format } from "date-fns";
import { Storage } from "aws-amplify";
import { useFormik } from "formik";
import toast from "react-hot-toast";
import {
  Box,
  Button,
  Divider,
  Drawer,
  IconButton,
  TextField,
  Typography,
  useMediaQuery,
} from "@mui/material";
import { styled } from "@mui/material/styles";
import EditIcon from "@mui/icons-material/Edit";
import { useDispatch } from "../../../store";
import { X as XIcon } from "../../../icons/x";
import { getInitials } from "../../../utils/get-initials";
import { PropertyList } from "../../property-list";
import { PropertyListItem } from "../../property-list-item";
import { branchApi } from "../../../api/branch-api";
import { BranchLogoDropzone } from "./branch-logo-dropzone";

const BranchPreview = (props) => {
  const dispatch = useDispatch();
  const { lgUp, onEdit, branch } = props;
  const align = lgUp ? "horizontal" : "vertical";
  const [branchLogo, setBranchLogo] = useState();
  const [file, setFile] = useState();
  const [croppedImage, setCroppedImage] = useState();

  const handleDrop = (newFile) => {
    newFile.length > 0 &&
      setFile(
        Object.assign(newFile[0], {
          preview: URL.createObjectURL(newFile[0]),
        })
      );
  };

  const handleRemove = (file) => {
    setFile();
    setCroppedImage("");
  };

  const handleSaveCroppedImage = async (cropper) => {
    const filename = `${branch.user}_branchLogo_${branch.id}`;
    let logoBase64;
    let logo;
    logoBase64 = await fetch(cropper.getCroppedCanvas().toDataURL());
    logo = await logoBase64.blob();
    await Storage.put(filename, logo);

    let editedBranch = {
      id: branch.id,
      _version: branch._version,
      logo: filename,
    };

    await branchApi.updateBranch(editedBranch, dispatch);
    setBranchLogo(logo);
  };

  useEffect(async () => {
    try {
      setBranchLogo(null);
      if (branch.logo) {
        const logo = await Storage.get(branch.logo);
        setBranchLogo(logo);
      } else {
        setBranchLogo(null);
      }
    } catch (error) {
      console.log(error);
    }
  }, [branch]);

  return (
    <>
      {branchLogo && (
        <img
          style={{ width: "100%", height: "auto" }}
          src={branchLogo}
          alt="Business Logo"
        />
      )}
      {!branchLogo && (
        <BranchLogoDropzone
          accept="image/*"
          file={file}
          croppedImage={croppedImage}
          onDrop={handleDrop}
          onRemove={handleRemove}
          maxFiles={1}
          onSaveCroppedImage={handleSaveCroppedImage}
          displayInDrawer={true}
        />
      )}
      <Box
        sx={{
          alignItems: "center",
          borderRadius: 1,
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-between",
        }}
      >
        <Typography sx={{ mt: 3 }} variant="h6">
          Details
        </Typography>
        <Box
          sx={{
            alignItems: "center",
            display: "flex",
            flexWrap: "wrap",
            mt: 3,
            m: -1,
            "& > button": {
              m: 1,
            },
          }}
        >
          <Button
            onClick={onEdit}
            size="small"
            startIcon={<EditIcon fontSize="small" />}
            sx={{ pt: 3 }}
          >
            Edit
          </Button>
        </Box>
      </Box>
      <Divider sx={{ my: 3 }} />

      <PropertyList>
        <PropertyListItem align={align} disableGutters label="Business Name">
          <Typography color="primary" variant="body2">
            {`${branch.name} (${branch.initials})`}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {branch.addressLine1}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {branch.addressLine2}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {branch.city}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {branch.pincode}
          </Typography>
        </PropertyListItem>

        <PropertyListItem align={align} disableGutters label="Bank Details">
          <Typography color="textSecondary" variant="body2">
            {branch.bankName}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {branch.bankAccountNumber && `A/c: ${branch.bankAccountNumber}`}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {branch.bankIFSC && `IFSC: ${branch.bankIFSC}`}
          </Typography>
          <Typography color="textSecondary" variant="body2">
            {branch.bankBranchName && `Branch: ${branch.bankBranchName}`}
          </Typography>
        </PropertyListItem>

        <Typography sx={{ mt: 3 }} variant="h6">
          Billing Details
        </Typography>
        <Divider sx={{ my: 3 }} />
        <PropertyListItem
          align={align}
          disableGutters
          label="PAN"
          value={branch.pan}
        />

        <PropertyListItem
          align={align}
          disableGutters
          label="GSTIN"
          value={branch.gstin}
        />

        <PropertyListItem
          align={align}
          disableGutters
          label="Contact No."
          value={branch.contact}
        />

        <PropertyListItem
          align={align}
          disableGutters
          label="E-mail"
          value={branch.email}
        />

        <PropertyListItem
          align={align}
          disableGutters
          label="Terms & Conditions"
          value={branch.invoiceTermsAndConditions}
        />
      </PropertyList>
    </>
  );
};

const BranchForm = (props) => {
  const { onCancel, branch } = props;
  const dispatch = useDispatch();
  const [file, setFile] = useState();
  const [croppedImage, setCroppedImage] = useState();

  const formik = useFormik({
    initialValues: {
      id: branch.id,
      name: branch.name,
      initials: branch.initials,
      addressLine1: branch.addressLine1,
      addressLine2: branch.addressLine2,
      city: branch.city,
      pincode: branch.pincode,
      gstin: branch.gstin,
      pan: branch.pan,
      invoiceTermsAndConditions: branch.invoiceTermsAndConditions,
      bankName: branch.bankName,
      bankAccountNumber: branch.bankAccountNumber,
      bankBranchName: branch.bankBranchName,
      bankIFSC: branch.bankIFSC,
      contact: branch.contact,
      _version: branch._version,
    },
    // validationSchema: Yup.object({
    //   name: Yup.string().max(255).required("Name is required"),
    //   initials: Yup.string().max(255).required("Required"),
    //   addressLine1: Yup.string()
    //     .max(255)
    //     .required("Address Line 1 is required"),
    //   city: Yup.string().max(255).required("City is required"),
    //   pincode: Yup.string().max(255).required("Pincode is required"),
    //   gstin: Yup.string()
    //     .trim()
    //     .matches(
    //       /^([0-9]){2}([a-zA-Z]){5}([0-9]){4}([a-zA-Z]){1}([0-9]){1}([a-zA-Z]){1}([0-9]){1}?$/,
    //       "Invalid GST Number"
    //     ),
    //   pan: Yup.string().max(255).required("PAN is required"),
    //   jurisdiction: Yup.string().max(255).required("Jurisdiction is required"),
    // }),
    onSubmit: async (values, helpers) => {
      try {
        // NOTE: Make API request
        const filename = `${branch.user}_branchLogo_${branch.id}`;
        let logoBase64;
        let logo;
        if (values.logo) {
          logoBase64 = await fetch(values.logo);
          logo = await logoBase64.blob();
          values.logo = filename;
          await Storage.put(filename, logo);
        }

        await branchApi.updateBranch(values, dispatch);

        toast.success("Branch updated!");
        onCancel();
      } catch (err) {
        console.error(err);
        toast.error("Something went wrong!");
        helpers.setStatus({ success: false });
        helpers.setErrors({ submit: err.message });
        helpers.setSubmitting(false);
      }
    },
  });

  const handleDrop = (newFile) => {
    newFile.length > 0 &&
      setFile(
        Object.assign(newFile[0], {
          preview: URL.createObjectURL(newFile[0]),
        })
      );
  };

  const handleRemove = (file) => {
    setFile();
    setCroppedImage("");
  };

  const handleSaveCroppedImage = (cropper) => {
    setCroppedImage(cropper.getCroppedCanvas().toDataURL());
    formik.setFieldValue(`logo`, cropper.getCroppedCanvas().toDataURL());
  };

  return (
    <>
      <form onSubmit={formik.handleSubmit} {...props}>
        <Box
          sx={{
            alignItems: "center",
            backgroundColor: (theme) =>
              theme.palette.mode === "dark" ? "neutral.800" : "neutral.100",
            bbranchRadius: 1,
            display: "flex",
            flexWrap: "wrap",
            justifyContent: "space-between",
            px: 3,
            py: 2.5,
          }}
        >
          <Typography variant="overline" sx={{ mr: 2 }} color="textSecondary">
            Branch
          </Typography>
          <Box
            sx={{
              alignItems: "center",
              display: "flex",
              m: -1,
              "& > button": {
                m: 1,
              },
            }}
          >
            <Button
              color="primary"
              type="submit"
              size="small"
              variant="contained"
            >
              Save changes
            </Button>
            <Button onClick={onCancel} size="small" variant="outlined">
              Cancel
            </Button>
          </Box>
        </Box>
        <Box sx={{ mt: 3 }}>
          <BranchLogoDropzone
            accept="image/*"
            file={file}
            croppedImage={croppedImage}
            onDrop={handleDrop}
            onRemove={handleRemove}
            maxFiles={1}
            onSaveCroppedImage={handleSaveCroppedImage}
            displayInDrawer={true}
          />
          {croppedImage && (
            <img
              style={{ width: "100%", height: "auto" }}
              src={croppedImage}
              alt="cropped"
            />
          )}
          {croppedImage && (
            <Box
              sx={{
                display: "flex",

                mt: 2,
              }}
            >
              <Button onClick={handleRemove} size="small" type="button">
                Remove Logo
              </Button>
            </Box>
          )}
        </Box>

        <Typography sx={{ my: 3 }} variant="h6">
          Details
        </Typography>

        <GoogleMaps
          formik={formik}
          error={Boolean(formik.touched.city && formik.errors.city)}
          label={"Your city in India"}
          field={"city"}
          setFieldValue={formik.setFieldValue}
          handleBlur={formik.handleBlur}
          values={formik.values}
        />

        <TextField
          margin="normal"
          error={Boolean(formik.touched.name && formik.errors.name)}
          fullWidth
          helperText={formik.touched.name && formik.errors.name}
          label="Business Name"
          name="name"
          onBlur={formik.handleBlur}
          onChange={(event) => {
            formik.setFieldValue(
              `initials`,
              `${getInitials(event.target.value)}`
            );
            formik.setFieldValue(`name`, event.target.value);
          }}
          value={formik.values.name}
          variant="outlined"
        />

        <TextField
          margin="normal"
          error={Boolean(formik.touched.initials && formik.errors.initials)}
          fullWidth
          helperText={formik.touched.initials && formik.errors.initials}
          label="Initials"
          name="initials"
          onBlur={formik.handleBlur}
          onChange={formik.handleChange}
          value={formik.values.initials}
          variant="outlined"
        />
        <TextField
          margin="normal"
          error={Boolean(
            formik.touched.addressLine1 && formik.errors.addressLine1
          )}
          fullWidth
          helperText={formik.touched.addressLine1 && formik.errors.addressLine1}
          label="Address Line 1"
          name="addressLine1"
          onBlur={formik.handleBlur}
          onChange={formik.handleChange}
          value={formik.values.addressLine1}
          variant="outlined"
        />
        <Button color="error" sx={{ mt: 3 }}>
          Delete branch
        </Button>
      </form>
    </>
  );
};

const BranchDrawerDesktop = styled(Drawer)({
  width: 500,
  flexShrink: 0,
  "& .MuiDrawer-paper": {
    position: "relative",
    width: 500,
  },
});

const BranchDrawerMobile = styled(Drawer)({
  flexShrink: 0,
  maxWidth: "100%",
  height: "calc(100% - 64px)",
  width: 500,
  "& .MuiDrawer-paper": {
    height: "calc(100% - 64px)",
    maxWidth: "100%",
    top: 64,
    width: 500,
  },
});

export const BranchDrawer = (props) => {
  const { containerRef, onClose, open, branch, ...other } = props;
  const [isEditing, setIsEditing] = useState(false);
  const lgUp = useMediaQuery((theme) => theme.breakpoints.up("lg"));

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setIsEditing(false);
  };

  // The reason for doing this, is that the persistent drawer has to be rendered, but not it's
  // content if an branch is not passed.
  const content = branch ? (
    <>
      <Box
        sx={{
          alignItems: "center",
          backgroundColor: "primary.main",
          color: "primary.contrastText",
          display: "flex",
          justifyContent: "space-between",
          px: 3,
          py: 2,
        }}
      >
        <Typography color="inherit" variant="h6">
          {branch.number}
        </Typography>
        <IconButton color="inherit" onClick={onClose}>
          <XIcon fontSize="small" />
        </IconButton>
      </Box>
      <Box
        sx={{
          px: 3,
          py: 4,
        }}
      >
        {!isEditing ? (
          <BranchPreview
            onApprove={onClose}
            onEdit={handleEdit}
            onReject={onClose}
            branch={branch}
            lgUp={lgUp}
          />
        ) : (
          <BranchForm onCancel={handleCancel} branch={branch} />
        )}
      </Box>
    </>
  ) : null;

  if (lgUp) {
    return (
      <BranchDrawerDesktop
        anchor="right"
        open={open}
        SlideProps={{ container: containerRef?.current }}
        variant="persistent"
        {...other}
      >
        {content}
      </BranchDrawerDesktop>
    );
  }

  return (
    <BranchDrawerMobile
      anchor="right"
      ModalProps={{ container: containerRef?.current }}
      onClose={onClose}
      open={open}
      SlideProps={{ container: containerRef?.current }}
      variant="temporary"
      {...other}
    >
      {content}
    </BranchDrawerMobile>
  );
};

BranchDrawer.propTypes = {
  containerRef: PropTypes.any,
  onClose: PropTypes.func,
  open: PropTypes.bool,
  branch: PropTypes.object,
};
